    <title>Interactive Ternary Plot for Distillation</title>
    <style>
        body {
            margin: 0;
            padding: 0;
            font-family: sans-serif;
            background-color: #f0f0f0;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
        }
        #canvas-container {
            padding: 20px;
            background-color: #fff;
            box-shadow: 0 4px 8px rgba(0,0,0,0.1);
            border-radius: 8px;
        }
        canvas {
            display: block;
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="canvas-container">
        <div id="visualization-canvas"></div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        const sketch = (p) => {
            const canvasSize = 650;
            const margin = 60;
            const plotSize = canvasSize - 2 * margin;

            // --- State ---
            let locator = { x: 0.2, y: 0.22 };
            let isDragging = false;

            // --- Static Data ---
            const azeotropes = {
                A1: { x: 0.655, y: 0, label: "A₁" },
                A2: { x: 0, y: 0.795, label: "A₂" },
                A3: { x: 0.605, y: 0.345, label: "A₃" },
                A4: { x: 0.26, y: 0.335, label: "A₄" }
            };
            const distillationBoundary = [azeotropes.A2, azeotropes.A4, azeotropes.A1];

            // === P5.js Lifecycle Functions ===

            p.setup = () => {
                const canvas = p.createCanvas(canvasSize, canvasSize);
                canvas.parent('visualization-canvas');
                p.cursor(p.ARROW);
            };

            p.draw = () => {
                p.background(255); // White background

                // Update cursor style
                updateCursor();

                // Draw static elements
                drawAxesAndLabels();
                drawAzeotropes();

                // Calculate and draw dynamic elements
                const residueCurve = calculateDistillationLine(locator);
                drawResidueCurve(residueCurve);
                drawLocator();
            };

            // === P5.js Interaction Handlers ===

            p.mousePressed = () => {
                const locatorPx = dataToPixel(locator.x, locator.y);
                const d = p.dist(p.mouseX, p.mouseY, locatorPx.x, locatorPx.y);
                if (d < 15) {
                    isDragging = true;
                }
            };

            p.mouseDragged = () => {
                if (isDragging) {
                    const mouseDataPos = pixelToData(p.mouseX, p.mouseY);
                    const closestPoint = findClosestPointOnPolyline(mouseDataPos, distillationBoundary);
                    locator = { x: closestPoint.x, y: closestPoint.y };
                }
            };

            p.mouseReleased = () => {
                isDragging = false;
            };

            // === Coordinate Conversion ===

            function dataToPixel(x, y) {
                const px = margin + x * plotSize;
                const py = (canvasSize - margin) - y * plotSize; // Invert Y-axis
                return { x: px, y: py };
            }

            function pixelToData(px, py) {
                const x = (px - margin) / plotSize;
                const y = ((canvasSize - margin) - py) / plotSize;
                return { x, y };
            }

            // === Drawing Functions ===

            function drawAxesAndLabels() {
                p.push();
                p.stroke(0);
                p.strokeWeight(1);
                p.fill(0);
                p.textSize(12);

                // Axes lines
                const origin = dataToPixel(0, 0);
                const xMax = dataToPixel(1, 0);
                const yMax = dataToPixel(0, 1);
                p.line(origin.x, origin.y, xMax.x, xMax.y); // X-axis
                p.line(origin.x, origin.y, yMax.x, yMax.y); // Y-axis
                p.line(xMax.x, xMax.y, yMax.x, yMax.y);   // Hypotenuse

                // Ticks and labels
                p.textAlign(p.CENTER, p.TOP);
                for (let i = 0; i <= 1.01; i += 0.2) {
                    const val = p.nfc(i, 1);
                    // X-axis ticks
                    const xTickPos = dataToPixel(i, 0);
                    p.line(xTickPos.x, xTickPos.y, xTickPos.x, xTickPos.y + 5);
                    p.text(val, xTickPos.x, xTickPos.y + 8);
                    // Y-axis ticks
                    const yTickPos = dataToPixel(0, i);
                    p.line(yTickPos.x, yTickPos.y, yTickPos.x - 5, yTickPos.y);
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text(val, yTickPos.x - 8, yTickPos.y);
                }

                // Component labels
                p.fill('#FF0000'); // Red
                p.noStroke();
                p.textSize(16);
                p.textAlign(p.LEFT, p.TOP);
                p.text("methanol", origin.x + 5, origin.y + 5);
                p.textAlign(p.RIGHT, p.TOP);
                p.text("chloroform", xMax.x, xMax.y + 5);
                p.textAlign(p.LEFT, p.BOTTOM);
                p.text("acetone", yMax.x + 5, yMax.y - 5);

                p.pop();
            }

            function drawAzeotropes() {
                p.push();
                p.stroke(0);
                p.fill('#990000'); // Dark red
                p.textSize(14);

                for (const key in azeotropes) {
                    const point = azeotropes[key];
                    const pos = dataToPixel(point.x, point.y);
                    p.ellipse(pos.x, pos.y, 12, 12); // radius 6px
                    p.fill(0);
                    p.noStroke();
                    p.textAlign(p.CENTER, p.CENTER);
                    // Label positioning logic
                    let labelOffsetX = 15, labelOffsetY = 0;
                    if (key === 'A2') labelOffsetX = -15;
                    if (key === 'A3') labelOffsetY = 15;
                    p.text(point.label, pos.x + labelOffsetX, pos.y + labelOffsetY);
                    p.fill('#990000');
                }
                p.pop();
            }

            function drawLocator() {
                const pos = dataToPixel(locator.x, locator.y);
                p.push();
                p.fill(255);
                p.stroke(0);
                p.strokeWeight(2);
                p.ellipse(pos.x, pos.y, 16, 16); // radius 8px
                p.pop();
            }

            function drawResidueCurve(points) {
                if (points.length < 2) return;

                p.push();
                p.noFill();
                p.stroke('#0000FF'); // Blue
                p.strokeWeight(2);
                p.beginShape();
                // To make curveVertex pass through points, duplicate first and last
                const first = dataToPixel(points[0].x, points[0].y);
                p.curveVertex(first.x, first.y);
                points.forEach(point => {
                    const pos = dataToPixel(point.x, point.y);
                    p.curveVertex(pos.x, pos.y);
                });
                const last = dataToPixel(points[points.length-1].x, points[points.length-1].y);
                p.curveVertex(last.x, last.y);
                p.endShape();
                p.pop();
            }

            function updateCursor() {
                const locatorPx = dataToPixel(locator.x, locator.y);
                const d = p.dist(p.mouseX, p.mouseY, locatorPx.x, locatorPx.y);
                if (d < 15 || isDragging) {
                    p.cursor(p.HAND);
                } else {
                    p.cursor(p.ARROW);
                }
            }


            // === Calculation & Simulation Logic ===

            // x: chloroform mole fraction, y: acetone mole fraction
            function getVector(x, y) {
              const A3 = {x: 0.605, y: 0.345}; // Unstable node (repeller)
              const M = {x: 0, y: 0};         // Stable node (attractor)

              // Vector from repeller to current point
              let v_repel_x = x - A3.x;
              let v_repel_y = y - A3.y;
              // Vector from current point to attractor
              let v_attract_x = M.x - x;
              let v_attract_y = M.y - y;

              // Normalize influence by distance squared, add epsilon to avoid division by zero
              let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
              let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

              // Weighted sum of influences
              let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
              let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

              // Add a small rotational field to shape the curves
              dx += -0.1 * y;
              dy += 0.1 * x;

              return { dx, dy };
            }

            function calculateDistillationLine(startPoint) {
                const stepSize = 0.005;
                const numSteps = 250;

                const forwardPath = [];
                let currentF = { ...startPoint };
                for (let i = 0; i < numSteps; i++) {
                    const vec = getVector(currentF.x, currentF.y);
                    currentF.x += vec.dx * stepSize;
                    currentF.y += vec.dy * stepSize;
                    if (currentF.x < 0 || currentF.y < 0 || currentF.x + currentF.y > 1) break;
                    forwardPath.push({ ...currentF });
                }

                const backwardPath = [];
                let currentB = { ...startPoint };
                for (let i = 0; i < numSteps; i++) {
                    const vec = getVector(currentB.x, currentB.y);
                    currentB.x -= vec.dx * stepSize;
                    currentB.y -= vec.dy * stepSize;
                    if (currentB.x < 0 || currentB.y < 0 || currentB.x + currentB.y > 1) break;
                    backwardPath.push({ ...currentB });
                }

                return [...backwardPath.reverse(), startPoint, ...forwardPath];
            }

            // === Geometric Helper Functions for Dragging ===

            function findClosestPointOnPolyline(point, polyline) {
                let closestPoint = null;
                let minDistanceSq = Infinity;

                for (let i = 0; i < polyline.length - 1; i++) {
                    const segmentStart = polyline[i];
                    const segmentEnd = polyline[i+1];
                    const candidate = findClosestPointOnSegment(point, segmentStart, segmentEnd);
                    const dx = point.x - candidate.x;
                    const dy = point.y - candidate.y;
                    const distSq = dx * dx + dy * dy;

                    if (distSq < minDistanceSq) {
                        minDistanceSq = distSq;
                        closestPoint = candidate;
                    }
                }
                return closestPoint;
            }

            function findClosestPointOnSegment(p, a, b) {
                const ap = { x: p.x - a.x, y: p.y - a.y };
                const ab = { x: b.x - a.x, y: b.y - a.y };

                const ab2 = ab.x * ab.x + ab.y * ab.y;
                const ap_dot_ab = ap.x * ab.x + ap.y * ab.y;
                let t = ap_dot_ab / ab2;

                t = p.max(0, p.min(1, t)); // Clamp t to the [0, 1] range

                return {
                    x: a.x + ab.x * t,
                    y: a.y + ab.y * t
                };
            }
        };

        new p5(sketch, 'visualization-canvas');
    </script>
</body>
