            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            canvasContainer.appendChild(renderer.domElement);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040, 2);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 1.5);
            directionalLight.position.set(5, 10, 7.5);
            scene.add(directionalLight);

            // Coordinate Axes
            drawAxes();

            // Initial Plot
            updateParaboloid();

            // Event Listeners
            setupEventListeners();
            window.addEventListener('resize', onWindowResize, false);

            // Start Animation Loop
            animate();
        }

        function drawAxes() {
            const axisLength = 5;
            const axisMaterial = new THREE.LineBasicMaterial({ color: 0x888888 });

            // X-axis (red for clarity, though plan said grey)
            let points = [new THREE.Vector3(-axisLength, 0, 0), new THREE.Vector3(axisLength, 0, 0)];
            let geometry = new THREE.BufferGeometry().setFromPoints(points);
            const xAxis = new THREE.Line(geometry, axisMaterial);
            scene.add(xAxis);

            // Y-axis (green for clarity)
            points = [new THREE.Vector3(0, -axisLength, 0), new THREE.Vector3(0, axisLength, 0)];
            geometry = new THREE.BufferGeometry().setFromPoints(points);
            const yAxis = new THREE.Line(geometry, axisMaterial);
            scene.add(yAxis);

            // Z-axis (blue for clarity)
            points = [new THREE.Vector3(0, 0, -axisLength), new THREE.Vector3(0, 0, axisLength)];
            geometry = new THREE.BufferGeometry().setFromPoints(points);
            const zAxis = new THREE.Line(geometry, axisMaterial);
            scene.add(zAxis);
        }

        function createParaboloid(a, b, c) {
            const range = 2.0;
            const slices = 50;
            const stacks = 50;

            const parametricFunction = (u, v, target) => {
                let x = a * v * range * Math.cos(u * 2 * Math.PI);
                let y = b * v * range * Math.sin(u * 2 * Math.PI);
                let z = c * Math.pow(v * range, 2);
                target.set(x, y, z);
            };

            const geometry = new ParametricGeometry(parametricFunction, slices, stacks);

            // Vertex Coloring
            const positions = geometry.attributes.position;
            const colors = new Float32Array(positions.count * 3);
            const colorStart = new THREE.Color('#FFD700'); // Gold
            const colorEnd = new THREE.Color('#FF4500'); // OrangeRed
            const zMax = c * Math.pow(range, 2);

            for (let i = 0; i < positions.count; i++) {
                const z = positions.getZ(i);
                const normalizedZ = z / zMax;
                const color = new THREE.Color().lerpColors(colorStart, colorEnd, normalizedZ);
                colors[i * 3] = color.r;
                colors[i * 3 + 1] = color.g;
                colors[i * 3 + 2] = color.b;
            }
            geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));

            // Solid Mesh Material
            const solidMaterial = new THREE.MeshPhongMaterial({
                side: THREE.DoubleSide,
                vertexColors: true
            });
            const solidMesh = new THREE.Mesh(geometry, solidMaterial);

            // Wireframe Mesh Material
            const wireframeMaterial = new THREE.MeshBasicMaterial({
                color: 0xcccccc,
                wireframe: true,
                transparent: true,
                opacity: 0.5
            });
            const wireframeMesh = new THREE.Mesh(geometry, wireframeMaterial);

            // Group meshes together
            const group = new THREE.Group();
            group.add(solidMesh);
            group.add(wireframeMesh);
            return group;
        }

        function updateParaboloid() {
            if (paraboloidGroup) {
                scene.remove(paraboloidGroup);
                paraboloidGroup.children.forEach(child => {
                    if (child.geometry) child.geometry.dispose();
                    if (child.material) child.material.dispose();
                });
            }

            const a = parseFloat(sliderA.value);
            const b = parseFloat(sliderB.value);
            const c = parseFloat(sliderC.value);

            paraboloidGroup = createParaboloid(a, b, c);
            scene.add(paraboloidGroup);
        }

        function setupEventListeners() {
            const syncAndUpdate = (slider, input) => {
                slider.addEventListener('input', () => {
                    input.value = slider.value;
                    updateParaboloid();
                });
                input.addEventListener('input', () => {
                    slider.value = input.value;
                    updateParaboloid();
                });
            };

            syncAndUpdate(sliderA, inputA);
            syncAndUpdate(sliderB, inputB);
            syncAndUpdate(sliderC, inputC);
        }

        function onWindowResize() {
            camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        init();
    </script>
