    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // --- STATE MANAGEMENT ---
        const state = {
            j1: 1.5,
            m1: 0.5,
            j2: 1.0,
            m2: 0.0,
            j: 2.5,
            isAnimating: false,
            animationPhaseZ: 0,
            animationPhaseJ: 0,
        };

        const colors = {
            J: 0x000000,
            J1: 0xff0000,
            J2: 0x0000ff,
            cone: 0xaaaaaa
        };

        // --- DOM ELEMENTS ---
        const controlPanel = document.getElementById('control-panel');
        const playPauseBtn = document.getElementById('btn-play-pause');
        const resetBtn = document.getElementById('btn-reset');

        // --- THREE.JS SETUP ---
        let scene, camera, renderer, controls;
        let mainGroup; // A group to hold all vectors and cones for easy cleanup

        const canvas = document.getElementById('main-canvas');
        const container = document.getElementById('canvas-container');

        function initThree() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            const aspect = container.clientWidth / container.clientHeight;
            camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
            camera.position.set(4, 4, 4);
            camera.up.set(0, 0, 1); // Z is up
            camera.lookAt(0, 0, 0);

            renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);

            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.target.set(0,0,0);

            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 5, 10);
            scene.add(directionalLight);

            const axesHelper = new THREE.AxesHelper(5);
            axesHelper.setColors(0x888888, 0x888888, 0x333333); // R, G, B for X, Y, Z
            scene.add(axesHelper);
        }

        // --- HELPER FUNCTIONS ---
        function formatNumber(val) {
            if (val % 1 === 0) return val.toString();
            const numerator = val * 2;
            return `${numerator}/2`;
        }

        function magnitude(q) {
            return Math.sqrt(q * (q + 1));
        }

        // --- UI UPDATE FUNCTIONS ---
        function populateSelector(selectorId, values, selectedValue) {
            const container = document.getElementById(selectorId);
            container.innerHTML = '';
            values.forEach(val => {
                const btn = document.createElement('button');
                btn.dataset.value = val;
                btn.textContent = formatNumber(val);
                if (val === selectedValue) {
                    btn.classList.add('selected');
                }
                container.appendChild(btn);
            });
        }

        function updateM1Selector() {
            const values = [];
            for (let m = -state.j1; m <= state.j1; m++) values.push(m);
            populateSelector('m1-selector', values, state.m1);
        }

        function updateM2Selector() {
            const values = [];
            for (let m = -state.j2; m <= state.j2; m++) values.push(m);
            populateSelector('m2-selector', values, state.m2);
        }

        function updateJSelector() {
            const values = [];
            for (let j = Math.abs(state.j1 - state.j2); j <= state.j1 + state.j2; j++) {
                values.push(j);
            }
            populateSelector('j-selector', values, state.j);
        }

        function updateButtonStyles() {
            document.querySelectorAll('.button-container button').forEach(btn => {
                btn.classList.remove('selected');
            });
            document.querySelector(`#j1-selector button[data-value="${state.j1}"]`)?.classList.add('selected');
            document.querySelector(`#m1-selector button[data-value="${state.m1}"]`)?.classList.add('selected');
            document.querySelector(`#j2-selector button[data-value="${state.j2}"]`)?.classList.add('selected');
            document.querySelector(`#m2-selector button[data-value="${state.m2}"]`)?.classList.add('selected');
            document.querySelector(`#j-selector button[data-value="${state.j}"]`)?.classList.add('selected');
        }

        // --- VISUALIZATION LOGIC ---
        function clearScene() {
            if (mainGroup) {
                scene.remove(mainGroup);
                mainGroup.traverse(obj => {
                    if (obj.geometry) obj.geometry.dispose();
                    if (obj.material) obj.material.dispose();
                });
            }
            mainGroup = new THREE.Group();
            scene.add(mainGroup);
        }

        function createVector(origin, dir, length, color) {
            const arrow = new THREE.ArrowHelper(dir.normalize(), origin, length, color, length * 0.1, length * 0.05);
            return arrow;
        }

        function createCone(height, radius, color) {
            const geometry = new THREE.ConeGeometry(radius, height, 64, 1, true);
            const material = new THREE.MeshPhongMaterial({
                color: color,
                opacity: 0.25,
                transparent: true,
                side: THREE.DoubleSide
            });
            const cone = new THREE.Mesh(geometry, material);
            cone.position.set(0, 0, height / 2); // Position so apex is at origin
            return cone;
        }

        function updateScene() {
            clearScene();
            resetAnimation();

            const L1 = magnitude(state.j1);
            const L2 = magnitude(state.j2);
            const L = magnitude(state.j);
            const m_total = state.m1 + state.m2;

            const Z_AXIS = new THREE.Vector3(0, 0, 1);

            // Per plan: Switch model based on this condition
            const useCoupledModel = state.j >= state.j1 && state.j >= state.j2 && L > 1e-6;

            if (useCoupledModel) {
                // --- Coupled Model ---
                // J precesses around Z-axis
                const cos_theta_J_Z = Math.min(1, Math.max(-1, m_total / L));
                const theta_J_Z = Math.acos(cos_theta_J_Z);

                // J1, J2 precess around J. Find angles using law of cosines.
                const cos_theta_J1_J = (L*L + L1*L1 - L2*L2) / (2 * L * L1);
                const theta_J1_J = Math.acos(Math.min(1, Math.max(-1, cos_theta_J1_J)));

                const cos_theta_J2_J = (L*L + L2*L2 - L1*L1) / (2 * L * L2);
                const theta_J2_J = Math.acos(Math.min(1, Math.max(-1, cos_theta_J2_J)));

                // Create J and its cone
                const j_vector = createVector(new THREE.Vector3(), Z_AXIS, L, colors.J);
                const j_cone = createCone(L, L * Math.sin(theta_J_Z), colors.cone);

                const j_system = new THREE.Group();
                j_system.quaternion.setFromUnitVectors(Z_AXIS, new THREE.Vector3(Math.sin(theta_J_Z), 0, Math.cos(theta_J_Z)));
                mainGroup.add(j_system);
                j_system.add(j_vector);
                mainGroup.add(j_cone); // cone stays fixed on Z-axis

                // Create J1, J2 system that precesses around J
                const j1j2_system = new THREE.Group();
                j_system.add(j1j2_system);

                // J1 vector and cone
                const j1_vec_dir = new THREE.Vector3(Math.sin(theta_J1_J), 0, Math.cos(theta_J1_J));
                const j1_vector = createVector(new THREE.Vector3(), j1_vec_dir, L1, colors.J1);
                const j1_cone = createCone(L1, L1 * Math.sin(theta_J1_J), colors.cone);
                j1_cone.quaternion.setFromUnitVectors(Z_AXIS, j1_vec_dir);
                j1j2_system.add(j1_vector);
                j1j2_system.add(j1_cone);

                // J2 vector and cone (opposite side of J1)
                const j2_vec_dir = new THREE.Vector3(Math.sin(theta_J2_J) * -1, 0, Math.cos(theta_J2_J));
                const j2_vector = createVector(new THREE.Vector3(), j2_vec_dir, L2, colors.J2);
                const j2_cone = createCone(L2, L2 * Math.sin(theta_J2_J), colors.cone);
                j2_cone.quaternion.setFromUnitVectors(Z_AXIS, j2_vec_dir);
                j1j2_system.add(j2_vector);
                j1j2_system.add(j2_cone);

            } else {
                // --- Uncoupled Model ---
                // J1 precesses around Z
                if (L1 > 1e-6) {
                    const cos_theta_J1_Z = Math.min(1, Math.max(-1, state.m1 / L1));
                    const theta_J1_Z = Math.acos(cos_theta_J1_Z);
                    const j1_system = new THREE.Group();
                    mainGroup.add(j1_system);

                    const j1_vec_dir = new THREE.Vector3(Math.sin(theta_J1_Z), 0, Math.cos(theta_J1_Z));
                    const j1_vector = createVector(new THREE.Vector3(), j1_vec_dir, L1, colors.J1);
                    j1_system.add(j1_vector);

                    const j1_cone = createCone(L1, L1 * Math.sin(theta_J1_Z), colors.cone);
                    mainGroup.add(j1_cone);
                }

                // J2 precesses around Z
                if (L2 > 1e-6) {
                    const cos_theta_J2_Z = Math.min(1, Math.max(-1, state.m2 / L2));
                    const theta_J2_Z = Math.acos(cos_theta_J2_Z);
                    const j2_system = new THREE.Group();
                    mainGroup.add(j2_system);

                    // Start J2 on the other side for visual clarity
                    const j2_vec_dir = new THREE.Vector3(Math.sin(theta_J2_Z) * -1, 0, Math.cos(theta_J2_Z));
                    const j2_vector = createVector(new THREE.Vector3(), j2_vec_dir, L2, colors.J2);
                    j2_system.add(j2_vector);

                    const j2_cone = createCone(L2, L2 * Math.sin(theta_J2_Z), colors.cone);
                    mainGroup.add(j2_cone);
                }
            }
        }

        function updateAnimation(deltaTime) {
            if (!state.isAnimating) return;

            state.animationPhaseZ += deltaTime * 1.5; // Slower precession around Z
            state.animationPhaseJ += deltaTime * 4.0; // Faster precession around J

            const L1 = magnitude(state.j1);
            const L2 = magnitude(state.j2);
            const L = magnitude(state.j);
            const useCoupledModel = state.j >= state.j1 && state.j >= state.j2 && L > 1e-6;

            if (useCoupledModel) {
                // mainGroup -> j_system -> j1j2_system
                if(mainGroup.children[0]) mainGroup.children[0].rotation.z = state.animationPhaseZ; // j_system
                if(mainGroup.children[0]?.children[1]) mainGroup.children[0].children[1].rotation.z = state.animationPhaseJ; // j1j2_system
            } else {
                // mainGroup -> j1_system, j2_system
                if(mainGroup.children[0]) mainGroup.children[0].rotation.z = state.animationPhaseZ; // j1_system
                if(mainGroup.children[2]) mainGroup.children[2].rotation.z = -state.animationPhaseZ * 1.2; // j2_system (rotate other way)
            }
        }

        // --- ANIMATION LOOP ---
        const clock = new THREE.Clock();
        function animate() {
            requestAnimationFrame(animate);
            const deltaTime = clock.getDelta();

            updateAnimation(deltaTime);
            controls.update();
            renderer.render(scene, camera);
        }

        // --- EVENT HANDLERS ---
        function handleControlClick(e) {
            if (e.target.tagName !== 'BUTTON') return;

            const group = e.currentTarget.id;
            const value = parseFloat(e.target.dataset.value);

            let needsFullUpdate = false;

            switch (group) {
                case 'j1-selector':
                    if (state.j1 === value) return;
                    state.j1 = value;
                    state.m1 = 0; // Reset m1 to a sensible default
                    if (state.m1 < -state.j1 || state.m1 > state.j1) state.m1 = -state.j1;
                    needsFullUpdate = true;
                    break;
                case 'm1-selector':
                    if (state.m1 === value) return;
                    state.m1 = value;
                    break;
                case 'j2-selector':
                    if (state.j2 === value) return;
                    state.j2 = value;
                    state.m2 = 0; // Reset m2
                    if (state.m2 < -state.j2 || state.m2 > state.j2) state.m2 = -state.j2;
                    needsFullUpdate = true;
                    break;
                case 'm2-selector':
                    if (state.m2 === value) return;
                    state.m2 = value;
                    break;
                case 'j-selector':
                    if (state.j === value) return;
                    state.j = value;
                    break;
            }

            if (needsFullUpdate) {
                updateM1Selector();
                updateM2Selector();
                // Auto-select max j value
                state.j = state.j1 + state.j2;
                updateJSelector();
            }

            updateButtonStyles();
            updateScene();
        }

        function togglePlayPause() {
            state.isAnimating = !state.isAnimating;
            playPauseBtn.textContent = state.isAnimating ? '❚❚' : '▶';
        }

        function resetAnimation() {
            state.isAnimating = false;
            playPauseBtn.textContent = '▶';
            state.animationPhaseJ = 0;
            state.animationPhaseZ = 0;
            // The actual reset of positions happens in updateScene or updateAnimation(0)
            updateAnimation(0); // reset transforms without waiting for next frame
        }

        function onWindowResize() {
            const w = container.clientWidth;
            const h = container.clientHeight;
            camera.aspect = w / h;
            camera.updateProjectionMatrix();
            renderer.setSize(w, h);
        }

        // --- INITIALIZATION ---
        function init() {
            initThree();

            updateM1Selector();
            updateM2Selector();
            updateJSelector();
            updateButtonStyles();
            updateScene();

            controlPanel.addEventListener('click', handleControlClick);
            playPauseBtn.addEventListener('click', togglePlayPause);
            resetBtn.addEventListener('click', updateScene); // Easiest way to fully reset

            window.addEventListener('resize', onWindowResize);

            animate();
        }

        init();

    </script>
</body>
</html>
