<body>

<div class="main-container">
    <div class="control-panel">
        <div class="control-group">
            <label for="steps-slider">steps</label>
            <input type="range" id="steps-slider" min="0" max="100" value="61" step="1">
            <span id="steps-value">+ 61</span>
        </div>
        <div class="control-group">
            <label>box</label>
            <button id="btn-none">None</button>
            <button id="btn-box">Box</button>
            <button id="btn-scattered" class="active">Scattered</button>
        </div>
    </div>

    <div id="viz-container">
        <div id="recursive-container" class="grid-display">
            <h3>Recursive</h3>
            <div id="canvas-recursive"></div>
        </div>
        <div id="iterative-container" class="grid-display">
            <h3>Iterative</h3>
            <div id="canvas-iterative"></div>
        </div>
    </div>
</div>

<script>
document.addEventListener('DOMContentLoaded', () => {
    // --- Configuration ---
    const GRID_SIZE = 10;
    const CELL_SIZE = 20;
    const CANVAS_SIZE = GRID_SIZE * CELL_SIZE;
    const START_POS = { x: 4, y: 4 };

    const COLORS = {
        EMPTY: '#FFFFFF',
        OBSTACLE: '#000000',
        FILLED: '#0000FF',
        GRID_LINE: '#CCCCCC'
    };

    const CELL_STATE = {
        EMPTY: 0,
        OBSTACLE: 1
    };

    // --- State Variables ---
    let grid = [];
    let recursiveHistory = [];
    let iterativeHistory = [];
    let activePattern = 'scattered';
    let p5Recursive, p5Iterative;

    // --- DOM Elements ---
    const stepsSlider = document.getElementById('steps-slider');
    const stepsValue = document.getElementById('steps-value');
    const btnNone = document.getElementById('btn-none');
    const btnBox = document.getElementById('btn-box');
    const btnScattered = document.getElementById('btn-scattered');
    const patternButtons = [btnNone, btnBox, btnScattered];

    // --- Grid Generation ---
    const generateGrid = (pattern) => {
        const newGrid = Array.from({ length: GRID_SIZE }, () => Array(GRID_SIZE).fill(CELL_STATE.EMPTY));

        // Simple seeded random function for consistency
        let seed = 12345;
        const seededRandom = () => {
            const x = Math.sin(seed++) * 10000;
            return x - Math.floor(x);
        };

        switch (pattern) {
            case 'box':
                for (let i = 0; i < GRID_SIZE; i++) {
                    newGrid[0][i] = CELL_STATE.OBSTACLE;
                    newGrid[GRID_SIZE - 1][i] = CELL_STATE.OBSTACLE;
                    newGrid[i][0] = CELL_STATE.OBSTACLE;
                    newGrid[i][GRID_SIZE - 1] = CELL_STATE.OBSTACLE;
                }
                break;
            case 'scattered':
                for (let y = 0; y < GRID_SIZE; y++) {
                    for (let x = 0; x < GRID_SIZE; x++) {
                        if (x === START_POS.x && y === START_POS.y) continue;
                        if (seededRandom() < 0.25) {
                            newGrid[y][x] = CELL_STATE.OBSTACLE;
                        }
                    }
                }
                break;
            case 'none':
            default:
                // Grid is already empty
                break;
        }
        return newGrid;
    };

    // --- Flood Fill Algorithms ---
    const floodFillRecursive = (x, y, currentGrid, visited, history) => {
        if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) return;
        if (visited[y][x] || currentGrid[y][x] === CELL_STATE.OBSTACLE) return;

        visited[y][x] = true;
        history.push({ x, y });

        // N, W, S, E order
        floodFillRecursive(x, y - 1, currentGrid, visited, history);
        floodFillRecursive(x - 1, y, currentGrid, visited, history);
        floodFillRecursive(x, y + 1, currentGrid, visited, history);
        floodFillRecursive(x + 1, y, currentGrid, visited, history);
    };

    const floodFillIterative = (startX, startY, currentGrid, history) => {
        const queue = [];
        const visited = Array.from({ length: GRID_SIZE }, () => Array(GRID_SIZE).fill(false));

        if (startX < 0 || startX >= GRID_SIZE || startY < 0 || startY >= GRID_SIZE) return;
        if (currentGrid[startY][startX] === CELL_STATE.OBSTACLE) return;

        queue.push({ x: startX, y: startY });
        visited[startY][startX] = true;

        while (queue.length > 0) {
            const { x, y } = queue.shift();
            history.push({ x, y });

            const neighbors = [
                { x: x, y: y - 1 }, // N
                { x: x - 1, y: y }, // W
                { x: x, y: y + 1 }, // S
                { x: x + 1, y: y }  // E
            ];

            for (const n of neighbors) {
                if (n.x >= 0 && n.x < GRID_SIZE && n.y >= 0 && n.y < GRID_SIZE &&
                    !visited[n.y][n.x] && currentGrid[n.y][n.x] !== CELL_STATE.OBSTACLE) {
                    visited[n.y][n.x] = true;
                    queue.push(n);
                }
            }
        }
    };

    // --- P5.js Sketches ---
    const createSketch = (historyProvider) => (p) => {
        p.setup = () => {
            const canvas = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
            canvas.parent(p.canvas.parentElement.id); // Attach to parent div
            p.noLoop(); // We will manually trigger redraws
        };

        p.drawGrid = (step) => {
            p.background(COLORS.EMPTY);
            p.stroke(COLORS.GRID_LINE);
            p.strokeWeight(1);

            const history = historyProvider();
            const filledCells = new Set();
            for(let i=0; i<step; i++) {
                if(history[i]) {
                    filledCells.add(`${history[i].x},${history[i].y}`);
                }
            }

            for (let y = 0; y < GRID_SIZE; y++) {
                for (let x = 0; x < GRID_SIZE; x++) {
                    p.noStroke();
                    if (grid[y][x] === CELL_STATE.OBSTACLE) {
                        p.fill(COLORS.OBSTACLE);
                    } else if (filledCells.has(`${x},${y}`)) {
                        p.fill(COLORS.FILLED);
                    } else {
                        p.fill(COLORS.EMPTY);
                    }
                    p.rect(x * CELL_SIZE, y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
                }
            }

            // Draw grid lines on top
            p.stroke(COLORS.GRID_LINE);
            for (let i = 0; i <= GRID_SIZE; i++) {
                p.line(i * CELL_SIZE, 0, i * CELL_SIZE, CANVAS_SIZE);
                p.line(0, i * CELL_SIZE, CANVAS_SIZE, i * CELL_SIZE);
            }
        };
    };

    // --- Main Logic ---
    const runSimulation = () => {
        // Reset histories
        recursiveHistory = [];
        iterativeHistory = [];

        // Run recursive algorithm
        const visitedRecursive = Array.from({ length: GRID_SIZE }, () => Array(GRID_SIZE).fill(false));
        floodFillRecursive(START_POS.x, START_POS.y, grid, visitedRecursive, recursiveHistory);

        // Run iterative algorithm
        floodFillIterative(START_POS.x, START_POS.y, grid, iterativeHistory);
    };

    const updateVisualization = () => {
        const step = parseInt(stepsSlider.value, 10);
        stepsValue.textContent = `+ ${step}`;
        p5Recursive.drawGrid(step);
        p5Iterative.drawGrid(step);
    };

    const resetAndRun = (newPattern, initialStep = 0) => {
        activePattern = newPattern;

        // Update button active state
        patternButtons.forEach(btn => {
            btn.classList.toggle('active', btn.id === `btn-${newPattern}`);
        });

        grid = generateGrid(activePattern);
        runSimulation();

        const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
        stepsSlider.max = maxSteps > 0 ? maxSteps : 1; // Ensure max is at least 1
        stepsSlider.value = Math.min(initialStep, stepsSlider.max);

        updateVisualization();
    };

    // --- Event Listeners ---
    stepsSlider.addEventListener('input', updateVisualization);

    patternButtons.forEach(button => {
        button.addEventListener('click', () => {
            const pattern = button.id.replace('btn-', '');
            if (pattern !== activePattern) {
                resetAndRun(pattern, 0);
            }
        });
    });

    // --- Initialization ---
    p5Recursive = new p5(createSketch(() => recursiveHistory), 'canvas-recursive');
    p5Iterative = new p5(createSketch(() => iterativeHistory), 'canvas-iterative');

    // Initial run with 'scattered' pattern and step 61
    resetAndRun('scattered', 61);
});
</script>

</body>
</html>
