    </style>
</head>
<body>

    <div id="header-section">
        <h1>Angle Bisectors on the Circumcircle</h1>
        <p>
            Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'. Drag the points A, B, or C along the circle to see the relationship hold.
        </p>
    </div>

    <div id="visualization-section">
        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        let points = {};
        let derivedPoints = {};

        let center;
        const radius = 200;

        let draggedPoint = null;
        const dragTolerance = 15;
        const pointDiameter = 12;

        function setup() {
            let canvas = createCanvas(500, 500);
            canvas.id('p5-canvas');
            canvas.parent('canvas-container');

            center = createVector(width / 2, height / 2);

            points = {
                A: { angle: 7 * PI / 6,   pos: createVector(0, 0), label: "A" }, // 210 degrees
                B: { angle: 11 * PI / 6,  pos: createVector(0, 0), label: "B" }, // 330 degrees
                C: { angle: PI / 2,       pos: createVector(0, 0), label: "C" }  // 90 degrees
            };

            derivedPoints = {
                'A_prime': { pos: createVector(0, 0), label: "A'" },
                'B_prime': { pos: createVector(0, 0), label: "B'" },
                'C_prime': { pos: createVector(0, 0), label: "C'" }
            };

            // Calculate initial positions
            for (let key in points) {
                updatePointPosition(points[key]);
            }
        }

        function draw() {
            background('#F0F0F0');

            updateDraggedPoint();
            updateDerivedPoints();

            drawCircumcircle();
            drawDerivedGeometry();
            drawTriangleABC();
            drawAllPoints();
            drawAllLabels();
        }

        function updatePointPosition(point) {
            point.pos.x = center.x + radius * cos(point.angle);
            point.pos.y = center.y + radius * sin(point.angle);
        }

        function updateDerivedPoints() {
            // A' is the midpoint of the arc BC (not containing A)
            const angleA_prime = atan2(sin(points.B.angle) + sin(points.C.angle), cos(points.B.angle) + cos(points.C.angle));
            // B' is the midpoint of the arc AC
            const angleB_prime = atan2(sin(points.A.angle) + sin(points.C.angle), cos(points.A.angle) + cos(points.C.angle));
            // C' is the midpoint of the arc AB
            const angleC_prime = atan2(sin(points.A.angle) + sin(points.B.angle), cos(points.A.angle) + cos(points.B.angle));

            derivedPoints.A_prime.pos.x = center.x + radius * cos(angleA_prime);
            derivedPoints.A_prime.pos.y = center.y + radius * sin(angleA_prime);

            derivedPoints.B_prime.pos.x = center.x + radius * cos(angleB_prime);
            derivedPoints.B_prime.pos.y = center.y + radius * sin(angleB_prime);

            derivedPoints.C_prime.pos.x = center.x + radius * cos(angleC_prime);
            derivedPoints.C_prime.pos.y = center.y + radius * sin(angleC_prime);
        }

        function drawCircumcircle() {
            noFill();
            stroke('#6A5ACD'); // SlateBlue
            strokeWeight(4);
            circle(center.x, center.y, radius * 2);
        }

        function drawTriangleABC() {
            stroke('#6A5ACD'); // SlateBlue
            strokeWeight(4);
            line(points.A.pos.x, points.A.pos.y, points.B.pos.x, points.B.pos.y);
            line(points.B.pos.x, points.B.pos.y, points.C.pos.x, points.C.pos.y);
            line(points.C.pos.x, points.C.pos.y, points.A.pos.x, points.A.pos.y);
        }

        function drawDerivedGeometry() {
            stroke('#FFA500'); // Orange
            strokeWeight(4);

            // Bisectors (AA', BB', CC')
            line(points.A.pos.x, points.A.pos.y, derivedPoints.A_prime.pos.x, derivedPoints.A_prime.pos.y);
            line(points.B.pos.x, points.B.pos.y, derivedPoints.B_prime.pos.x, derivedPoints.B_prime.pos.y);
            line(points.C.pos.x, points.C.pos.y, derivedPoints.C_prime.pos.x, derivedPoints.C_prime.pos.y);

            // Triangle A'B'C'
            line(derivedPoints.A_prime.pos.x, derivedPoints.A_prime.pos.y, derivedPoints.B_prime.pos.x, derivedPoints.B_prime.pos.y);
            line(derivedPoints.B_prime.pos.x, derivedPoints.B_prime.pos.y, derivedPoints.C_prime.pos.x, derivedPoints.C_prime.pos.y);
            line(derivedPoints.C_prime.pos.x, derivedPoints.C_prime.pos.y, derivedPoints.A_prime.pos.x, derivedPoints.A_prime.pos.y);
        }

        function drawAllPoints() {
            // Draw interactive points A, B, C
            for (let key in points) {
                const p = points[key];
                fill('#FFA500'); // Orange
                stroke('#000000');
                strokeWeight(1);
                circle(p.pos.x, p.pos.y, pointDiameter);

                // Crosshair
                const r = pointDiameter / 2;
                line(p.pos.x - r, p.pos.y, p.pos.x + r, p.pos.y);
                line(p.pos.x, p.pos.y - r, p.pos.x, p.pos.y + r);
            }

            // Draw calculated points A', B', C'
            for (let key in derivedPoints) {
                const p = derivedPoints[key];
                fill('#FFA500'); // Orange
                stroke('#000000');
                strokeWeight(1);
                circle(p.pos.x, p.pos.y, pointDiameter);
            }
        }

        function drawAllLabels() {
            fill('#000000');
            noStroke();
            textSize(16);
            textAlign(CENTER, CENTER);
            const labelOffset = 20;

            const allPoints = { ...points, ...derivedPoints };

            for (let key in allPoints) {
                const p = allPoints[key];
                const v = p5.Vector.sub(p.pos, center).normalize();
                const labelPos = p5.Vector.add(p.pos, v.mult(labelOffset));
                text(p.label, labelPos.x, labelPos.y);
            }
        }

        function mousePressed() {
            for (let key in points) {
                const p = points[key];
                if (dist(mouseX, mouseY, p.pos.x, p.pos.y) < dragTolerance) {
                    draggedPoint = key;
                    return; // Stop after finding the first point
                }
            }
        }

        function mouseDragged() {
            updateDraggedPoint();
        }

        function updateDraggedPoint() {
            if (draggedPoint && mouseIsPressed) {
                let currentAngle = atan2(mouseY - center.y, mouseX - center.x);
                points[draggedPoint].angle = currentAngle;
                updatePointPosition(points[draggedPoint]);
            }
        }

        function mouseReleased() {
            draggedPoint = null;
        }

    </script>
</body>
</html>
