    <script>
        // Data structure for all dynamic processes
        const processes = {
            respiration: {
                label: "Cellular Respiration",
                description: "Animals and plants break down sugars for energy, releasing carbon dioxide.",
                arrow: [200, 240, 250, 180],
                labelPos: { x: 150, y: 180 },
