            ellipse(92, 200, 60, 70);

            // Factory
            fill('#696969'); // Building
            rect(450, 220, 80, 140);
            fill('#A9A9A9'); // Smokestack
            rect(460, 150, 20, 70);
            rect(455, 140, 30, 10);

            // Sheep
            fill(255); // Wool
            ellipse(200, 250, 50, 35);
            ellipse(210, 240, 25, 20);
            ellipse(190, 240, 25, 20);
            fill(240); // Head
            ellipse(225, 255, 20, 25);
            fill(0); // Eye
            circle(230, 252, 2);

            // Fossil Deposits
            fill(0);
            circle(100, 450, 10);
            circle(200, 400, 8);
            circle(230, 450, 12);
            circle(350, 480, 15);
        }

        function drawProcess(processName) {
            const p = processes[processName];

            // Draw arrow(s)
            if (p.arrows) { // For diffusion with two arrows
                p.arrows.forEach(arr => drawArrow(arr[0], arr[1], arr[2], arr[3]));
            } else {
                drawArrow(p.arrow[0], p.arrow[1], p.arrow[2], p.arrow[3]);
            }

            // Draw label
            noStroke();
            fill(0);
            textSize(12);
            textAlign(LEFT, TOP);
            text(p.label, p.labelPos.x, p.labelPos.y);

            // Check for hover
            const box = p.labelBbox;
            if (mouseX > box.x && mouseX < box.x + box.w && mouseY > box.y && mouseY < box.y + box.h) {
                hoveredDescription = p.description;
            }
        }

        function drawArrow(x1, y1, x2, y2) {
            stroke(0);
            strokeWeight(1.5);
            line(x1, y1, x2, y2);

            // Draw arrowhead
            push();
            translate(x2, y2);
            let angle = atan2(y2 - y1, x2 - x1);
            rotate(angle);
            noStroke();
            fill(0);
            triangle(0, 0, -8, -4, -8, 4);
            pop();
        }

        function drawTooltip(description) {
            const padding = 10;
            const text_size = 12;
            const tooltipX = mouseX + 15;
            const tooltipY = mouseY + 15;

            textSize(text_size);
            textAlign(LEFT, TOP);
            let textW = textWidth(description);
            let maxW = 200; // Max width before wrapping
            let textH = text_size * (ceil(textW / maxW) || 1) * 1.5; // Approximate wrapped height
            if (textW > maxW) textW = maxW;

            // Draw tooltip box
            noStroke();
            fill(0, 0, 0, 180);
            rect(tooltipX, tooltipY, textW + padding * 2, textH + padding, 5);

            // Draw tooltip text
            fill(255);
            text(description, tooltipX + padding, tooltipY + padding / 2, maxW);
        }
    </script>

</body>
</html>
