<script>
let sketch = function(p) {
    // --- STATE & CONTROLS ---
    let state = {
        t: 0.4,
        showPosition: false,
        showVelocity: false,
        showAcceleration: true,
        coords: 'rectangular',
        showCircle: false
    };

    let scaleFactor = 100;

    // --- P5.JS LIFECYCLE ---
    p.setup = function() {
        const canvasContainer = document.getElementById('canvas-container');
        const canvas = p.createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
        canvas.parent('canvas-container');
        p.windowResized = () => p.resizeCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);

        // --- ATTACH EVENT LISTENERS ---
        const timeSlider = document.getElementById('slider-time');
        timeSlider.addEventListener('input', (e) => {
            state.t = parseFloat(e.target.value);
            p.redraw();
        });

        document.getElementById('btn-reset-time').addEventListener('click', () => {
            state.t = 0.4;
            timeSlider.value = state.t;
            p.redraw();
        });

        document.querySelectorAll('input[name="position"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showPosition = e.target.value === 'on';
                p.redraw();
            });
        });

        document.querySelectorAll('input[name="velocity"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showVelocity = e.target.value === 'on';
                p.redraw();
            });
        });

        document.querySelectorAll('input[name="acceleration"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showAcceleration = e.target.value === 'on';
                p.redraw();
            });
        });

        document.getElementById('select-coords').addEventListener('change', (e) => {
            state.coords = e.target.value;
            p.redraw();
        });

        document.querySelectorAll('input[name="circle"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showCircle = e.target.value === 'on';
                p.redraw();
            });
        });

        p.noLoop(); // Redraw only on interaction
        p.redraw();
    };

    p.draw = function() {
        p.background(255);

        // --- COORDINATE SYSTEM SETUP ---
        p.translate(p.width / 2, p.height / 2);
        p.scale(1, -1); // Flip Y-axis for standard Cartesian coordinates
        const minDim = Math.min(p.width, p.height);
        scaleFactor = minDim / 5.5;

        drawAxes();
        drawCurve();

        // --- KINEMATIC CALCULATIONS ---
        const t = state.t;
        const pos = p.createVector(p.cos(t), p.sin(2 * t));
        const vel = p.createVector(-p.sin(t), 2 * p.cos(2 * t));
        const accel = p.createVector(-p.cos(t), -4 * p.sin(2 * t));

        // --- RENDER VISUAL ELEMENTS ---
        if (state.showCircle) drawOsculatingCircle(pos, vel, accel);
        if (state.showPosition) drawVector(p.createVector(0,0), pos, p.color(0, 0, 255), 'r');
        if (state.showVelocity) drawVector(pos, vel, p.color(139, 0, 0), 'v');
        if (state.showAcceleration) drawVector(pos, accel, p.color(128, 0, 128), 'a');

        drawBasisVectors(pos, vel, accel);

        // Draw the point on the curve
        p.push();
        p.noStroke();
        p.fill(0);
        p.circle(pos.x * scaleFactor, pos.y * scaleFactor, 8);
        p.pop();
    };

    // --- DRAWING HELPER FUNCTIONS ---

    function drawAxes() {
        p.push();
        p.stroke(200);
        p.strokeWeight(1);
        p.textSize(16);
        p.textAlign(p.CENTER, p.CENTER);

        // Grid lines
        for (let i = -2; i <= 2; i++) {
            if (i === 0) continue;
            // Vertical
            p.line(i * scaleFactor, -p.height/2, i * scaleFactor, p.height/2);
             // Horizontal
            p.line(-p.width/2, i * scaleFactor, p.width/2, i * scaleFactor);
        }

        p.stroke(0);
        p.strokeWeight(1.5);
        // X-Axis
        p.line(-p.width/2, 0, p.width/2, 0);
        // Y-Axis
        p.line(0, -p.height/2, 0, p.height/2);

        // Labels
        p.fill(0);
        p.noStroke();
        p.scale(1, -1); // Un-flip for text
        p.text('x(t)', p.width/2 - 20, -10);
        p.text('y(t)', 15, -p.height/2 + 20);

        // Ticks
        for (let i = -2; i <= 2; i++) {
             if (i === 0) continue;
             p.text(i, i * scaleFactor, 15);
             p.text(i, -15, -i * scaleFactor);
        }
        p.pop();
    }

    function drawCurve() {
        p.push();
        p.noFill();
        p.stroke(0, 123, 255); // Blue color
        p.strokeWeight(2);
        p.beginShape();
        for (let t_path = 0; t_path <= p.TWO_PI; t_path += 0.01) {
            let x = p.cos(t_path) * scaleFactor;
            let y = p.sin(2 * t_path) * scaleFactor;
            p.vertex(x, y);
        }
        p.endShape();
        p.pop();
    }

    function drawVector(base, vec, color, label) {
        p.push();
        p.stroke(color);
        p.fill(color);
        p.strokeWeight(2);
        const start = p5.Vector.mult(base, scaleFactor);
        const end = p5.Vector.add(start, p5.Vector.mult(vec, scaleFactor));
        p.line(start.x, start.y, end.x, end.y);

        // Arrowhead
        let arrowSize = 7;
        let angle = p.atan2(end.y - start.y, end.x - start.x);
        p.translate(end.x, end.y);
        p.rotate(angle);
        p.triangle(0, 0, -arrowSize, arrowSize / 2, -arrowSize, -arrowSize / 2);

        p.pop();
    }

    function drawBasisVector(base, vec, label, color) {
        p.push();
        p.stroke(color);
        p.fill(color);
        p.strokeWeight(1.5);
        p.drawingContext.setLineDash([5, 5]);

        const start = p5.Vector.mult(base, scaleFactor);
        const scaledVec = p5.Vector.mult(vec, scaleFactor * 0.5); // Fixed length for basis vectors
        const end = p5.Vector.add(start, scaledVec);
        p.line(start.x, start.y, end.x, end.y);

        // Arrowhead
        let arrowSize = 6;
        let angle = scaledVec.heading();
        p.translate(end.x, end.y);
        p.rotate(angle);
        p.triangle(0, 0, -arrowSize, arrowSize / 2, -arrowSize, -arrowSize / 2);
        p.rotate(-angle);

        // Label
        p.noStroke();
        p.scale(1, -1);
        p.textSize(18);
        p.textStyle(p.ITALIC);
        const labelPos = p5.Vector.mult(scaledVec, 1.2).div(scaleFactor);
        p.text(label, labelPos.x * scaleFactor, -labelPos.y * scaleFactor + 5);

        p.pop();
    }

    function drawBasisVectors(pos, vel, accel) {
        const basisColor = p.color(50);
        if (state.coords === 'rectangular') {
            const iHat = p.createVector(1, 0);
            const jHat = p.createVector(0, 1);
            drawBasisVector(pos, iHat, 'î', basisColor);
            drawBasisVector(pos, jHat, 'ĵ', basisColor);
        } else if (state.coords === 'polar') {
            if (pos.mag() < 0.01) return; // Avoid issues at origin
            const e_r = pos.copy().normalize();
            const e_theta = p.createVector(-e_r.y, e_r.x);
            drawBasisVector(pos, e_r, 'êᵣ', basisColor);
            drawBasisVector(pos, e_theta, 'êᵠ', basisColor);
        } else if (state.coords === 'normal-tangential') {
            if (vel.mag() < 0.01) return; // Avoid issues at stationary points
            const e_t = vel.copy().normalize();

            // Calculate normal vector direction based on curvature
            const cross_product_z = vel.x * accel.y - vel.y * accel.x;
            const e_n = (cross_product_z >= 0) ? p.createVector(-e_t.y, e_t.x) : p.createVector(e_t.y, -e_t.x);

            drawBasisVector(pos, e_t, 'êₜ', basisColor);
            drawBasisVector(pos, e_n, 'êₙ', basisColor);
        }
    }

    function drawOsculatingCircle(pos, vel, accel) {
        const velMag = vel.mag();
        const cross_product_z = vel.x * accel.y - vel.y * accel.x;

        // Only draw if curvature is significant
        if (velMag > 0.01 && p.abs(cross_product_z) > 0.01) {
            const rho = p.pow(velMag, 3) / p.abs(cross_product_z); // Radius of curvature

            // Normal vector pointing towards center of curvature
            const e_t = vel.copy().normalize();
            const e_n = (cross_product_z >= 0) ? p.createVector(-e_t.y, e_t.x) : p.createVector(e_t.y, -e_t.x);

            const center = p5.Vector.add(pos, p5.Vector.mult(e_n, rho));

            p.push();
            p.noFill();
            p.stroke(150);
            p.strokeWeight(1);
            p.drawingContext.setLineDash([4, 4]);
            p.circle(center.x * scaleFactor, center.y * scaleFactor, rho * 2 * scaleFactor);
            p.pop();
        }
    }
};

new p5(sketch);
</script>

</body>
</html>
