        const massValue = document.getElementById('mass-value');

        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnStepBack = document.getElementById('btn-step-back');
        const btnStepForward = document.getElementById('btn-step-forward');
        const btnReset = document.getElementById('btn-reset');

        // --- State Variables ---
        let state = {
            amplitude: 0.3,
            stiffness: 1.0,
            mass: 1.0,
            time: 0,
            isPlaying: false,
            angularFrequency: 1.0,
            positionHistory: [],
        };

        const DEFAULTS = {
            amplitude: 0.3,
            stiffness: 1.0,
            mass: 1.0,
        };

        const TIME_STEP = 0.1;

        // --- State Management ---
        function updateAngularFrequency() {
            state.angularFrequency = Math.sqrt(state.stiffness / state.mass);
        }

        function resetSimulation(fullReset = false) {
            state.isPlaying = false;
            btnPlayPause.textContent = '▶';

            if (fullReset) {
                state.amplitude = DEFAULTS.amplitude;
                state.stiffness = DEFAULTS.stiffness;
                state.mass = DEFAULTS.mass;

                sliderAmplitude.value = state.amplitude;
                sliderStiffness.value = state.stiffness;
                sliderMass.value = state.mass;
            }

            amplitudeValue.textContent = parseFloat(state.amplitude).toFixed(3);
            stiffnessValue.textContent = parseFloat(state.stiffness).toFixed(2);
            massValue.textContent = parseFloat(state.mass).toFixed(2);

            updateAngularFrequency();
            state.time = 0;
            state.positionHistory = [];
        }

        // --- Event Listeners ---
        sliderAmplitude.addEventListener('input', (e) => {
            state.amplitude = parseFloat(e.target.value);
            resetSimulation();
        });

        sliderStiffness.addEventListener('input', (e) => {
            state.stiffness = parseFloat(e.target.value);
            resetSimulation();
        });

        sliderMass.addEventListener('input', (e) => {
            state.mass = parseFloat(e.target.value);
            resetSimulation();
        });

        btnPlayPause.addEventListener('click', () => {
            state.isPlaying = !state.isPlaying;
            btnPlayPause.textContent = state.isPlaying ? '||' : '▶';
        });

        btnStepForward.addEventListener('click', () => {
            if (!state.isPlaying) {
                state.time += TIME_STEP;
                // Add a point for stepping, otherwise the plot won't update
                const y = state.amplitude * Math.sin(state.angularFrequency * state.time);
                state.positionHistory.push({ t: state.time, y: y });
                // Sort history by time to handle back and forth steps
                state.positionHistory.sort((a, b) => a.t - b.t);
            }
        });

        btnStepBack.addEventListener('click', () => {
            if (!state.isPlaying) {
                state.time = Math.max(0, state.time - TIME_STEP);
            }
        });

        btnReset.addEventListener('click', () => {
            resetSimulation(true);
        });

        // --- p5.js Sketch for Plot ---
        const plotSketch = (p) => {
            const padding = { top: 20, right: 20, bottom: 40, left: 50 };
            let plotWidth, plotHeight;
            let timeAxisMax = 25;

            p.setup = () => {
                const container = document.getElementById('plot-canvas-container');
                const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                canvas.parent(container);
                plotWidth = p.width - padding.left - padding.right;
                plotHeight = p.height - padding.top - padding.bottom;
                p.frameRate(60);
            };

            p.draw = () => {
                p.background(255);
                p.translate(padding.left, padding.top);

                // Update state if playing
                if (state.isPlaying) {
                    state.time += p.deltaTime / 1000; // time in seconds
                    const y = state.amplitude * Math.sin(state.angularFrequency * state.time);
                    state.positionHistory.push({ t: state.time, y: y });
                }

                // Auto-scale time axis
                if (state.time > timeAxisMax) {
                    timeAxisMax *= 1.5;
                }

                drawAxesAndGrid();
                drawWaveform();
            };

            p.windowResized = () => {
                 const container = document.getElementById('plot-canvas-container');
                 p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                 plotWidth = p.width - padding.left - padding.right;
                 plotHeight = p.height - padding.top - padding.bottom;
            };

            function drawAxesAndGrid() {
                p.stroke(200);
                p.strokeWeight(1);
                p.fill(100);
                p.textAlign(p.RIGHT, p.CENTER);

                // Y-axis grid & labels (-1, 0, 1)
                for (let yVal = -1; yVal <= 1; yVal += 1) {
                    const y = p.map(yVal, -1, 1, plotHeight, 0);
                    p.stroke(yVal === 0 ? 150 : 'var(--grid-color)');
                    p.line(0, y, plotWidth, y);
                    p.noStroke();
                    p.text(yVal.toFixed(1), -10, y);
                }

                // X-axis grid & labels
                p.textAlign(p.CENTER, p.TOP);
                const tickCount = 10;
                for (let i = 0; i <= tickCount; i++) {
                    const t = p.map(i, 0, tickCount, 0, timeAxisMax);
                    const x = p.map(t, 0, timeAxisMax, 0, plotWidth);
                    p.stroke('var(--grid-color)');
                    p.line(x, 0, x, plotHeight);
                    p.noStroke();
                    p.text(Math.round(t), x, plotHeight + 10);
                }

                // Axis labels
                p.push();
                p.textAlign(p.CENTER, p.CENTER);
                p.text("Time (s)", plotWidth / 2, plotHeight + 30);
                p.rotate(-p.HALF_PI);
                p.text("Position", -plotHeight / 2, -40);
                p.pop();
            }

            function drawWaveform() {
                p.noFill();
                p.stroke('var(--primary-color)');
                p.strokeWeight(2);
                p.beginShape();
                for (const point of state.positionHistory) {
                    if (point.t <= timeAxisMax) {
                        const x = p.map(point.t, 0, timeAxisMax, 0, plotWidth);
                        const y = p.map(point.y, -1, 1, plotHeight, 0);
                        p.vertex(x, y);
                    }
                }
                p.endShape();
            }
        };

        // --- p5.js Sketch for Animation ---
        const animationSketch = (p) => {
            const yRange = 1.2;

            p.setup = () => {
                const container = document.getElementById('animation-canvas-container');
                const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                canvas.parent(container);
            };

            p.draw = () => {
                p.background(255);

                // Calculate current position
                const currentY = state.amplitude * Math.sin(state.angularFrequency * state.time);

                // Map to canvas coordinates
                const massSize = p.min(p.width * 0.4, 40);
                const anchorY = p.height * 0.1;
                const equilibriumY = p.height / 2;
                const massDrawY = equilibriumY - p.map(currentY, 0, yRange, 0, p.height/2.5);

                drawVerticalAxis();
                drawSpring(anchorY, massDrawY - massSize / 2, 20, 15);

                // Draw mass
                p.fill('var(--primary-color)');
                p.stroke(50);
                p.strokeWeight(2);
                p.rectMode(p.CENTER);
                p.rect(p.width / 2, massDrawY, massSize, massSize);
            };

            p.windowResized = () => {
                 const container = document.getElementById('animation-canvas-container');
                 p.resizeCanvas(container.offsetWidth, container.offsetHeight);
            };

            function drawVerticalAxis() {
                p.stroke(200);
                p.strokeWeight(1);
                p.line(p.width / 2, 0, p.width / 2, p.height);

                p.fill(100);
                p.noStroke();
                p.textAlign(p.LEFT, p.CENTER);
                for(let yVal = -1; yVal <= 1; yVal++) {
                    const yPos = p.height/2 - p.map(yVal, 0, yRange, 0, p.height/2.5);
                    p.text(yVal, p.width/2 + 10, yPos);
                }
            }

            function drawSpring(startY, endY, width, coils) {
                p.stroke(0);
                p.strokeWeight(1.5);
                p.noFill();

                const springHeight = endY - startY;
                const coilHeight = springHeight / coils;
                const x = p.width / 2;

                p.beginShape();
                p.vertex(x, startY); // Anchor point
                for (let i = 1; i < coils; i++) {
                    const y = startY + i * coilHeight;
                    const xOffset = (i % 2 === 0) ? width / 2 : -width / 2;
                    p.vertex(x + xOffset, y - coilHeight / 2);
                }
                p.vertex(x, endY); // Connect to mass
                p.endShape();

                // Draw fixed anchor
                p.strokeWeight(3);
                p.line(x - width, startY, x + width, startY);
            }
        };

        // --- Initialize ---
        document.addEventListener('DOMContentLoaded', () => {
            resetSimulation(true);
            new p5(plotSketch, 'plot-canvas-container');
            new p5(animationSketch, 'animation-canvas-container');
        });

    </script>
</body>
</html>
