            color: #333;
        }
    </style>
</head>
<body>

    <div class="main-container">
        <!-- Control Panel Section -->
        <div class="control-panel">
            <span>distance function</span>
            <button id="btn-manhattan">Manhattan</button>
            <button id="btn-euclidean">squared Euclidean</button>
            <button id="btn-chebyshev">Chebyshev</button>
        </div>

        <!-- Grid Area Section -->
        <div id="grid-container">
            <!-- Grid cells will be dynamically generated here -->
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- STATE VARIABLES ---
            const ROWS = 9;
            const COLS = 7;
            let currentDistanceFunction = 'manhattan';
            // Initialize a 9x7 grid with all 'false'
            let gridState = Array.from({ length: ROWS }, () => Array(COLS).fill(false));

            // Set initial "on" cells as specified
            gridState[2][1] = true;
            gridState[6][4] = true;

            // --- DOM ELEMENT REFERENCES ---
            const gridContainer = document.getElementById('grid-container');
            const btnManhattan = document.getElementById('btn-manhattan');
            const btnEuclidean = document.getElementById('btn-euclidean');
            const btnChebyshev = document.getElementById('btn-chebyshev');
            const buttons = [btnManhattan, btnEuclidean, btnChebyshev];

            // --- DISTANCE CALCULATION LOGIC ---
            const distanceFunctions = {
                manhattan: (r1, c1, r2, c2) => Math.abs(r1 - r2) + Math.abs(c1 - c2),
                euclidean: (r1, c1, r2, c2) => Math.pow(r1 - r2, 2) + Math.pow(c1 - c2, 2),
                chebyshev: (r1, c1, r2, c2) => Math.max(Math.abs(r1 - r2), Math.abs(c1 - c2)),
            };

            function calculateDistanceTransform() {
                const sources = [];
                for (let r = 0; r < ROWS; r++) {
                    for (let c = 0; c < COLS; c++) {
                        if (gridState[r][c]) {
                            sources.push([r, c]);
                        }
                    }
                }

                const distanceGrid = Array.from({ length: ROWS }, () => Array(COLS).fill(Infinity));
                const distFunc = distanceFunctions[currentDistanceFunction];

                if (sources.length === 0) {
                    return distanceGrid; // All cells remain at Infinity
                }

                for (let r = 0; r < ROWS; r++) {
                    for (let c = 0; c < COLS; c++) {
                        let minDistance = Infinity;
                        for (const source of sources) {
                            const [sourceR, sourceC] = source;
                            const distance = distFunc(r, c, sourceR, sourceC);
                            if (distance < minDistance) {
                                minDistance = distance;
                            }
                        }
                        distanceGrid[r][c] = minDistance;
                    }
                }

                return distanceGrid;
            }

            // --- RENDERING AND UI UPDATE LOGIC ---
            function renderGrid() {
                const distanceGrid = calculateDistanceTransform();
                gridContainer.innerHTML = ''; // Clear previous grid

                for (let r = 0; r < ROWS; r++) {
                    for (let c = 0; c < COLS; c++) {
                        const cell = document.createElement('div');
                        cell.classList.add('cell');

                        const isCellOn = gridState[r][c];
                        cell.classList.add(isCellOn ? 'cell-on' : 'cell-off');

                        // Add data attributes to identify the cell on click
                        cell.dataset.row = r;
                        cell.dataset.col = c;

                        const span = document.createElement('span');
                        const distance = distanceGrid[r][c];
                        span.textContent = (distance === Infinity) ? '∞' : distance;

                        cell.appendChild(span);

                        // Attach click event listener
                        cell.addEventListener('click', handleCellClick);

                        gridContainer.appendChild(cell);
                    }
                }
            }

            function updateButtonStyles() {
                buttons.forEach(button => {
                    button.classList.remove('active-btn');
                });

                const activeBtnId = `btn-${currentDistanceFunction}`;
                const activeButton = document.getElementById(activeBtnId);
                if (activeButton) {
                    activeButton.classList.add('active-btn');
                }
            }

            // --- EVENT HANDLERS ---
            function handleCellClick(event) {
                // Use currentTarget to ensure we get the div with the dataset
                const cell = event.currentTarget;
                const r = parseInt(cell.dataset.row, 10);
                const c = parseInt(cell.dataset.col, 10);

                // Toggle the state
                gridState[r][c] = !gridState[r][c];

                // Recalculate and redraw the entire grid
                renderGrid();
            }

            function handleDistanceChange(newFunction) {
                if (currentDistanceFunction !== newFunction) {
                    currentDistanceFunction = newFunction;
                    updateButtonStyles();
                    renderGrid();
                }
            }

            // --- INITIALIZATION ---
            btnManhattan.addEventListener('click', () => handleDistanceChange('manhattan'));
            btnEuclidean.addEventListener('click', () => handleDistanceChange('euclidean'));
            btnChebyshev.addEventListener('click', () => handleDistanceChange('chebyshev'));

            // Initial setup
            updateButtonStyles(); // Set the default active button style
            renderGrid();         // Draw the initial grid
        });
    </script>

</body>
</html>
