            const genModelType = btnModelEp.classList.contains('active') ? 'ep' : 'ar';
            const tmaxRange = btnTmaxLow.classList.contains('active') ? 'low' : 'high';

            let n = parseInt(sliderN.value);
            let tmin = parseInt(sliderTmin.value);
            let tmax = parseInt(sliderTmax.value);
            let tref = parseInt(sliderTref.value);
            let paramVal = parseFloat(sliderParam.value);

            // 2. Slider constraint and dynamic UI logic
            // T_max range
            const newTmaxMax = tmaxRange === 'low' ? 1200 : 6000;
            if (parseInt(sliderTmax.max) !== newTmaxMax) {
                sliderTmax.max = newTmaxMax;
                if (tmax > newTmaxMax) {
                    tmax = newTmaxMax;
                    sliderTmax.value = tmax;
                }
            }

            // Temperature value constraints
            if (tmin >= tmax) {
                tmin = tmax - 1;
                if (tmin < sliderTmin.min) tmin = parseInt(sliderTmin.min);
                sliderTmin.value = tmin;
            }
            sliderTmax.min = tmin + 1;

            if (tref < tmin) {
                tref = tmin;
                sliderTref.value = tref;
            }
            if (tref > tmax) {
                tref = tmax;
                sliderTref.value = tref;
            }
            sliderTref.min = tmin;
            sliderTref.max = tmax;

            // 3. Update UI labels and values
            labelN.textContent = n;
            labelTmin.textContent = tmin;
            labelTmax.textContent = tmax;
            labelTref.textContent = tref;
            labelParamValue.textContent = paramVal;

            labelParamName.innerHTML = genModelType === 'ep' ? '\\(a_{EP}\\)' : '\\(a_{Ar}\\)';

            // 4. Data Generation
            const tempsC = Array.from({ length: n }, (_, i) => {
                // Ensure n > 1 to avoid division by zero
                return n > 1 ? tmin + i * (tmax - tmin) / (n - 1) : tmin;
            });
            const T_ref_K = tref + 273.15;
            const generatedY = tempsC.map(t => modelFunctions[genModelType](t, paramVal, T_ref_K));

            // 5. Data Fitting
            const fitModelType = genModelType === 'ep' ? 'ar' : 'ep';
            const fitModelFunc = modelFunctions[fitModelType];

            // Grid search for best fit parameter 'a'
            let bestA = 0;
            let minRss = Infinity;
            const searchMin = 0, searchMax = 10000, searchStep = 1;

            for (let a_test = searchMin; a_test <= searchMax; a_test += searchStep) {
                let currentRss = 0;
                for (let i = 0; i < n; i++) {
                    const y_pred = fitModelFunc(tempsC[i], a_test, T_ref_K);
                    currentRss += (generatedY[i] - y_pred) ** 2;
                }
                if (currentRss < minRss) {
                    minRss = currentRss;
                    bestA = a_test;
                }
            }

            // Calculate R-squared
            const yMean = generatedY.reduce((a, b) => a + b, 0) / n;
            const ssTot = generatedY.reduce((sum, y) => sum + (y - yMean) ** 2, 0);
            const r2 = ssTot > 0 ? 1 - (minRss / ssTot) : 1;

            // 6. Update results display
            const fitParamName = fitModelType === 'ep' ? 'a_{EP}' : 'a_{Ar}';
            fitResultsDiv.innerHTML = `\\(${fitParamName} = ${bestA.toLocaleString()}\\) &nbsp;&nbsp; \\(r^2 = ${r2.toFixed(3)}\\)`;

            // 7. Redraw plot
            drawPlot(tempsC, generatedY, bestA, genModelType, fitModelType, T_ref_K);

            // 8. Re-render MathJax
            if (window.MathJax && window.MathJax.typesetPromise) {
                window.MathJax.typesetPromise([labelParamName, fitResultsDiv]).catch(err => console.error('MathJax error:', err));
            }
        };

        // --- Plotting Function ---
        const drawPlot = (xData, yData, aFit, genModel, fitModel, tRefK) => {
            // Trace 1: Generated data points
            const dataTrace = {
                x: xData,
                y: yData,
                mode: 'markers',
                type: 'scatter',
                name: 'Generated Data',
                marker: { 
                    color: 'rgba(0, 123, 255, 0.7)', 
                    size: 10,
                    line: {
                        color: 'rgba(0, 80, 180, 1)',
                        width: 2
                    }
                }
            };

            // Trace 2: Fitted model curve
            const fitCurveX = [];
            const fitCurveY = [];
            const numCurvePoints = 200;
            const tmin = xData[0];
            const tmax = xData[xData.length - 1];
            for (let i = 0; i < numCurvePoints; i++) {
                const temp = tmin + i * (tmax - tmin) / (numCurvePoints - 1);
                fitCurveX.push(temp);
                fitCurveY.push(modelFunctions[fitModel](temp, aFit, tRefK));
            }

            const fitTrace = {
                x: fitCurveX,
                y: fitCurveY,
                mode: 'lines',
                type: 'scatter',
                name: 'Fitted Model',
                line: {
                    color: '#28a745',
                    width: 3
                }
            };

            // Plot Layout
            const genModelName = genModel === 'ep' ? 'Eyring-Polanyi' : 'Arrhenius';
            const fitModelName = fitModel === 'ep' ? 'Eyring-Polanyi' : 'Arrhenius';

            const layout = {
                title: `${fitModelName} model fit to ${genModelName} data`,
                xaxis: { title: 'T (°C)' },
                yaxis: { title: 'k(T) / k(T<sub>ref</sub>)' },
                margin: { l: 50, r: 30, b: 40, t: 50 },
                legend: { x: 0.01, y: 0.99, bgcolor: 'rgba(255,255,255,0.6)' },
                hovermode: 'x unified'
            };

            Plotly.newPlot(plotContainer, [dataTrace, fitTrace], layout, {responsive: true});
        };

        // --- Event Listeners ---
        function setupEventListeners() {
            btnModelEp.addEventListener('click', () => {
                btnModelEp.classList.add('active');
                btnModelAr.classList.remove('active');
                update();
            });

            btnModelAr.addEventListener('click', () => {
                btnModelAr.classList.add('active');
                btnModelEp.classList.remove('active');
                update();
            });

            btnTmaxLow.addEventListener('click', () => {
                btnTmaxLow.classList.add('active');
                btnTmaxHigh.classList.remove('active');
                update();
            });

            btnTmaxHigh.addEventListener('click', () => {
                btnTmaxHigh.classList.add('active');
                btnTmaxLow.classList.remove('active');
                update();
            });

            [sliderN, sliderTmin, sliderTmax, sliderTref, sliderParam].forEach(slider => {
                slider.addEventListener('input', update);
            });
        }

        // --- Initialization ---
        setInitialState();
        setupEventListeners();
        // A short delay to allow MathJax to load before the first typeset call
        setTimeout(update, 100); 

    });
    </script>

</body>
</html>
