    <script>
        // --- GLOBAL STATE AND CONSTANTS ---
        let n;
        const buttons = {};
        let nDisplay;

        const COLORS = {
            yellow: '#fefd00',
            red: '#ee1f24',
            blue: '#0048a1',
            black: '#000000',
            white: '#FFFFFF'
        };
        const POLYGON_COLORS = [COLORS.yellow, COLORS.red, COLORS.blue];

        // --- P5.JS SETUP ---
        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const canvas = createCanvas(1000, 450);
            canvas.parent(canvasContainer);

            // Get references to DOM elements
            nDisplay = document.getElementById('n-display');
            for (let i = 3; i <= 10; i++) {
                const btn = document.getElementById(`btn-n-${i}`);
                buttons[i] = btn;
                btn.addEventListener('click', () => updateN(i));
            }

            // Initialize the visualization
            updateN(6); // Default value as per plan

            noLoop(); // p5.js will not loop the draw function automatically
        }

        // --- INTERACTION LOGIC ---
        function updateN(newN) {
            n = newN;
            updateUI();
            drawScene();
        }

        function updateUI() {
            // 1. Update the text display
            nDisplay.textContent = n;

            // 2. Update button 'active' state
            for (const key in buttons) {
                buttons[key].classList.remove('active');
            }
            buttons[n].classList.add('active');
        }

        // --- DRAWING LOGIC ---
        function drawScene() {
            background(COLORS.white);
            stroke(COLORS.black);
            strokeWeight(1.5);

            // --- PART 1: DRAW THE POLYGON (LEFT SIDE) ---
            drawPolygon();

            // --- PART 2: DRAW THE REARRANGED RECTANGLES (RIGHT SIDE) ---
            drawRearrangement();
        }

        function drawPolygon() {
            const R = 180; // Radius of the circumscribed circle
            const cx = width / 4;
            const cy = height / 2;
            const angleStep = PI / n; // Angle for each slice of the 2n-gon

            for (let j = 0; j < 2 * n; j++) {
                const angle1 = j * angleStep - HALF_PI; // -HALF_PI to make it point up
                const angle2 = (j + 1) * angleStep - HALF_PI;

                const x1 = cx + R * cos(angle1);
                const y1 = cy + R * sin(angle1);
                const x2 = cx + R * cos(angle2);
                const y2 = cy + R * sin(angle2);

                fill(POLYGON_COLORS[j % 3]);
                triangle(cx, cy, x1, y1, x2, y2);
            }
        }

        function drawRearrangement() {
            // Calculate dimensions based on the polygon's triangles
            const R = 180;
            const angleStep = PI / n;
            const s = 2 * R * sin(angleStep / 2); // Base length of an isosceles triangle
            const a = R * cos(angleStep / 2); // Apothem (height) of an isosceles triangle

            const startX = width / 2 + 50;
            const startY = height / 2;
            const spacing = 15;

            // Draw the full rectangles
            const numFullRects = floor(n / 2);
            for (let k = 0; k < numFullRects; k++) {
                const rectX = startX + k * (s + spacing);

                // Each rectangle is composed of 4 isosceles triangles
                const centerX = rectX + s / 2;
                const topY = startY - a;
                const bottomY = startY + a;

                // Top triangle (Yellow)
                fill(COLORS.yellow);
                triangle(rectX, topY, rectX + s, topY, centerX, startY);

                // Bottom triangle (Red)
                fill(COLORS.red);
                triangle(rectX, bottomY, rectX + s, bottomY, centerX, startY);

                // Left triangle (Blue)
                fill(COLORS.blue);
                triangle(rectX, topY, rectX, bottomY, centerX, startY);

                // Right triangle (Yellow)
                fill(COLORS.yellow);
                triangle(rectX + s, topY, rectX + s, bottomY, centerX, startY);
            }

            // If n is odd, draw the remaining "half-rectangle"
            if (n % 2 !== 0) {
                const halfRectX = startX + numFullRects * (s + spacing);
                const topY = startY - a;
                const bottomY = startY + a;

                // This shape is made of two of the original isosceles triangles placed base-to-base

                // Top triangle (Blue - hardcoded as per plan)
                fill(COLORS.blue);
                triangle(halfRectX, startY, halfRectX + s, startY, halfRectX + s / 2, topY);

                // Bottom triangle (Red - hardcoded as per plan)
                fill(COLORS.red);
                triangle(halfRectX, startY, halfRectX + s, startY, halfRectX + s / 2, bottomY);
            }
        }

    </script>

</body>
</html>
