    </div>

    <div id="main-container">
        <div id="workspace-panel">
            <h3>robot workspace</h3>
            <div id="canvas-workspace"></div>
        </div>
        <div id="phase-space-panel">
            <h3 id="phase-space-title"></h3>
            <div id="canvas-phase-space"></div>
        </div>
    </div>

<script>
// --- GLOBAL STATE & CONFIGURATION ---
let qSlider, pSlider, qValueSpan, pValueSpan, resetButton;

// P5 Sketch instances
let workspaceSketch, phaseSpaceSketch;

// Simulation parameters
const W = 400; // Canvas width/height
const L1 = 75; // Link 1 length
const L2 = 50; // Link 2 length
const LINK_RADIUS = 5; // For capsule collision, visually it's 2*LINK_RADIUS

// Default state values
const defaultState = {
    q: 2,
    p: 1.0,
    obstacles: [
        { x: 200, y: 100, radius: 15 },
        { x: 200, y: 300, radius: 15 }
    ],
    startConfig: { q1: -1.5, q2: 0.5 },
    goalConfig: { q1: 1.5, q2: 1.0 }
};

// Live state
let Q, P;
let obstacles = [];
let startConfig, goalConfig;

// C-space and Pathfinding data
let cspace, distField, path;
let N; // Grid resolution

// Interaction flags
let isDraggingObstacle1 = false;
let isDraggingObstacle2 = false;
let isDraggingStart = false;
let isDraggingGoal = false;

// --- UTILITY FUNCTIONS ---
const mapToRange = (val, in_min, in_max, out_min, out_max) => (val - in_min) * (out_max - out_min) / (in_max - in_min) + out_min;
const angleToGrid = (q) => Math.floor(mapToRange(q, -Math.PI, Math.PI, 0, N));
const gridToAngle = (i) => mapToRange(i + 0.5, 0, N, -Math.PI, Math.PI);
const configToGrid = (config) => [angleToGrid(config.q2), angleToGrid(config.q1)];
const gridToConfig = (i, j) => ({ q1: gridToAngle(j), q2: gridToAngle(i) });


// --- COLLISION DETECTION ---
// Check collision between a single robot link (capsule) and a single circular obstacle
function checkLinkObstacleCollision(p1, p2, linkRadius, obs) {
    const v = p5.Vector.sub(p2, p1);
    const w = p5.Vector.sub(obs, p1);

    const t = p5.Vector.dot(w, v) / p5.Vector.dot(v, v);
    const clampedT = Math.max(0, Math.min(1, t));

    const closestPoint = p5.Vector.add(p1, p5.Vector.mult(v, clampedT));
    const dist = closestPoint.dist(obs);

    return dist < (linkRadius + obs.radius);
}

// Check collision for the entire robot arm for a given configuration
function checkCollision(q1, q2) {
    const p0 = new p5.Vector(W / 2, W / 2);
    const p1 = new p5.Vector(p0.x + L1 * Math.cos(q1), p0.y + L1 * Math.sin(q1));
    const p2 = new p5.Vector(p1.x + L2 * Math.cos(q1 + q2), p1.y + L2 * Math.sin(q1 + q2));

    for (const obs of obstacles) {
        const obsVec = new p5.Vector(obs.x, obs.y);
        if (checkLinkObstacleCollision(p0, p1, LINK_RADIUS, { ...obs, x: obsVec.x, y: obsVec.y })) return true;
        if (checkLinkObstacleCollision(p1, p2, LINK_RADIUS, { ...obs, x: obsVec.x, y: obsVec.y })) return true;
    }
    return false;
}

// --- CORE ALGORITHMS ---

function calculateCSpace() {
    N = 10 * Math.pow(2, Q);
    cspace = Array(N).fill(null).map(() => Array(N).fill(false));
    for (let i = 0; i < N; i++) {
        for (let j = 0; j < N; j++) {
            const config = gridToConfig(i, j);
            cspace[i][j] = checkCollision(config.q1, config.q2);
        }
    }
}

function runBFS() {
    distField = Array(N).fill(null).map(() => Array(N).fill(Infinity));
    const queue = [];

    const [goalI, goalJ] = configToGrid(goalConfig);

    if (goalI < 0 || goalI >= N || goalJ < 0 || goalJ >= N || cspace[goalI][goalJ]) {
        console.warn("Goal is in collision or out of bounds.");
        return;
    }

    distField[goalI][goalJ] = 0;
    queue.push([goalI, goalJ]);

    let head = 0;
    while (head < queue.length) {
        const [i, j] = queue[head++];

        for (let di = -1; di <= 1; di++) {
            for (let dj = -1; dj <= 1; dj++) {
                if (di === 0 && dj === 0) continue;

                const ni = i + di;
                const nj = j + dj;

                if (ni >= 0 && ni < N && nj >= 0 && nj < N && !cspace[ni][nj] && distField[ni][nj] === Infinity) {
                    distField[ni][nj] = distField[i][j] + Math.sqrt(di*di + dj*dj);
                    queue.push([ni, nj]);
                }
            }
        }
    }
}


function findPath() {
    path = [];
    const [startI, startJ] = configToGrid(startConfig);

    if (startI < 0 || startI >= N || startJ < 0 || startJ >= N || cspace[startI][startJ] || distField[startI][startJ] === Infinity) {
        console.warn("Start is in collision or unreachable.");
        return;
    }

    let [currI, currJ] = [startI, startJ];

    path.push(gridToConfig(currI, currJ));

    for (let k = 0; k < N * N; k++) { // Failsafe loop limit
        if (distField[currI][currJ] === 0) break;

        let bestNext = null;
        let minDist = Infinity;

        for (let di = -1; di <= 1; di++) {
            for (let dj = -1; dj <= 1; dj++) {
                if (di === 0 && dj === 0) continue;
                const ni = currI + di;
                const nj = currJ + dj;

                if (ni >= 0 && ni < N && nj >= 0 && nj < N && distField[ni][nj] < minDist) {
                    minDist = distField[ni][nj];
                    bestNext = [ni, nj];
                }
            }
        }

        if (bestNext) {
            [currI, currJ] = bestNext;
            path.push(gridToConfig(currI, currJ));
        } else {
            console.error("Pathfinding backtrack failed.");
            break;
        }
    }
}

function calculatePathLength() {
    if (!path || path.length < 2) {
        return 0;
    }
    let length = 0;
    for (let i = 0; i < path.length - 1; i++) {
        const p1 = path[i];
        const p2 = path[i + 1];
        length += Math.sqrt(Math.pow(p2.q1 - p1.q1, 2) + Math.pow(p2.q2 - p1.q2, 2));
    }
    return length;
}

function updatePathLengthDisplay() {
    const len = calculatePathLength();
    document.getElementById('phase-space-title').textContent = `robot phase space, path length = ${len.toFixed(2)} rads`;
}

function fullRecalculation() {
    console.time("Full Recalculation");
    calculateCSpace();
    runBFS();
    findPath();
    updatePathLengthDisplay();
    console.timeEnd("Full Recalculation");
}

function partialRecalculation() {
    findPath();
    updatePathLengthDisplay();
}

// --- p5.js SKETCHES ---

const workspaceSketchFn = (p) => {
    p.setup = () => {
        p.createCanvas(W, W).parent('canvas-workspace');
        p.angleMode(p.RADIANS);
    };

    p.draw = () => {
        p.background('#F0F0F0');

        // Interpolate robot config from path
        let currentConfig;
        if (path && path.length > 0) {
            if (path.length === 1) {
                currentConfig = path[0];
            } else {
                const pathP = P * (path.length - 1);
                const startIndex = p.floor(pathP);
                const endIndex = p.ceil(pathP);
                const t = pathP - startIndex;

                if (path[startIndex] && path[endIndex]) {
                    currentConfig = {
                        q1: p.lerp(path[startIndex].q1, path[endIndex].q1, t),
                        q2: p.lerp(path[startIndex].q2, path[endIndex].q2, t)
                    };
                } else {
                    currentConfig = goalConfig; // Fallback
                }
            }
        } else {
             // If no path, show start config
            currentConfig = startConfig;
        }
        const { q1, q2 } = currentConfig;

        // Draw robot arm
        p.push();
        p.translate(W / 2, W / 2);

        // Angle indicators
        p.noFill();
        p.stroke(150, 150, 255, 150);
        p.strokeWeight(2);
        p.arc(0, 0, 60, 60, 0, q1);
        p.stroke(255, 150, 150, 150);
        const p1x = L1 * p.cos(q1);
        const p1y = L1 * p.sin(q1);
        p.push();
        p.translate(p1x, p1y);
        p.rotate(q1);
        p.arc(0, 0, 50, 50, 0, q2);
        p.pop();

        // Arm links
        p.stroke(0);
        p.strokeWeight(1);
        p.fill(120);
        p.rotate(q1);
        p.rect(0, -LINK_RADIUS, L1, 2 * LINK_RADIUS, LINK_RADIUS);
        p.translate(L1, 0);
        p.rotate(q2);
        p.rect(0, -LINK_RADIUS, L2, 2 * LINK_RADIUS, LINK_RADIUS);

        // Text on link
        p.fill(255);
        p.noStroke();
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(10);
        p.text("Robot Arm", L2/2, 0);

        p.pop();

        // Draw obstacles
        obstacles.forEach((obs, i) => {
            p.stroke(100, 70, 40);
            p.strokeWeight(2);
            p.fill(205, 133, 63, 150); // Peru with alpha
            p.ellipse(obs.x, obs.y, obs.radius * 2, obs.radius * 2);
            // Pattern
            p.noFill();
            p.stroke(100, 70, 40, 100);
            p.strokeWeight(1);
            for(let r = obs.radius * 1.5; r > 0; r -= 5) {
                p.ellipse(obs.x, obs.y, r, r);
            }
        });
    };

    p.mousePressed = () => {
        if (p.dist(p.mouseX, p.mouseY, obstacles[0].x, obstacles[0].y) < obstacles[0].radius) {
            isDraggingObstacle1 = true;
        } else if (p.dist(p.mouseX, p.mouseY, obstacles[1].x, obstacles[1].y) < obstacles[1].radius) {
            isDraggingObstacle2 = true;
        }
    };

    p.mouseDragged = () => {
        if (isDraggingObstacle1 || isDraggingObstacle2) {
             if (p.mouseX > 0 && p.mouseX < W && p.mouseY > 0 && p.mouseY < W) {
                if (isDraggingObstacle1) {
                    obstacles[0].x = p.mouseX;
                    obstacles[0].y = p.mouseY;
                }
                if (isDraggingObstacle2) {
                    obstacles[1].x = p.mouseX;
                    obstacles[1].y = p.mouseY;
                }
                fullRecalculation();
            }
        }
    };

    p.mouseReleased = () => {
        isDraggingObstacle1 = false;
        isDraggingObstacle2 = false;
    };
};

const phaseSpaceSketchFn = (p) => {
    let cellSize;

    p.setup = () => {
        p.createCanvas(W, W).parent('canvas-phase-space');
        p.angleMode(p.RADIANS);
    };

    p.draw = () => {
        if (!cspace || !distField) return;
        cellSize = W / N;

        // Draw C-space grid and distance field
        p.noStroke();
        let maxDist = 0;
        for (let i = 0; i < N; i++) {
            for (let j = 0; j < N; j++) {
                if(distField[i][j] !== Infinity && distField[i][j] > maxDist) {
                    maxDist = distField[i][j];
                }
            }
        }

        for (let i = 0; i < N; i++) {
            for (let j = 0; j < N; j++) {
                if (cspace[i][j]) {
                    p.fill(204, 0, 0); // #CC0000
                } else if (distField[i][j] === Infinity) {
                    p.fill(40); // Dark gray for unreachable
                } else {
                    const brightness = p.map(distField[i][j], 0, maxDist, 240, 60);
                    p.fill(brightness);
                }
                p.rect(j * cellSize, i * cellSize, cellSize, cellSize);
            }
        }

        // Draw path
        if (path && path.length > 1) {
            p.stroke(0, 255, 0); // #00FF00
            p.strokeWeight(3);
            p.noFill();
            p.beginShape();
            path.forEach(config => {
                const [i, j] = configToGrid(config);
                p.vertex((j + 0.5) * cellSize, (i + 0.5) * cellSize);
            });
            p.endShape();
        }

        // Draw start/goal markers
        const drawMarker = (config, isStart) => {
            const [i, j] = configToGrid(config);
            const x = (j + 0.5) * cellSize;
            const y = (i + 0.5) * cellSize;
            const r = Math.max(8, cellSize * 0.8);

            p.strokeWeight(2);
            p.stroke(255);
            p.fill(0, 200, 0);
            if (isStart && isDraggingStart && cspace[i][j]) p.fill(255,0,0);
            if (!isStart && isDraggingGoal && cspace[i][j]) p.fill(255,0,0);
            p.ellipse(x, y, r, r);
            p.line(x - r/3, y, x + r/3, y);
            p.line(x, y - r/3, x, y + r/3);
        };
        drawMarker(startConfig, true);
        drawMarker(goalConfig, false);

        // Draw current position marker on path
        if (path && path.length > 0) {
            const pathP = P * (path.length - 1);
            const startIndex = p.floor(pathP);
            const endIndex = p.ceil(pathP);
            const t = pathP - startIndex;

            if (path[startIndex] && path[endIndex]) {
                const i_interp = p.lerp(configToGrid(path[startIndex])[0], configToGrid(path[endIndex])[0], t);
                const j_interp = p.lerp(configToGrid(path[startIndex])[1], configToGrid(path[endIndex])[1], t);
                const x = (j_interp + 0.5) * cellSize;
                const y = (i_interp + 0.5) * cellSize;
                p.fill(255, 255, 0);
                p.stroke(0);
                p.strokeWeight(1.5);
                p.ellipse(x, y, 10, 10);
            }
        }
    };

    const getGridPosFromMouse = () => {
        if (!cellSize) return null;
        const j = p.floor(p.mouseX / cellSize);
        const i = p.floor(p.mouseY / cellSize);
        if (i >= 0 && i < N && j >= 0 && j < N) {
            return [i, j];
        }
        return null;
    };

    p.mousePressed = () => {
        const gridPos = getGridPosFromMouse();
        if(!gridPos) return;

        const [startI, startJ] = configToGrid(startConfig);
        if (gridPos[0] === startI && gridPos[1] === startJ) {
            isDraggingStart = true;
        }

        const [goalI, goalJ] = configToGrid(goalConfig);
        if (gridPos[0] === goalI && gridPos[1] === goalJ) {
            isDraggingGoal = true;
        }
    };

    p.mouseDragged = () => {
        const gridPos = getGridPosFromMouse();
        if (!gridPos) return;

        const [i, j] = gridPos;
        const newConfig = gridToConfig(i,j);

        if (isDraggingStart && !cspace[i][j]) {
            startConfig = newConfig;
            partialRecalculation();
        } else if (isDraggingGoal && !cspace[i][j]) {
            goalConfig = newConfig;
            runBFS(); // Goal moved, need new distance field
            findPath();
            updatePathLengthDisplay();
        }
    };

    p.mouseReleased = () => {
        isDraggingStart = false;
        isDraggingGoal = false;
    };
};

// --- INITIALIZATION ---
function setupControls() {
    qSlider = document.getElementById('slider-q');
    pSlider = document.getElementById('slider-p');
    qValueSpan = document.getElementById('value-q');
    pValueSpan = document.getElementById('value-p');
    resetButton = document.getElementById('btn-reset');

    qSlider.oninput = (e) => {
        Q = parseInt(e.target.value);
        qValueSpan.textContent = Q;
        fullRecalculation();
    };

    pSlider.oninput = (e) => {
        P = parseFloat(e.target.value);
        pValueSpan.textContent = P.toFixed(2);
    };

    resetButton.onclick = initialize;
}

function initialize() {
    // Reset state to defaults
    Q = defaultState.q;
    P = defaultState.p;
    obstacles = JSON.parse(JSON.stringify(defaultState.obstacles));
    startConfig = { ...defaultState.startConfig };
    goalConfig = { ...defaultState.goalConfig };

    // Update UI controls
    qSlider.value = Q;
    pSlider.value = P;
    qValueSpan.textContent = Q;
    pValueSpan.textContent = P.toFixed(2);

    // Perform initial calculations
    fullRecalculation();
}

window.onload = () => {
    setupControls();
    initialize();

    // Instantiate p5 sketches
    workspaceSketch = new p5(workspaceSketchFn, 'canvas-workspace');
    phaseSpaceSketch = new p5(phaseSpaceSketchFn, 'canvas-phase-space');
};
</script>
</body>
</html>
