<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group" id="problem-group">
                <h4>problem</h4>
                <div>
                    <label>dimension</label>
                    <div class="button-group">
                        <button id="btn-2d">2D</button>
                        <button id="btn-3d">3D</button>
                    </div>
                </div>
                <div>
                    <div class="slider-label">
                        <label for="slider-seed">random seed</label>
                        <span id="seed-value">525</span>
                    </div>
                    <input type="range" id="slider-seed" min="0" max="1000" value="525" step="1">
                </div>
                <div>
                     <div class="slider-label">
                        <label for="slider-number">number</label>
                        <span id="number-value">40</span>
                    </div>
                    <input type="range" id="slider-number" min="5" max="150" value="40" step="1">
                </div>
            </div>
            <div class="control-group" id="method-group">
                <h4>method</h4>
                <div>
                    <div class="button-group">
                        <button id="btn-prim">Prim</button>
                        <button id="btn-kruskal">Kruskal</button>
                    </div>
                </div>
            </div>
            <div class="control-group" id="process-group">
                <h4>process</h4>
                <div>
                    <div class="button-group">
                        <button id="btn-play">►</button>
                        <button id="btn-pause">||</button>
                        <button id="btn-reset">|◄</button>
                    </div>
                </div>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        const sketch = (p) => {
            // State variables
            let dimension = '2D';
            let algorithm = 'prim';
            let animationState = 'stopped'; // 'stopped', 'playing', 'paused'

            let randomSeed = 525;
            let numPoints = 40;

            let points = [];
            let mstEdges = [];
            let animationSequence = [];
            let animationStep = 0;
            let animationFrameCounter = 0;
            const animationSpeed = 10; // Frames to wait before next step

            // DOM Elements
            let btn2d, btn3d, sliderSeed, seedValue, sliderNumber, numberValue;
            let btnPrim, btnKruskal, btnPlay, btnPause, btnReset;
            let canvasContainer;

            // DSU for Kruskal's
            class DSU {
                constructor(n) {
                    this.parent = Array.from({ length: n }, (_, i) => i);
                }
                find(i) {
                    if (this.parent[i] === i) return i;
                    return this.parent[i] = this.find(this.parent[i]);
                }
                union(i, j) {
                    const rootI = this.find(i);
                    const rootJ = this.find(j);
                    if (rootI !== rootJ) {
                        this.parent[rootI] = rootJ;
                        return true;
                    }
                    return false;
                }
            }

            p.setup = () => {
                canvasContainer = p.select('#canvas-container');
                const canvasSize = p.min(canvasContainer.width, canvasContainer.height) * 0.9;
                const canvas = p.createCanvas(canvasSize, canvasSize, dimension === '3D' ? p.WEBGL : p.P2D);
                canvas.parent(canvasContainer);

                // --- Get DOM Elements ---
                btn2d = p.select('#btn-2d');
                btn3d = p.select('#btn-3d');
                sliderSeed = p.select('#slider-seed');
                seedValue = p.select('#seed-value');
                sliderNumber = p.select('#slider-number');
                numberValue = p.select('#number-value');
                btnPrim = p.select('#btn-prim');
                btnKruskal = p.select('#btn-kruskal');
                btnPlay = p.select('#btn-play');
                btnPause = p.select('#btn-pause');
                btnReset = p.select('#btn-reset');

                // --- Attach Event Listeners ---
                btn2d.mousePressed(() => { dimension = '2D'; handleFullReset(); });
                btn3d.mousePressed(() => { dimension = '3D'; handleFullReset(); });

                sliderSeed.input(() => {
                    randomSeed = sliderSeed.value();
                    seedValue.html(randomSeed);
                });
                sliderSeed.changed(handleFullReset);

                sliderNumber.input(() => {
                    numPoints = sliderNumber.value();
                    numberValue.html(numPoints);
                });
                sliderNumber.changed(handleFullReset);

                btnPrim.mousePressed(() => { algorithm = 'prim'; handlePartialReset(); });
                btnKruskal.mousePressed(() => { algorithm = 'kruskal'; handlePartialReset(); });

                btnPlay.mousePressed(playAnimation);
                btnPause.mousePressed(pauseAnimation);
                btnReset.mousePressed(resetAnimation);

                // --- Initial Setup ---
                handleFullReset();
            };

            p.draw = () => {
                p.background('#E0E0E0');
                if (dimension === '3D') {
                    p.orbitControl();
                    p.stroke(150);
                    p.noFill();
                    p.box(p.width); // Wireframe cube to define space
                }

                drawPoints();
                drawEdges();

                if (animationState === 'playing') {
                    animationFrameCounter++;
                    if (animationFrameCounter >= animationSpeed) {
                        animationFrameCounter = 0;
                        if (animationStep < animationSequence.length) {
                            mstEdges.push(animationSequence[animationStep]);
                            animationStep++;
                        } else {
                            animationState = 'stopped';
                            updateUI();
                        }
                    }
                }
            };

            // --- Control Handlers ---
            function playAnimation() {
                if (animationState === 'stopped' || animationState === 'paused') {
                    // If starting from the beginning, pre-calculate the sequence
                    if (animationStep === 0) {
                        precalculateMST();
                    }
                    animationState = 'playing';
                    updateUI();
                }
            }

            function pauseAnimation() {
                if (animationState === 'playing') {
                    animationState = 'paused';
                    updateUI();
                }
            }

            function resetAnimation() {
                animationState = 'stopped';
                animationStep = 0;
                mstEdges = [];
                updateUI();
            }

            function handleFullReset() {
                if (p.canvas) p.canvas.remove();
                const canvasSize = p.min(canvasContainer.width, canvasContainer.height) * 0.9;
                const canvas = p.createCanvas(canvasSize, canvasSize, dimension === '3D' ? p.WEBGL : p.P2D);
                canvas.parent(canvasContainer);

                generatePoints();
                handlePartialReset();
            }

            function handlePartialReset() {
                resetAnimation();
                precalculateMST();
                updateUI(); // updateUI will be called by resetAnimation
            }

            // --- UI Update ---
            function updateUI() {
                // Dimension buttons
                btn2d.class(dimension === '2D' ? 'active' : '');
                btn3d.class(dimension === '3D' ? 'active' : '');

                // Algorithm buttons
                btnPrim.class(algorithm === 'prim' ? 'active' : '');
                btnKruskal.class(algorithm === 'kruskal' ? 'active' : '');

                // Process buttons
                const isFinished = animationStep >= animationSequence.length;
                btnPlay.elt.disabled = animationState === 'playing' || isFinished;
                btnPause.elt.disabled = animationState !== 'playing';
                btnReset.elt.disabled = false; // Always enabled as per plan

                // Disable config controls during animation
                const isAnimating = animationState === 'playing' || animationState === 'paused';
                btn2d.elt.disabled = isAnimating;
                btn3d.elt.disabled = isAnimating;
                sliderSeed.elt.disabled = isAnimating;
                sliderNumber.elt.disabled = isAnimating;
                btnPrim.elt.disabled = isAnimating;
                btnKruskal.elt.disabled = isAnimating;
            }

            // --- Point and MST Logic ---
            function generatePoints() {
                points = [];
                p.randomSeed(randomSeed);
                const margin = p.width * 0.1;
                const range = p.width - 2 * margin;

                for (let i = 0; i < numPoints; i++) {
                    if (dimension === '2D') {
                        let x = p.random(margin, p.width - margin);
                        let y = p.random(margin, p.height - margin);
                        points.push(p.createVector(x, y));
                    } else { // 3D
                        let x = p.random(-range / 2, range / 2);
                        let y = p.random(-range / 2, range / 2);
                        let z = p.random(-range / 2, range / 2);
                        points.push(p.createVector(x, y, z));
                    }
                }
            }

            function precalculateMST() {
                if (points.length === 0) return;

                if (algorithm === 'prim') {
                    animationSequence = calculatePrimSequence();
                } else { // kruskal
                    animationSequence = calculateKruskalSequence();
                }
            }

            function calculateKruskalSequence() {
                const edges = [];
                const sequence = [];

                for (let i = 0; i < points.length; i++) {
                    for (let j = i + 1; j < points.length; j++) {
                        const d = points[i].dist(points[j]);
                        edges.push({ u: i, v: j, weight: d });
                    }
                }

                edges.sort((a, b) => a.weight - b.weight);

                const dsu = new DSU(points.length);
                for (const edge of edges) {
                    if (dsu.union(edge.u, edge.v)) {
                        sequence.push(edge);
                        if (sequence.length === points.length - 1) break;
                    }
                }
                return sequence;
            }

            function calculatePrimSequence() {
                if (points.length < 2) return [];

                const sequence = [];
                const visited = new Array(points.length).fill(false);
                const minDist = new Array(points.length).fill(Infinity);
                const edgeTo = new Array(points.length).fill(null);

                minDist[0] = 0;

                for (let i = 0; i < points.length; i++) {
                    let u = -1;
                    // Find vertex with min distance not yet in tree
                    for (let v = 0; v < points.length; v++) {
                        if (!visited[v] && (u === -1 || minDist[v] < minDist[u])) {
                            u = v;
                        }
                    }

                    if (minDist[u] === Infinity) break;

                    visited[u] = true;
                    if (edgeTo[u] !== null) {
                        sequence.push({ u: edgeTo[u], v: u, weight: minDist[u] });
                    }

                    // Update distances of adjacent vertices
                    for (let v = 0; v < points.length; v++) {
                        if (!visited[v]) {
                            const weight = points[u].dist(points[v]);
                            if (weight < minDist[v]) {
                                minDist[v] = weight;
                                edgeTo[v] = u;
                            }
                        }
                    }
                }
                return sequence;
            }

            // --- Drawing Functions ---
            function drawPoints() {
                p.noStroke();
                p.fill('blue');
                for (const pt of points) {
                    if (dimension === '2D') {
                        p.ellipse(pt.x, pt.y, 8, 8);
                    } else { // 3D
                        p.push();
                        p.translate(pt.x, pt.y, pt.z);
                        p.sphere(8);
                        p.pop();
                    }
                }
            }

            function drawEdges() {
                p.stroke('red');
                p.strokeWeight(2);
                for (const edge of mstEdges) {
                    const p1 = points[edge.u];
                    const p2 = points[edge.v];
                    if (dimension === '2D') {
                        p.line(p1.x, p1.y, p2.x, p2.y);
                    } else { // 3D
                        p.line(p1.x, p1.y, p1.z, p2.x, p2.y, p2.z);
                    }
                }
            }
        };

        new p5(sketch);
    </script>
