    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Element References ---
            const selectMethod = document.getElementById('select-method');
            const sliderNum1 = document.getElementById('slider-num1');
            const btnIntYes = document.getElementById('btn-int-yes');
            const btnIntNo = document.getElementById('btn-int-no');
            const sliderNum2 = document.getElementById('slider-num2');
            const outputDisplay = document.getElementById('output-display');

            // --- Helper Functions ---

            /**
             * Handles floating point inaccuracies for results.
             * @param {number} num - The number to format.
             * @returns {number} The formatted number.
             */
            function formatResult(num) {
                return parseFloat(num.toPrecision(12));
            }

            /**
             * Converts a number into its integer representation and the required division factor.
             * e.g., 1.23 -> { int: 123, factor: 100 }, 5 -> { int: 5, factor: 1 }
             * @param {number} num - The input number.
             * @returns {{int: number, factor: number}}
             */
            function getIntegerAndFactor(num) {
                if (Number.isInteger(num)) {
                    return { int: num, factor: 1 };
                }
                const s = num.toString();
                const decimalPlaces = s.split('.')[1]?.length || 0;
                const factor = Math.pow(10, decimalPlaces);
                const int = Math.round(num * factor);
                return { int, factor };
            }

            // --- Core Logic ---

            function updateDisplay() {
                const method = selectMethod.value;
                const num1 = parseFloat(sliderNum1.value);
                const num2 = parseFloat(sliderNum2.value);

                let steps = [];

                if (method === 'estimation') {
                    const { int: i1 } = getIntegerAndFactor(num1);
                    const { int: i2 } = getIntegerAndFactor(num2);

                    steps.push(`start with: ${num1} \u00D7 ${num2}`);
                    steps.push(`convert to: ${i1}. \u00D7 ${i2}. = ${i1 * i2}.`);
                    steps.push(`estimate as: ${Math.round(num1)} \u00D7 ${Math.round(num2)} = ${Math.round(num1) * Math.round(num2)}`);
                    steps.push(`result is: ${formatResult(num1 * num2)}`);
                } else if (method === 'place values') {
                    const { int: i1, factor: f1 } = getIntegerAndFactor(num1);
                    const { int: i2, factor: f2 } = getIntegerAndFactor(num2);

                    steps.push(`start with: ${num1} \u00D7 ${num2}`);

                    const p1 = f1 > 1 ? `(${i1}. \u00F7 ${f1})` : `${i1}`;
                    const p2 = f2 > 1 ? `(${i2}. \u00F7 ${f2})` : `${i2}`;
                    steps.push(`rewrite as: ${p1} \u00D7 ${p2}`);

                    if (f1 > 1 || f2 > 1) {
                        steps.push(`= (${i1}. \u00D7 ${i2}.) \u00F7 (${f1} \u00D7 ${f2})`);
                        steps.push(`= ${i1 * i2}. \u00F7 ${f1 * f2}`);
                    }

                    steps.push(`= ${formatResult(num1 * num2)}`);
                }

                // Render the steps
                outputDisplay.innerHTML = '';
                steps.forEach(step => {
                    const p = document.createElement('p');
                    p.textContent = step;
                    outputDisplay.appendChild(p);
                });
            }

            // --- Event Listeners ---

            selectMethod.addEventListener('change', updateDisplay);
            sliderNum1.addEventListener('input', updateDisplay);
            sliderNum2.addEventListener('input', updateDisplay);

            btnIntYes.addEventListener('click', () => {
                btnIntYes.classList.add('active');
                btnIntNo.classList.remove('active');

                sliderNum2.step = '1';
                // Snap to the nearest integer
                sliderNum2.value = Math.round(parseFloat(sliderNum2.value));

                updateDisplay();
            });

            btnIntNo.addEventListener('click', () => {
                btnIntNo.classList.add('active');
                btnIntYes.classList.remove('active');

                sliderNum2.step = '0.1';

                updateDisplay();
            });

            // --- Initial State ---
            function initialize() {
                btnIntYes.classList.add('active');
                btnIntNo.classList.remove('inactive');
                updateDisplay();
            }

            initialize();
        });
    </script>
</body>
</html>
