<script type="module">
    import * as THREE from 'three';
    import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

    // --- DOM Elements ---
    const selectMode = document.getElementById('select-vibration-mode');
    const btnPlay = document.getElementById('btn-play');
    const btnPause = document.getElementById('btn-pause');
    const btnReset = document.getElementById('btn-reset');
    const canvasContainer = document.getElementById('three-js-canvas-container');
    const plotlyContainer = document.getElementById('plotly-chart-container');
    const descriptionArea = document.getElementById('description-area');
    const labelLeft = document.getElementById('label-left');
    const labelRight = document.getElementById('label-right');

    // --- State Variables ---
    let renderer, sceneMolecule, sceneVectors, cameraMolecule, cameraVectors, controlsMolecule, controlsVectors;
    let moleculeGroup, vectorsGroup;
    let animationId = null;
    let isPlaying = false;
    let time = 0;
    const atoms = { C: null, H: [] };
    const bonds = [];
    const dipoleArrows = [];
    let netDipoleArrow;
    let plotInitialized = false;
    let plotImages = {};

    // --- Constants ---
    const BOND_LENGTH = 1.5;
    const ATOM_RADIUS = { C: 0.3, H: 0.2 };
    const ANIM_AMPLITUDE = 0.3;
    const ANIM_SPEED = 0.05;

    const VEC3_UP = new THREE.Vector3(0, 1, 0);
    const H_INITIAL_POS = [
        new THREE.Vector3(1, 1, 1).normalize().multiplyScalar(BOND_LENGTH),
        new THREE.Vector3(1, -1, -1).normalize().multiplyScalar(BOND_LENGTH),
        new THREE.Vector3(-1, 1, -1).normalize().multiplyScalar(BOND_LENGTH),
        new THREE.Vector3(-1, -1, 1).normalize().multiplyScalar(BOND_LENGTH)
    ];

    const DISPLACEMENTS = {
        'symmetric-stretch': H_INITIAL_POS.map(p => p.clone().normalize()),
        'asymmetric-stretch': [
            new THREE.Vector3(1, 1, 1).normalize(),
            new THREE.Vector3(-1, -1, -1).normalize(),
            new THREE.Vector3(1, -1, -1).normalize().multiplyScalar(0.1),
            new THREE.Vector3(-1, 1, -1).normalize().multiplyScalar(0.1),
        ],
        'bending-e': [
            new THREE.Vector3(0, 0, 1),
            new THREE.Vector3(0, 0, -1),
            new THREE.Vector3(0, 0, -1),
            new THREE.Vector3(0, 0, 1),
        ],
        'bending-f2': [
            new THREE.Vector3(1, -1, 0).normalize(),
            new THREE.Vector3(-1, 1, 0).normalize(),
            new THREE.Vector3(-1, -1, 0).normalize(),
            new THREE.Vector3(1, 1, 0).normalize(),
        ]
    };

    const MODE_PROPERTIES = {
        'symmetric-stretch': {
            left: 'stretching A₁ Raman active 2916 cm⁻¹',
            right: 'no change in the molecular dipole moment',
            active: false
        },
        'asymmetric-stretch': {
            left: 'stretching F₂ IR active 3019 cm⁻¹',
            right: 'change in the molecular dipole moment',
            active: true
        },
        'bending-e': {
            left: 'bending E Raman active 1534 cm⁻¹',
            right: 'no change in the molecular dipole moment',
            active: false
        },
        'bending-f2': {
            left: 'bending F₂ IR active 1306 cm⁻¹',
            right: 'change in the molecular dipole moment',
            active: true
        }
    };

    // --- THREE.js Initialization ---
    function initThree() {
        // Scenes
        sceneMolecule = new THREE.Scene();
        sceneMolecule.background = new THREE.Color(0xffffff);
        sceneVectors = new THREE.Scene();
        sceneVectors.background = new THREE.Color(0xffffff);

        // Renderer
        renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        renderer.setScissorTest(true);
        canvasContainer.appendChild(renderer.domElement);

        // Cameras
        const aspect = (canvasContainer.clientWidth / 2) / canvasContainer.clientHeight;
        cameraMolecule = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
        cameraMolecule.position.set(0, 0, 6);
        cameraVectors = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
        cameraVectors.position.set(0, 0, 6);

        // Lights
        const ambientLight = new THREE.AmbientLight(0x707070);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
        directionalLight.position.set(1, 1, 2);
        sceneMolecule.add(ambientLight.clone());
        sceneMolecule.add(directionalLight.clone());
        sceneVectors.add(ambientLight.clone());
        sceneVectors.add(directionalLight.clone());

        // Controls
        controlsMolecule = new OrbitControls(cameraMolecule, renderer.domElement);
        controlsMolecule.enablePan = false;
        controlsMolecule.target.set(0, 0, 0);
        controlsVectors = new OrbitControls(cameraVectors, renderer.domElement);
        controlsVectors.enablePan = false;
        controlsVectors.target.set(0, 0, 0);

        // Create Molecule and Vectors
        createMolecule();
        createVectors();

        // Handle Resizing
        window.addEventListener('resize', onWindowResize, false);
    }

    function createMolecule() {
        moleculeGroup = new THREE.Group();
        // Carbon atom
        const cGeom = new THREE.SphereGeometry(ATOM_RADIUS.C, 32, 32);
        const cMat = new THREE.MeshLambertMaterial({ color: 0x222222 });
        atoms.C = new THREE.Mesh(cGeom, cMat);
        moleculeGroup.add(atoms.C);

        // Hydrogen atoms and bonds
        const hGeom = new THREE.SphereGeometry(ATOM_RADIUS.H, 32, 32);
        const hMat = new THREE.MeshLambertMaterial({ color: 0xadd8e6, emissive: 0x222233 });
        const bondMat = new THREE.MeshBasicMaterial({ color: 0x888888 });

        H_INITIAL_POS.forEach(pos => {
            const hAtom = new THREE.Mesh(hGeom, hMat);
            hAtom.position.copy(pos);
            atoms.H.push(hAtom);
            moleculeGroup.add(hAtom);

            const bond = createCylinder(atoms.C.position, hAtom.position, bondMat);
            bonds.push(bond);
            moleculeGroup.add(bond);
        });

        sceneMolecule.add(moleculeGroup);
    }

    function createVectors() {
        vectorsGroup = new THREE.Group();
        const origin = new THREE.Vector3(0, 0, 0);

        for (let i = 0; i < 4; i++) {
            const dir = H_INITIAL_POS[i].clone().normalize();
            const arrow = new THREE.ArrowHelper(dir, origin, BOND_LENGTH, 0x0000ff);
            dipoleArrows.push(arrow);
            vectorsGroup.add(arrow);
        }

        netDipoleArrow = new THREE.ArrowHelper(new THREE.Vector3(1, 0, 0), origin, 0, 0xff0000);
        netDipoleArrow.visible = false;
        vectorsGroup.add(netDipoleArrow);

        sceneVectors.add(vectorsGroup);
    }

    function createCylinder(start, end, mat) {
        const dir = end.clone().sub(start);
        const length = dir.length();
        dir.normalize();
        const geom = new THREE.CylinderGeometry(0.05, 0.05, length, 8);
        const mesh = new THREE.Mesh(geom, mat);
        mesh.quaternion.setFromUnitVectors(VEC3_UP, dir);
        mesh.position.copy(start).add(end).multiplyScalar(0.5);
        return mesh;
    }

    function updateCylinder(cylinder, start, end) {
        const dir = end.clone().sub(start);
        const length = dir.length();
        dir.normalize();
        cylinder.scale.y = length;
        cylinder.quaternion.setFromUnitVectors(VEC3_UP, dir);
        cylinder.position.copy(start).add(end).multiplyScalar(0.5);
    }

    // --- Animation Logic ---
    function animate() {
        if (!isPlaying) return;
        animationId = requestAnimationFrame(animate);
        time += ANIM_SPEED;

        updatePositions(time);
        render();
    }

    function updatePositions(t) {
        const mode = selectMode.value;
        const displacementVectors = DISPLACEMENTS[mode];
        const oscillation = Math.sin(t);

        const currentDipoles = [];

        atoms.H.forEach((hAtom, i) => {
            const displacement = displacementVectors[i].clone().multiplyScalar(ANIM_AMPLITUDE * oscillation);
            hAtom.position.copy(H_INITIAL_POS[i]).add(displacement);
            updateCylinder(bonds[i], atoms.C.position, hAtom.position);

            const currentBondVector = hAtom.position.clone();
            currentDipoles.push(currentBondVector);

            dipoleArrows[i].setDirection(currentBondVector.normalize());
            dipoleArrows[i].setLength(currentBondVector.length());
        });

        const netDipole = currentDipoles.reduce((sum, vec) => sum.add(vec), new THREE.Vector3());
        if (netDipole.length() > 0.01) {
            netDipoleArrow.setDirection(netDipole.clone().normalize());
            netDipoleArrow.setLength(netDipole.length() * 2, 0.4, 0.2); // Scale up for visibility
            netDipoleArrow.visible = true;
        } else {
            netDipoleArrow.visible = false;
        }
    }

    function render() {
        if (!renderer) return;

        controlsMolecule.update();
        controlsVectors.update();

        const w = canvasContainer.clientWidth;
        const h = canvasContainer.clientHeight;
        const halfW = w / 2;

        renderer.setScissor(0, 0, halfW, h);
        renderer.setViewport(0, 0, halfW, h);
        renderer.render(sceneMolecule, cameraMolecule);

        renderer.setScissor(halfW, 0, halfW, h);
        renderer.setViewport(halfW, 0, halfW, h);
        renderer.render(sceneVectors, cameraVectors);
    }

    function resetAnimation() {
        isPlaying = false;
        if (animationId) cancelAnimationFrame(animationId);
        animationId = null;
        time = 0;
        updatePositions(0); // Reset to initial state
        render(); // Render the reset state
    }

    function onWindowResize() {
        const w = canvasContainer.clientWidth;
        const h = canvasContainer.clientHeight;
        const aspect = (w / 2) / h;

        if (renderer) {
            renderer.setSize(w, h);
            cameraMolecule.aspect = aspect;
            cameraMolecule.updateProjectionMatrix();
            cameraVectors.aspect = aspect;
            cameraVectors.updateProjectionMatrix();
            render();
        }

        if (plotInitialized) {
            Plotly.Plots.resize(plotlyContainer);
        }
    }

    // --- Plotly Logic ---
    async function renderSpectrum() {
        if (!plotImages.f2_stretch || !plotImages.f2_bend) {
            plotImages.f2_stretch = generatePoseImage('asymmetric-stretch');
            plotImages.f2_bend = generatePoseImage('bending-f2');
        }

        const wavenumber = [];
        for (let i = 400; i <= 4000; i += 2) {
            wavenumber.push(i);
        }

        const transmittance = wavenumber.map(x => {
            let y = 0.95 + (Math.random() - 0.5) * 0.02; // Baseline with noise

            // F2 bend peak at 1306
            y -= 0.8 * Math.exp(-Math.pow(x - 1306, 2) / (2 * Math.pow(20, 2)));
            // F2 stretch peak at 3019
            y -= 0.7 * Math.exp(-Math.pow(x - 3019, 2) / (2 * Math.pow(25, 2)));

            // Rotational fine structure simulation
            if(Math.abs(x - 1306) < 100 || Math.abs(x - 3019) < 150) {
                 y -= Math.abs(Math.sin(x/5)) * 0.05 * Math.random();
            }

            return Math.max(0.05, Math.min(1.0, y));
        });

        const trace = {
            x: wavenumber,
            y: transmittance,
            type: 'scatter',
            mode: 'lines',
            line: { color: '#333', width: 1 }
        };

        const layout = {
            title: 'Methane (CH₄) IR Spectrum',
            xaxis: {
                title: 'wavenumber (cm⁻¹)',
                range: [4000, 400],
                autorange: 'reversed'
            },
            yaxis: {
                title: 'Transmittance',
                range: [0, 1.1]
            },
            annotations: [
                {
                    x: 1306, y: 0.1,
                    xref: 'x', yref: 'y',
                    text: '<b>ν F₂</b><br>1306 cm⁻¹',
                    font: {color: '#d9534f'},
                    showarrow: true, arrowhead: 2, ax: 0, ay: -60
                },
                {
                    x: 3019, y: 0.2,
                    xref: 'x', yref: 'y',
                    text: '<b>ν F₂</b><br>3019 cm⁻¹',
                    font: {color: '#d9534f'},
                    showarrow: true, arrowhead: 2, ax: 0, ay: -60
                }
            ],
            images: [
                {
                    source: plotImages.f2_bend,
                    xref: "x", yref: "paper",
                    x: 1306, y: 0.6,
                    sizex: 400, sizey: 0.2,
                    xanchor: "center", yanchor: "middle"
                },
                 {
                    source: plotImages.f2_stretch,
                    xref: "x", yref: "paper",
                    x: 3019, y: 0.6,
                    sizex: 400, sizey: 0.2,
                    xanchor: "center", yanchor: "middle"
                }
            ],
            margin: { t: 50, l: 60, r: 20, b: 60 },
            autosize: true
        };

        await Plotly.newPlot('plotly-chart-container', [trace], layout, {responsive: true});
        plotInitialized = true;
    }

    function generatePoseImage(mode) {
        const size = 100;
        const tempRenderer = new THREE.WebGLRenderer({ alpha: true, antialias: true });
        tempRenderer.setSize(size, size);

        const tempScene = new THREE.Scene();
        const tempCamera = new THREE.PerspectiveCamera(50, 1, 0.1, 100);
        tempCamera.position.set(0, 0, 6);
        tempScene.add(new THREE.AmbientLight(0xffffff, 1.5));

        const poseGroup = new THREE.Group();
        const cGeom = new THREE.SphereGeometry(ATOM_RADIUS.C, 16, 16);
        const cMat = new THREE.MeshLambertMaterial({ color: 0x222222 });
        const cAtom = new THREE.Mesh(cGeom, cMat);
        poseGroup.add(cAtom);

        const hGeom = new THREE.SphereGeometry(ATOM_RADIUS.H, 16, 16);
        const hMat = new THREE.MeshLambertMaterial({ color: 0xadd8e6 });
        const bondMat = new THREE.MeshBasicMaterial({ color: 0x888888 });

        const displacementVectors = DISPLACEMENTS[mode];
        const oscillation = 1.0; 

        H_INITIAL_POS.forEach((pos, i) => {
            const hAtom = new THREE.Mesh(hGeom, hMat);
            const displacement = displacementVectors[i].clone().multiplyScalar(ANIM_AMPLITUDE * oscillation);
            hAtom.position.copy(pos).add(displacement);
            poseGroup.add(hAtom);

            const bond = createCylinder(cAtom.position, hAtom.position, bondMat);
            poseGroup.add(bond);

            // Add displacement arrows for clarity
            if (displacement.length() > 0.01) {
                 const arrow = new THREE.ArrowHelper(displacement.normalize(), pos, displacement.length()*3, 0xff0000, 0.3, 0.15);
                 poseGroup.add(arrow);
            }
        });

        tempScene.add(poseGroup);
        tempRenderer.render(tempScene, tempCamera);

        const dataURL = tempRenderer.domElement.toDataURL();
        tempRenderer.dispose();
        return dataURL;
    }

    // --- UI Control ---
    function updateView(mode) {
        resetAnimation();

        if (mode === 'ir-spectrum') {
            canvasContainer.style.display = 'none';
            descriptionArea.style.display = 'none';
            plotlyContainer.style.display = 'block';

            [btnPlay, btnPause, btnReset].forEach(btn => btn.disabled = true);

            if (!plotInitialized) {
                renderSpectrum();
            } else {
                Plotly.Plots.resize(plotlyContainer);
            }
        } else {
            canvasContainer.style.display = 'block';
            descriptionArea.style.display = 'flex';
            plotlyContainer.style.display = 'none';

            [btnPlay, btnPause, btnReset].forEach(btn => btn.disabled = false);

            updateDescription(mode);
            requestAnimationFrame(render); // redraw once
        }
    }

    function updateDescription(mode) {
        const props = MODE_PROPERTIES[mode];
        if (!props) return;

        labelLeft.textContent = props.left;
        labelRight.textContent = props.right;

        const className = props.active ? 'ir-active' : 'ir-inactive';
        labelLeft.className = className;
        labelRight.className = className;
    }

    // --- Event Listeners ---
    selectMode.addEventListener('change', (e) => {
        updateView(e.target.value);
    });

    btnPlay.addEventListener('click', () => {
        if (!isPlaying) {
            isPlaying = true;
            animate();
        }
    });

    btnPause.addEventListener('click', () => {
        isPlaying = false;
        if (animationId) {
            cancelAnimationFrame(animationId);
            animationId = null;
        }
    });

    btnReset.addEventListener('click', () => {
        resetAnimation();
    });

    // --- Initial Load ---
    initThree();
    updateView(selectMode.value);

</script>

</body>
</html>
