    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // --- DOM Element References ---
            const timeSlider = document.getElementById('slider-time');
            const timeValueSpan = document.getElementById('span-time-value');
            const resetButton = document.getElementById('btn-reset');
            const materialCanvas = document.getElementById('canvas-material-bar');
            const plotDiv = document.getElementById('div-plot');
            const ctx = materialCanvas.getContext('2d');

            // --- Constants and Physical Model ---
            const CANVAS_WIDTH = materialCanvas.width;
            const CANVAS_HEIGHT = materialCanvas.height;
            const BAR_HEIGHT = 50; // Height of the colored bar
            const TOTAL_LENGTH_MM = 16;
            const PIXELS_PER_MM = CANVAS_WIDTH / TOTAL_LENGTH_MM;
            const GLASS_END_MM = 6;
            const GLASS_END_PX = GLASS_END_MM * PIXELS_PER_MM;

            /**
             * Calculates the Stefan distance (s) in mm for a given time (t) in seconds.
             * @param {number} t - Time in seconds.
             * @returns {number} Stefan distance in mm.
             */
            const stefanDistance = (t) => 0.155 * Math.sqrt(t);

            // --- Visualization Functions ---

            /**
             * Draws the material bar (Glass-Water-Ice) on the canvas for a given time.
             * @param {number} t - Current time in seconds.
             */
            function drawMaterialBar(t) {
                const s_mm = stefanDistance(t);
                const water_width_px = s_mm * PIXELS_PER_MM;
                const ice_start_px = GLASS_END_PX + water_width_px;

                // 1. Clear canvas
                ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

                // 2. Draw Glass Block (Hot Plate)
                const glassGradient = ctx.createLinearGradient(0, 0, GLASS_END_PX, 0);
                glassGradient.addColorStop(0, '#FF0000'); // Red (hot)
                glassGradient.addColorStop(1, '#FFFFFF'); // White (at interface)
                ctx.fillStyle = glassGradient;
                ctx.fillRect(0, 0, GLASS_END_PX, BAR_HEIGHT);

                // 3. Draw Melted Water Layer
                ctx.fillStyle = '#00BFFF'; // Light Blue
                ctx.fillRect(GLASS_END_PX, 0, water_width_px, BAR_HEIGHT);

                // 4. Draw Ice Block
                ctx.fillStyle = '#003366'; // Dark Blue
                ctx.fillRect(ice_start_px, 0, CANVAS_WIDTH - ice_start_px, BAR_HEIGHT);

                // 5. Draw Axis
                ctx.strokeStyle = '#333';
                ctx.lineWidth = 1;
                ctx.beginPath();
                ctx.moveTo(0, BAR_HEIGHT + 5);
                ctx.lineTo(CANVAS_WIDTH, BAR_HEIGHT + 5);
                ctx.stroke();

                // Axis Labels
                ctx.fillStyle = '#333';
                ctx.font = '12px Arial';
                ctx.textAlign = 'center';
                for (let mm = 0; mm <= 15; mm += 5) {
                    const x_pos = mm * PIXELS_PER_MM;
                    ctx.fillText(mm.toString(), x_pos, BAR_HEIGHT + 20);
                    // Tick mark
                    ctx.beginPath();
                    ctx.moveTo(x_pos, BAR_HEIGHT + 5);
                    ctx.lineTo(x_pos, BAR_HEIGHT + 10);
                    ctx.stroke();
                }
            }

            /**
             * Initializes the Plotly chart.
             * @param {number} initialTime - The time to set the initial indicator line.
             */
            function initPlot(initialTime) {
                // Generate data for the main curve s(t)
                const t_values = [];
                const s_values = [];
                for (let t = 0; t <= 100; t += 0.1) {
                    t_values.push(t);
                    s_values.push(stefanDistance(t));
                }

                // Trace for the s(t) curve
                const curveTrace = {
                    x: t_values,
                    y: s_values,
                    mode: 'lines',
                    name: 's(t)',
                    line: {
                        color: '#FFA500', // Orange
                        width: 3
                    }
                };

                const initial_s = stefanDistance(initialTime);

                // Layout with titles, ranges, and the initial indicator line
                const layout = {
                    title: 'Stefan distance versus time',
                    xaxis: {
                        title: 'time (s)',
                        range: [0, 100]
                    },
                    yaxis: {
                        title: 'distance (mm)',
                        range: [0, 1.6]
                    },
                    shapes: [{
                        type: 'line',
                        x0: initialTime,
                        y0: 0,
                        x1: initialTime,
                        y1: initial_s,
                        line: {
                            color: '#000000',
                            width: 2,
                            dash: 'dash'
                        }
                    }],
                    showlegend: false,
                    margin: { t: 40, l: 50, r: 20, b: 40 }
                };

                Plotly.newPlot('div-plot', [curveTrace], layout, {responsive: true});
            }

            /**
             * Master update function called on user interaction.
             * @param {number} currentTime - The new time from the slider.
             */
            function updateVisuals(currentTime) {
                // 1. Update numeric display
                timeValueSpan.textContent = parseFloat(currentTime).toFixed(1);

                // 2. Redraw material bar
                drawMaterialBar(currentTime);

                // 3. Update Plotly indicator line
                const s_val = stefanDistance(currentTime);
                Plotly.relayout('div-plot', {
                    'shapes[0].x0': currentTime,
                    'shapes[0].x1': currentTime,
                    'shapes[0].y1': s_val
                });
            }

            // --- Event Listeners ---

            // Slider interaction
            timeSlider.addEventListener('input', (event) => {
                updateVisuals(event.target.value);
            });

            // Reset button click
            resetButton.addEventListener('click', () => {
                timeSlider.value = 0;
                // Manually trigger the input event to sync all visuals
                timeSlider.dispatchEvent(new Event('input'));
            });

            // --- Initial Page Load ---
            const initialTime = parseFloat(timeSlider.value);
            initPlot(initialTime); // Renders the plot with indicator at t=9.8
            drawMaterialBar(initialTime); // Renders the canvas at t=9.8
        });
    </script>
