    <script>
        // --- DOM Element References ---
        const dateSlider = document.getElementById('date-slider');
        const dateDisplay = document.getElementById('date-display');
        const todayButton = document.getElementById('today-button');
        const localTimeDigital = document.getElementById('local-time-digital');
        const solarTimeDigital = document.getElementById('solar-time-digital');
        const offsetDisplay = document.getElementById('offset-display');

        // --- State Variables ---
        let userLatitude = 51.5;  // Default: London
        let userLongitude = 0;    // Default: Greenwich
        let localP5, solarP5;

        // --- Helper Functions ---
        const padZero = (num) => num.toString().padStart(2, '0');

        const getDayOfYear = (date) => {
            const start = new Date(date.getFullYear(), 0, 0);
            const diff = date - start;
            const oneDay = 1000 * 60 * 60 * 24;
            return Math.floor(diff / oneDay) - 1; // 0-indexed
        };

        const getDateFromDay = (year, day) => {
            const date = new Date(year, 0); // initialize a date in `year-01-01`
            return new Date(date.setDate(day + 1)); // day is 0-indexed
        };

        const formatDateForDisplay = (date) => {
            const options = { weekday: 'long', day: 'numeric', month: 'long' };
            return date.toLocaleDateString('en-US', options);
        };

        const updateDateDisplay = () => {
            const dayOfYear = parseInt(dateSlider.value);
            const currentYear = new Date().getFullYear();
            const date = getDateFromDay(currentYear, dayOfYear);
            dateDisplay.textContent = formatDateForDisplay(date);
        };

        // --- Core Calculation Logic ---
        const calculateSolarTime = (localTime, dayOfYear, longitude) => {
            // 1. Equation of Time (EoT) calculation
            const B = (360 / 365.24) * (dayOfYear - 81) * (Math.PI / 180); // in radians
            const eotMinutes = 9.87 * Math.sin(2 * B) - 7.53 * Math.cos(B) - 1.5 * Math.sin(B);

            // 2. Calculate Solar Time from UTC
            const utcTimeMillis = localTime.getTime() + localTime.getTimezoneOffset() * 60000;
            const timeOffsetMinutes = eotMinutes + 4 * longitude;
            const solarTimeMillis = utcTimeMillis + timeOffsetMinutes * 60000;

            return new Date(solarTimeMillis);
        };

        // --- P5.js Clock Drawing Logic ---
        const clockSketch = (p) => {
            let currentTime = new Date();
            let faceColor = '#FFFFFF';

            p.setup = () => {
                p.createCanvas(250, 250);
                p.angleMode(p.DEGREES);
                p.noLoop(); // We'll manually call redraw
            };

            p.update = (time, color) => {
                currentTime = time;
                faceColor = color;
                p.redraw();
            };

            p.draw = () => {
                p.clear();
                p.translate(p.width / 2, p.height / 2);
                p.rotate(-90); // Start from the top

                const hr = currentTime.getHours();
                const mn = currentTime.getMinutes();
                const sc = currentTime.getSeconds();
                const ms = currentTime.getMilliseconds();

                // Clock face
                p.strokeWeight(4);
                p.stroke(0);
                p.fill(faceColor);
                p.ellipse(0, 0, 240, 240);

                // Markers
                p.strokeWeight(2);
                for (let i = 0; i < 60; i++) {
                    const angle = p.map(i, 0, 60, 0, 360);
                    const isHour = i % 5 === 0;
                    const len = isHour ? 10 : 5;
                    const weight = isHour ? 4 : 2;
                    p.strokeWeight(weight);
                    p.line(100, 0, 100 + len, 0);
                    p.rotate(6);
                }

                // Numbers
                p.push();
                p.rotate(90); // Reset rotation for text
                p.noStroke();
                p.fill(0);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(20);
                for (let i = 1; i <= 12; i++) {
                    const angle = p.map(i, 0, 12, 0, 360);
                    const x = 85 * p.cos(angle - 90);
                    const y = 85 * p.sin(angle - 90);
                    p.text(i, x, y);
                }
                p.pop();

                // Hands
                // Hour hand
                p.push();
                const hrAngle = p.map(hr % 12 + mn / 60, 0, 12, 0, 360);
                p.rotate(hrAngle);
                p.strokeWeight(8);
                p.stroke(0);
                p.line(0, 0, 50, 0);
                p.pop();

                // Minute hand
                p.push();
                const mnAngle = p.map(mn + sc / 60, 0, 60, 0, 360);
                p.rotate(mnAngle);
                p.strokeWeight(5);
                p.stroke(0);
                p.line(0, 0, 80, 0);
                p.pop();

                // Second hand
                p.push();
                const scAngle = p.map(sc + ms/1000, 0, 60, 0, 360);
                p.rotate(scAngle);
                p.strokeWeight(2);
                p.stroke(255, 0, 0);
                p.line(-20, 0, 100, 0);
                p.pop();

                // Center pin
                p.fill(0);
                p.noStroke();
                p.ellipse(0, 0, 10, 10);
            };
        };

        // --- Main Update Loop ---
        const updateClocks = () => {
            const localTime = new Date();
            const dayOfYear = parseInt(dateSlider.value);
            const solarTime = calculateSolarTime(localTime, dayOfYear, userLongitude);

            // Update digital displays
            localTimeDigital.textContent = `${padZero(localTime.getHours())}:${padZero(localTime.getMinutes())}:${padZero(localTime.getSeconds())}`;
            solarTimeDigital.textContent = `${padZero(solarTime.getHours())}:${padZero(solarTime.getMinutes())}:${padZero(solarTime.getSeconds())}`;

            // Update analog clocks
            if (localP5 && solarP5) {
                localP5.update(localTime, '#E6E6FA'); // Lavender
                solarP5.update(solarTime, '#FAFAD2'); // LightGoldenRodYellow
            }

            // Update offset display
            const offsetMinutes = Math.round((solarTime.getTime() - localTime.getTime()) / 60000);
            offsetDisplay.textContent = `offset of solar time from local time: ${offsetMinutes} minutes`;
        };

        // --- Event Listeners Setup ---
        dateSlider.addEventListener('input', updateDateDisplay);

        todayButton.addEventListener('click', () => {
            const now = new Date();
            const todayDayOfYear = getDayOfYear(now);
            dateSlider.value = todayDayOfYear;
            updateDateDisplay();
            // The main interval will pick up the change on its next tick
        });

        // --- Initialization ---
        const initializeApp = () => {
            console.log(`Using coordinates: Lat ${userLatitude.toFixed(2)}, Lon ${userLongitude.toFixed(2)}`);

            // Set initial slider and date display
            updateDateDisplay();

            // Instantiate p5 sketches
            localP5 = new p5(clockSketch, 'local-clock-canvas');
            solarP5 = new p5(clockSketch, 'solar-clock-canvas');

            // Start the main update loop
            updateClocks(); // Initial call to draw immediately
            setInterval(updateClocks, 1000);
        };

        window.onload = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        userLatitude = position.coords.latitude;
                        userLongitude = position.coords.longitude;
                        initializeApp();
                    },
                    (error) => {
                        console.warn(`Geolocation error (${error.code}): ${error.message}. Using default location.`);
                        initializeApp(); // Use default coordinates
                    }
                );
            } else {
                console.warn("Geolocation is not supported by this browser. Using default location.");
                initializeApp(); // Use default coordinates
            }
        };

    </script>

</body>
