        </div>
    </div>

    <script>
        const sketch = (p) => {
            // --- STATE VARIABLES ---
            let currentView = 'diagram';

            // Checkbox states
            let showGridLines = false;
            let showTieLines = true;
            let showAlphaPhase = true;
            let showBetaPhase = true;

            // Interactive point
            let userPoint = { xA: 0.42, xB: 0.47, xC: 0.11 };

            // --- GEOMETRY & CONSTANTS ---
            let canvasSize, margin, triHeight, P_A, P_B, P_C;
            const C_A = '#008800'; // Green
            const C_B = '#0000FF'; // Blue
            const C_C = '#FF0000'; // Red
            const C_ALPHA = '#FFA500'; // Orange
            const C_BETA = '#FF00FF'; // Magenta

            // --- DATA (Hardcoded) ---
            // Phase envelope data points (xA, xB, xC)
            const alphaCurvePoints = [
                { xA: 0.02, xB: 0.9, xC: 0.08 }, { xA: 0.05, xB: 0.8, xC: 0.15 },
                { xA: 0.1, xB: 0.7, xC: 0.2 }, { xA: 0.15, xB: 0.6, xC: 0.25 },
                { xA: 0.2, xB: 0.5, xC: 0.3 }, { xA: 0.25, xB: 0.4, xC: 0.35 },
                { xA: 0.3, xB: 0.3, xC: 0.4 }, { xA: 0.34, xB: 0.24, xC: 0.42 } // Plait point
            ];
            const betaCurvePoints = [
                { xA: 0.02, xB: 0.08, xC: 0.9 }, { xA: 0.05, xB: 0.1, xC: 0.85 },
                { xA: 0.1, xB: 0.12, xC: 0.78 }, { xA: 0.15, xB: 0.14, xC: 0.71 },
                { xA: 0.2, xB: 0.16, xC: 0.64 }, { xA: 0.25, xB: 0.18, xC: 0.57 },
                { xA: 0.3, xB: 0.21, xC: 0.49 }, { xA: 0.34, xB: 0.24, xC: 0.42 } // Plait point
            ].reverse(); // Reverse to draw from plait point down to C-rich side

            const phaseEnvelopePolygon = [...alphaCurvePoints, ...betaCurvePoints];

            // Tie lines connecting alpha and beta curve points
            const tieLines = [
                { alpha: alphaCurvePoints[0], beta: betaCurvePoints[7] },
                { alpha: alphaCurvePoints[1], beta: betaCurvePoints[6] },
                { alpha: alphaCurvePoints[2], beta: betaCurvePoints[5] },
                { alpha: alphaCurvePoints[3], beta: betaCurvePoints[4] },
                { alpha: alphaCurvePoints[4], beta: betaCurvePoints[3] },
                { alpha: alphaCurvePoints[5], beta: betaCurvePoints[2] },
                { alpha: alphaCurvePoints[6], beta: betaCurvePoints[1] },
            ];

            // Pre-calculate midpoints for interpolation
            tieLines.forEach(tl => {
                tl.mid = {
                    xA: (tl.alpha.xA + tl.beta.xA) / 2,
                    xB: (tl.alpha.xB + tl.beta.xB) / 2,
                    xC: (tl.alpha.xC + tl.beta.xC) / 2
                };
            });

            // --- p5.js LIFECYCLE ---
            p.setup = () => {
                const containerWidth = document.getElementById('visualization').clientWidth;
                canvasSize = Math.min(containerWidth, 600);
                margin = canvasSize * 0.15;

                const c = p.createCanvas(canvasSize, canvasSize);
                c.parent('canvas-container');
                c.mousePressed(handleCanvasClick);

                // Define triangle vertices in pixel space
                const side = canvasSize - 2 * margin;
                triHeight = side * Math.sqrt(3) / 2;
                const topY = (canvasSize - triHeight) / 2;

                P_A = p.createVector(canvasSize / 2, topY);
                P_B = p.createVector(margin, topY + triHeight);
                P_C = p.createVector(margin + side, topY + triHeight);

                p.noLoop();
                setupUI();
                updateAndRedraw();
            };

            p.draw = () => {
                p.background(255);

                drawTriangleAxes();

                if (currentView === 'diagram') {
                    if (showGridLines) drawGridLines();
                    drawPhaseEnvelope();
                    if (showTieLines) drawPredefinedTieLines();

                    const userPointCartesian = barycentricToCartesian(userPoint.xA, userPoint.xB, userPoint.xC);
                    const isTwoPhase = isInsidePolygon(userPointCartesian, phaseEnvelopePolygon);

                    drawUserPoint(userPoint, userPointCartesian);

                    if (isTwoPhase) {
                        handleTwoPhaseRegion(userPoint);
                    }
                    updateInfoBoxes(userPoint, isTwoPhase);

                } else if (currentView === 'phases') {
                    drawPhasesView(userPoint);
                    updateInfoBoxes(userPoint, false); // Hide phase boxes in this view
                }
            };

            // --- UI & EVENT HANDLING ---
            const setupUI = () => {
                // Buttons
                document.getElementById('btn-diagram').onclick = () => switchView('diagram');
                document.getElementById('btn-phases').onclick = () => switchView('phases');

                // Checkboxes
                const chkGrid = document.getElementById('check-grid-lines');
                chkGrid.checked = showGridLines;
                chkGrid.onchange = (e) => { showGridLines = e.target.checked; p.redraw(); };

                const chkTie = document.getElementById('check-tie-lines');
                chkTie.checked = showTieLines;
                chkTie.onchange = (e) => { showTieLines = e.target.checked; p.redraw(); };

                const chkAlpha = document.getElementById('check-alpha-phase');
                chkAlpha.checked = showAlphaPhase;
                chkAlpha.onchange = (e) => { showAlphaPhase = e.target.checked; p.redraw(); };

                const chkBeta = document.getElementById('check-beta-phase');
                chkBeta.checked = showBetaPhase;
                chkBeta.onchange = (e) => { showBetaPhase = e.target.checked; p.redraw(); };

                updateButtonStyles();
            };

            const switchView = (view) => {
                currentView = view;
                const checkboxes = document.getElementById('controls-checkboxes');
                checkboxes.style.display = (view === 'diagram') ? 'flex' : 'none';
                updateButtonStyles();
                p.redraw();
            };

            const updateButtonStyles = () => {
                document.getElementById('btn-diagram').classList.toggle('active', currentView === 'diagram');
                document.getElementById('btn-phases').classList.toggle('active', currentView === 'phases');
            };

            const handleCanvasClick = () => {
                const mouse = p.createVector(p.mouseX, p.mouseY);
                if (isInsideTriangle(mouse)) {
                    const newComp = cartesianToBarycentric(mouse);
                    // Clamp values to be non-negative
                    userPoint.xA = Math.max(0, newComp.xA);
                    userPoint.xB = Math.max(0, newComp.xB);
                    userPoint.xC = Math.max(0, newComp.xC);
                    // Re-normalize
                    const sum = userPoint.xA + userPoint.xB + userPoint.xC;
                    userPoint.xA /= sum;
                    userPoint.xB /= sum;
                    userPoint.xC /= sum;

                    updateAndRedraw();
                }
            };

            const updateAndRedraw = () => {
                p.redraw();
            };

            // --- DRAWING FUNCTIONS ---

            const drawTriangleAxes = () => {
                p.strokeWeight(2);
                p.stroke(0);
                p.noFill();
                p.triangle(P_A.x, P_A.y, P_B.x, P_B.y, P_C.x, P_C.y);

                p.textAlign(p.CENTER, p.CENTER);
                p.noStroke();
                // Vertex labels
                p.fill(C_A);
                p.textSize(16);
                p.text('A', P_A.x, P_A.y - 20);
                p.fill(C_B);
                p.text('B', P_B.x - 20, P_B.y + 10);
                p.fill(C_C);
                p.text('C', P_C.x + 20, P_C.y + 10);

                // Axis ticks and labels
                p.strokeWeight(1);
                p.textSize(10);
                for (let i = 1; i <= 9; i++) {
                    const frac = i / 10.0;
                    // B->A axis (for C)
                    const p1_BA = p5.Vector.lerp(P_B, P_A, frac);
                    const p2_BA = p5.Vector.lerp(P_C, P_A, frac);
                    p.stroke(C_C);
                    p.line(p1_BA.x, p1_BA.y, p1_BA.x + 5, p1_BA.y + 8);
                    p.noStroke(); p.fill(C_C); p.text(frac.toFixed(1), p1_BA.x + 15, p1_BA.y + 15);

                    // C->B axis (for A)
                    const p1_CB = p5.Vector.lerp(P_C, P_B, frac);
                    p.stroke(C_A);
                    p.line(p1_CB.x, p1_CB.y, p1_CB.x, p1_CB.y - 10);
                    p.noStroke(); p.fill(C_A); p.text(frac.toFixed(1), p1_CB.x, p1_CB.y - 20);

                    // A->C axis (for B)
                    const p1_AC = p5.Vector.lerp(P_A, P_C, frac);
                    p.stroke(C_B);
                    p.line(p1_AC.x, p1_AC.y, p1_AC.x - 5, p1_AC.y + 8);
                    p.noStroke(); p.fill(C_B); p.text(frac.toFixed(1), p1_AC.x - 15, p1_AC.y + 15);
                }
            };

            const drawGridLines = () => {
                p.stroke(220);
                p.strokeWeight(0.5);
                for (let i = 1; i <= 9; i++) {
                    const frac = i / 10.0;
                    // Lines of constant C
                    const p1_c = p5.Vector.lerp(P_A, P_B, frac);
                    const p2_c = p5.Vector.lerp(P_A, P_C, 1-frac);
                    p.line(p1_c.x, p1_c.y, p2_c.x, p2_c.y);
                    // Lines of constant A
                    const p1_a = p5.Vector.lerp(P_B, P_A, frac);
                    const p2_a = p5.Vector.lerp(P_C, P_A, frac);
                    p.line(p1_a.x, p1_a.y, p2_a.x, p2_a.y);
                    // Lines of constant B
                    const p1_b = p5.Vector.lerp(P_C, P_A, frac);
                    const p2_b = p5.Vector.lerp(P_C, P_B, 1-frac);
                    p.line(p1_b.x, p1_b.y, p2_b.x, p2_b.y);
                }
            };

            const drawPhaseEnvelope = () => {
                // Draw alpha side
                p.stroke(C_ALPHA);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                alphaCurvePoints.forEach(pt => {
                    const c = barycentricToCartesian(pt.xA, pt.xB, pt.xC);
                    p.vertex(c.x, c.y);
                });
                p.endShape();

                // Draw beta side
                p.stroke(C_BETA);
                p.beginShape();
                betaCurvePoints.forEach(pt => {
                    const c = barycentricToCartesian(pt.xA, pt.xB, pt.xC);
                    p.vertex(c.x, c.y);
                });
                p.endShape();
            };

            const drawPredefinedTieLines = () => {
                p.stroke(180);
                p.strokeWeight(1);
                tieLines.forEach(tl => {
                    const p_alpha = barycentricToCartesian(tl.alpha.xA, tl.alpha.xB, tl.alpha.xC);
                    const p_beta = barycentricToCartesian(tl.beta.xA, tl.beta.xB, tl.beta.xC);
                    p.line(p_alpha.x, p_alpha.y, p_beta.x, p_beta.y);
                });
            };

            const drawUserPoint = (comp, pos) => {
                // Draw projection lines
                p.strokeWeight(1);
                p.setLineDash([5, 5]);
                // To A-axis (constant A)
                p.stroke(C_A); p.line(pos.x, pos.y, barycentricToCartesian(0, comp.xB / (comp.xB + comp.xC), comp.xC / (comp.xB + comp.xC)).x, barycentricToCartesian(0, comp.xB / (comp.xB + comp.xC), comp.xC / (comp.xB + comp.xC)).y);
                // To B-axis (constant B)
                p.stroke(C_B); p.line(pos.x, pos.y, barycentricToCartesian(comp.xA / (comp.xA + comp.xC), 0, comp.xC / (comp.xA + comp.xC)).x, barycentricToCartesian(comp.xA / (comp.xA + comp.xC), 0, comp.xC / (comp.xA + comp.xC)).y);
                // To C-axis (constant C)
                p.stroke(C_C); p.line(pos.x, pos.y, barycentricToCartesian(comp.xA / (comp.xA + comp.xB), comp.xB / (comp.xA + comp.xB), 0).x, barycentricToCartesian(comp.xA / (comp.xA + comp.xB), comp.xB / (comp.xA + comp.xB), 0).y);
                p.noStroke();
                p.setLineDash([]);

                // Draw the point itself
                p.fill(0);
                p.stroke(255);
                p.strokeWeight(2);
                p.ellipse(pos.x, pos.y, 10, 10);
            };

            const handleTwoPhaseRegion = (mainComp) => {
                const { alpha, beta } = findTieLineForPoint(mainComp);

                if (!alpha || !beta) return;

                const p_alpha = barycentricToCartesian(alpha.xA, alpha.xB, alpha.xC);
                const p_beta = barycentricToCartesian(beta.xA, beta.xB, beta.xC);
                const p_main = barycentricToCartesian(mainComp.xA, mainComp.xB, mainComp.xC);

                // Draw connecting tie line
                p.stroke(0);
                p.strokeWeight(1.5);
                p.setLineDash([3, 3]);
                p.line(p_alpha.x, p_alpha.y, p_beta.x, p_beta.y);
                p.setLineDash([]);

                if (showAlphaPhase) {
                    p.fill(C_ALPHA);
                    p.stroke(255);
                    p.strokeWeight(2);
                    p.ellipse(p_alpha.x, p_alpha.y, 10, 10);
                }

                if (showBetaPhase) {
                    p.fill(C_BETA);
                    p.stroke(255);
                    p.strokeWeight(2);
                    p.ellipse(p_beta.x, p_beta.y, 10, 10);
                }

                // Update info boxes with these found compositions
                updateInfoBoxes(mainComp, true, alpha, beta);
            };

            const drawPhasesView = (mainComp) => {
                 // Fill one-phase region
                p.fill(251, 229, 230, 150); // Light red
                p.noStroke();
                p.beginShape();
                p.vertex(P_A.x, P_A.y);
                p.vertex(P_B.x, P_B.y);
                p.vertex(P_C.x, P_C.y);
                p.endShape(p.CLOSE);

                // Fill two-phase region
                p.fill(212, 234, 252, 200); // Light blue
                p.beginShape();
                phaseEnvelopePolygon.forEach(pt => {
                    const c = barycentricToCartesian(pt.xA, pt.xB, pt.xC);
                    p.vertex(c.x, c.y);
                });
                p.endShape(p.CLOSE);

                // Redraw envelope boundary
                drawPhaseEnvelope();

                // Add labels
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(18);
                p.text("One Phase", canvasSize / 2, P_A.y + 50);
                const twoPhaseCenter = barycentricToCartesian(0.18, 0.4, 0.42);
                p.text("Two Phases", twoPhaseCenter.x, twoPhaseCenter.y);

                // Label plait point
                const plaitPoint = alphaCurvePoints[alphaCurvePoints.length-1];
                const plaitPos = barycentricToCartesian(plaitPoint.xA, plaitPoint.xB, plaitPoint.xC);
                p.textSize(12);
                p.text("Plait Point", plaitPos.x, plaitPos.y - 20);
                p.stroke(0); p.strokeWeight(1);
                p.line(plaitPos.x, plaitPos.y - 12, plaitPos.x, plaitPos.y - 2);

                // Draw user point
                const userPointCartesian = barycentricToCartesian(mainComp.xA, mainComp.xB, mainComp.xC);
                drawUserPoint(mainComp, userPointCartesian);
            }

            // --- COORDINATE & MATH FUNCTIONS ---
            const barycentricToCartesian = (xA, xB, xC) => {
                const px = xA * P_A.x + xB * P_B.x + xC * P_C.x;
                const py = xA * P_A.y + xB * P_B.y + xC * P_C.y;
                return p.createVector(px, py);
            };

            const cartesianToBarycentric = (p_mouse) => {
                const D = (P_A.x - P_C.x) * (P_B.y - P_C.y) - (P_B.x - P_C.x) * (P_A.y - P_C.y);
                const xA = ((p_mouse.x - P_C.x) * (P_B.y - P_C.y) - (P_B.x - P_C.x) * (p_mouse.y - P_C.y)) / D;
                const xB = ((P_A.x - P_C.x) * (p_mouse.y - P_C.y) - (p_mouse.x - P_C.x) * (P_A.y - P_C.y)) / D;
                const xC = 1 - xA - xB;
                return { xA, xB, xC };
            };

            const isInsideTriangle = (pt) => {
                const b = cartesianToBarycentric(pt);
                return b.xA >= -0.01 && b.xB >= -0.01 && b.xC >= -0.01; // Small tolerance
            };

            const isInsidePolygon = (point, vs) => {
                // Ray-casting algorithm
                let x = point.x, y = point.y;
                let inside = false;
                const polygonPoints = vs.map(comp => barycentricToCartesian(comp.xA, comp.xB, comp.xC));

                for (let i = 0, j = polygonPoints.length - 1; i < polygonPoints.length; j = i++) {
                    let xi = polygonPoints[i].x, yi = polygonPoints[i].y;
                    let xj = polygonPoints[j].x, yj = polygonPoints[j].y;

                    let intersect = ((yi > y) != (yj > y))
                        && (x < (xj - xi) * (y - yi) / (yj - yi) + xi);
                    if (intersect) inside = !inside;
                }
                return inside;
            };

            const findTieLineForPoint = (pt) => {
                // Simplified interpolation based on xC component
                if (tieLines.length < 2) return {alpha: null, beta: null};

                let lower_tl = tieLines[0];
                let upper_tl = tieLines[tieLines.length - 1];

                for (let i = 0; i < tieLines.length -1; i++) {
                    if (pt.xC >= tieLines[i].mid.xC && pt.xC <= tieLines[i+1].mid.xC) {
                        lower_tl = tieLines[i];
                        upper_tl = tieLines[i+1];
                        break;
                    }
                }
                 if (pt.xC < tieLines[0].mid.xC) {
                    lower_tl = upper_tl = tieLines[0];
                 } else if (pt.xC > tieLines[tieLines.length - 1].mid.xC) {
                    lower_tl = upper_tl = tieLines[tieLines.length - 1];
                 }

                const denom = upper_tl.mid.xC - lower_tl.mid.xC;
                const t = (denom === 0) ? 0.5 : (pt.xC - lower_tl.mid.xC) / denom;

                const lerpComp = (p1, p2, t) => {
                    return {
                        xA: p.lerp(p1.xA, p2.xA, t),
                        xB: p.lerp(p1.xB, p2.xB, t),
                        xC: p.lerp(p1.xC, p2.xC, t),
                    };
                };

                const alpha = lerpComp(lower_tl.alpha, upper_tl.alpha, t);
                const beta = lerpComp(lower_tl.beta, upper_tl.beta, t);

                return { alpha, beta };
            };

            // --- DOM UPDATE ---
            const updateInfoBoxes = (main, isTwoPhase, alpha, beta) => {
