        '3-Opt': document.getElementById('result-3opt'),
    };

    // --- STATE MANAGEMENT ---
    let points = [];
    let results = {
        OrZweig: { tour: [], length: 0, time: 0 },
        OrOpt:   { tour: [], length: 0, time: 0 },
        TwoOpt:  { tour: [], length: 0, time: 0 },
