        '3-Opt': { tour: [], length: 0, time: 0 },
    };
    let leftSketch, rightSketch;

    // --- HELPER FUNCTIONS ---

    // Seeded Pseudo-Random Number Generator (PRNG) using LCG
    const createPrng = (seed) => {
        let state = seed % 2147483647;
        if (state <= 0) state += 2147483646;
        return () => {
            state = (state * 16807) % 2147483647;
            return (state - 1) / 2147483646;
        };
    };

    const distSq = (p1, p2) => (p1.x - p2.x)**2 + (p1.y - p2.y)**2;
    const dist = (p1, p2) => Math.sqrt(distSq(p1, p2));
    const calculateTourLength = (tour) => {
        let total = 0;
        for (let i = 0; i < tour.length - 1; i++) {
            total += dist(points[tour[i]], points[tour[i+1]]);
        }
        total += dist(points[tour[tour.length-1]], points[tour[0]]);
        return total;
    };

    // --- TSP ALGORITHMS ---

    // Base Heuristic: Nearest Neighbor
    const solveNearestNeighbor = () => {
        const unvisited = new Set(points.map((_, i) => i));
        const tour = [];
        let current = 0; // Start at point 0
        unvisited.delete(current);
        tour.push(current);

        while (unvisited.size > 0) {
            let nearestDist = Infinity;
            let nearestNode = -1;
            for (const next of unvisited) {
                const d = distSq(points[current], points[next]);
                if (d < nearestDist) {
                    nearestDist = d;
                    nearestNode = next;
                }
            }
            current = nearestNode;
            tour.push(current);
            unvisited.delete(current);
        }
        return tour;
    };

    // Local Search: 2-Opt
    const solveTwoOpt = () => {
        let tour = solveNearestNeighbor();
        let improved = true;
        while (improved) {
            improved = false;
            for (let i = 0; i < tour.length - 1; i++) {
                for (let j = i + 2; j < tour.length; j++) {
                    const p1 = points[tour[i]], p2 = points[tour[i + 1]];
                    const p3 = points[tour[j]], p4 = points[tour[(j + 1) % tour.length]];
                    if (dist(p1, p3) + dist(p2, p4) < dist(p1, p2) + dist(p3, p4)) {
                        const newTour = tour.slice(0, i + 1)
                                            .concat(tour.slice(i + 1, j + 1).reverse())
                                            .concat(tour.slice(j + 1));
                        tour = newTour;
                        improved = true;
                    }
                }
            }
        }
        return { tour, length: calculateTourLength(tour) };
    };

    // Local Search: OrZweig (implemented as 1-Opt move, relocating single cities)
    const solveOrZweig = () => {
        let tour = solveNearestNeighbor();
        let bestLength = calculateTourLength(tour);
        let improved = true;
        while(improved){
            improved = false;
            for(let i = 0; i < tour.length; i++){
                const cityToMove = tour[i];
                const tempTour = tour.slice(0, i).concat(tour.slice(i + 1));
                for(let j = 0; j <= tempTour.length; j++){
                    const newTour = tempTour.slice(0, j).concat([cityToMove]).concat(tempTour.slice(j));
                    const newLength = calculateTourLength(newTour);
                    if(newLength < bestLength - 1e-9){
                        tour = newTour;
                        bestLength = newLength;
                        improved = true;
                        // Break inner loops to restart search from the new tour
                        i = tour.length;
                        break;
                    }
                }
            }
        }
        return { tour, length: bestLength };
    };

    // Local Search: Or-Opt (moving segments of 1, 2, 3)
    const solveOrOpt = () => {
        let tour = solveNearestNeighbor();
        let bestLength = calculateTourLength(tour);
        let improved = true;
        while (improved) {
            improved = false;
            for (let segLen = 1; segLen <= 3; segLen++) {
                for (let i = 0; i < tour.length; i++) {
                    const segment = [];
                    for(let k=0; k<segLen; k++) segment.push(tour[(i + k) % tour.length]);

                    const tempTour = tour.filter(c => !segment.includes(c));

                    for (let j = 0; j <= tempTour.length; j++) {
                        const newTour = tempTour.slice(0, j).concat(segment).concat(tempTour.slice(j));
                        const newLength = calculateTourLength(newTour);
                        if (newLength < bestLength - 1e-9) {
                            tour = newTour;
                            bestLength = newLength;
                            improved = true;
                             // Break all loops and restart
                            i = tour.length; segLen = 4;
                            break;
                        }
                    }
                }
            }
        }
        return { tour, length: bestLength };
    };

    // Local Search: 3-Opt
    const solve3Opt = () => {
        let tour = solveNearestNeighbor();
        let improved = true;
        while(improved) {
            improved = false;
            for (let i = 0; i < tour.length; i++) {
                for (let j = i + 2; j < tour.length; j++) {
                    for (let k = j + 2; k < tour.length + (i > 0 ? 1 : 0); k++) {
                        if (k >= tour.length && i === 0) continue;
                        const i1=i, i2=i+1;
                        const j1=j, j2=j+1;
                        const k1=k % tour.length, k2=(k+1) % tour.length;

                        const p = [tour[i1], tour[i2], tour[j1], tour[j2], tour[k1], tour[k2]].map(idx => points[idx]);
                        const d0 = dist(p[0], p[1]) + dist(p[2], p[3]) + dist(p[4], p[5]);

                        // 7 reconnections to check (case 0 is original)
                        const cases = [
                            dist(p[0],p[1]) + dist(p[2],p[4]) + dist(p[3],p[5]), // 2-opt
                            dist(p[0],p[2]) + dist(p[1],p[3]) + dist(p[4],p[5]), // 2-opt
                            dist(p[0],p[2]) + dist(p[1],p[4]) + dist(p[3],p[5]), // 3-opt
                            dist(p[0],p[3]) + dist(p[1],p[4]) + dist(p[2],p[5]), // 3-opt
                            dist(p[0],p[3]) + dist(p[1],p[5]) + dist(p[2],p[4]), // 3-opt (reversed)
                            dist(p[0],p[4]) + dist(p[1],p[2]) + dist(p[3],p[5]), // 2-opt
                            dist(p[0],p[4]) + dist(p[1],p[3]) + dist(p[2],p[5])  // 3-opt
                        ];

                        let bestCase = -1; let minDelta = -1e-9;
                        for(let c=0; c<cases.length; c++) {
                            if (cases[c] - d0 < minDelta) {
                                minDelta = cases[c] - d0;
                                bestCase = c + 1;
                            }
                        }

                        if (bestCase > 0) {
                            const seg1 = tour.slice(i2, j1+1);
                            const seg2 = tour.slice(j2, k1+1);
                            const seg3 = tour.slice(k2).concat(tour.slice(0, i1+1));

                            let newTour;
                            // Rebuild tour based on best case (simplified, only a few are implemented for demo)
                            switch (bestCase) {
                                case 2: newTour = tour.slice(0, i1+1).concat(seg1.reverse()).concat(tour.slice(j1+1)); break;
                                case 3: newTour = tour.slice(0, i1+1).concat(seg2).concat(seg1).concat(tour.slice(k1+1)); break;
                                case 4: newTour = tour.slice(0, i1+1).concat(seg2).concat(seg1.reverse()).concat(tour.slice(k1+1)); break;
                                default: newTour = null; // Other cases are more complex to implement
                            }

                            if (newTour) {
                                tour = newTour;
                                improved = true;
                                i = tour.length; j = tour.length; k = tour.length; // break all
                            }
                        }
                    }
                }
            }
        }
        return { tour, length: calculateTourLength(tour) };
    };

    // Christofides Algorithm (with greedy matching heuristic)
    const solveCCA = () => {
        const n = points.length;
        if (n === 0) return { tour: [], length: 0 };
        const adj = Array(n).fill(0).map(() => []);

        // 1. Minimum Spanning Tree (Prim's algorithm)
        const mstEdges = [];
        const inMST = new Array(n).fill(false);
        const key = new Array(n).fill(Infinity);
        const parent = new Array(n).fill(-1);
        key[0] = 0;

        for (let count = 0; count < n; count++) {
            let min = Infinity, u = -1;
            for (let v = 0; v < n; v++) {
                if (!inMST[v] && key[v] < min) {
                    min = key[v]; u = v;
                }
            }
            if(u === -1) continue;
            inMST[u] = true;
            if (parent[u] !== -1) {
                mstEdges.push([u, parent[u]]);
                adj[u].push(parent[u]);
                adj[parent[u]].push(u);
            }
            for (let v = 0; v < n; v++) {
                const d = dist(points[u], points[v]);
                if (!inMST[v] && d < key[v]) {
                    parent[v] = u; key[v] = d;
                }
            }
        }

        // 2. Find odd-degree vertices
        const oddDegreeVertices = [];
        for (let i = 0; i < n; i++) {
            if (adj[i].length % 2 !== 0) {
                oddDegreeVertices.push(i);
            }
        }

        // 3. Greedy Minimum-Weight Perfect Matching
        let unmatched = [...oddDegreeVertices];
        while (unmatched.length > 0) {
            let u = unmatched.pop();
            let bestV = -1, minD = Infinity;
            for (let i=0; i<unmatched.length; i++) {
                let v = unmatched[i];
                const d = dist(points[u], points[v]);
                if (d < minD) {
                    minD = d;
                    bestV = i;
                }
            }
            if (bestV !== -1) {
                let v_node = unmatched.splice(bestV, 1)[0];
                adj[u].push(v_node);
                adj[v_node].push(u);
            }
        }

        // 4. Find Eulerian Circuit
        const circuit = [];
        const tempAdj = adj.map(neighbors => [...neighbors]);
        const stack = [0];
        while(stack.length > 0) {
            let u = stack[stack.length - 1];
            if (tempAdj[u].length > 0) {
                let v = tempAdj[u].pop();
                // Remove reverse edge
                const v_idx_in_u = tempAdj[v].indexOf(u);
                if (v_idx_in_u > -1) tempAdj[v].splice(v_idx_in_u, 1);
                stack.push(v);
            } else {
                circuit.push(stack.pop());
            }
        }
        circuit.reverse();

        // 5. Shortcut to Hamiltonian Circuit
        const visited = new Array(n).fill(false);
        const tour = [];
        for (const city of circuit) {
            if (!visited[city]) {
                tour.push(city);
                visited[city] = true;
            }
        }

        return { tour, length: calculateTourLength(tour) };
    };

    const algorithms = {
        OrZweig: solveOrZweig,
        OrOpt: solveOrOpt,
        TwoOpt: solveTwoOpt,
        CCA: solveCCA,
        '3-Opt': solve3Opt
    };

    // --- MAIN CONTROL FLOW ---
    const runSimulation = () => {
        const numPoints = parseInt(sliderPoints.value, 10);
        const seed = parseInt(seedInput.value, 10);

        // Generate points
        const prng = createPrng(seed);
        points = Array(numPoints).fill(0).map(() => ({ x: prng(), y: prng() }));

        // Run all algorithms
        for (const name in algorithms) {
            const startTime = performance.now();
            const result = algorithms[name]();
            const endTime = performance.now();

            results[name] = {
                tour: result.tour,
                length: result.length,
                time: (endTime - startTime) / 1000
            };
        }

        updateUI();
        updatePlots();
    };

    // --- UI UPDATE FUNCTIONS ---
    const updateUI = () => {
        // Update table
        for (const name in results) {
            timingCells[name].textContent = results[name].time.toFixed(6);
            resultCells[name].textContent = results[name].length.toFixed(6);
        }

        // Update winner display
        const mathResults = ['OrZweig', 'OrOpt', 'TwoOpt', 'CCA'].map(name => results[name].length);
        const minMathResult = Math.min(...mathResults);
        const result3Opt = results['3-Opt'].length;
        const epsilon = 1e-9;

        if (result3Opt < minMathResult - epsilon) {
            winnerDisplay.textContent = '3-Opt wins';
        } else if (minMathResult < result3Opt - epsilon) {
            winnerDisplay.textContent = 'Mathematica wins';
        } else {
            winnerDisplay.textContent = 'Tie';
        }
    };

    const updatePlots = () => {
        const selectedMethod = document.querySelector('input[name="math-method"]:checked').value;
        leftPlotTitle.textContent = selectedMethod;

        leftSketch.updateData({
            points: points,
            tour: results[selectedMethod].tour,
            color: '#0072B2' // Blue
        });
        rightSketch.updateData({
            points: points,
            tour: results['3-Opt'].tour,
            color: '#E69F00' // Orange
        });
    };

    // --- p5.js SKETCH CREATION ---
    const createPlotSketch = (containerId) => {
        return (p) => {
            let data = { points: [], tour: [], color: '#000000' };

            p.setup = () => {
                const container = document.getElementById(containerId);
                const size = container.clientWidth;
                const canvas = p.createCanvas(size, size);
                canvas.parent(containerId);
                p.noLoop();
            };

            p.updateData = (newData) => {
                data = newData;
                if(p._setupDone) p.redraw();
            };

            p.draw = () => {
                const margin = 30;
                const w = p.width - 2 * margin;
                const h = p.height - 2 * margin;

                p.background(255);
                p.translate(margin, margin);

                // Draw axes
                p.stroke(200);
                p.strokeWeight(1);
                p.line(0, 0, 0, h);
                p.line(0, h, w, h);

                // Draw ticks and labels
                p.textAlign(p.CENTER, p.CENTER);
                p.noStroke();
                p.fill(150);
                for (let i = 0.2; i <= 1.0; i += 0.2) {
                    p.stroke(220);
                    p.line(w * i, h, w * i, h + 5); // x-axis
                    p.line(-5, h * (1-i), 0, h * (1-i)); // y-axis
                    p.noStroke();
                    p.text(i.toFixed(1), w * i, h + 15);
                    p.text(i.toFixed(1), -15, h * (1-i));
                }

                if (!data.points || data.points.length === 0) return;

                // Draw tour path
                if (data.tour && data.tour.length > 0) {
                    p.stroke(data.color);
                    p.strokeWeight(2);
                    p.noFill();
                    p.beginShape();
                    data.tour.forEach(index => {
                        const pt = data.points[index];
                        p.vertex(pt.x * w, (1 - pt.y) * h);
                    });
                    // Close the loop
                    const firstPt = data.points[data.tour[0]];
                    p.vertex(firstPt.x * w, (1 - firstPt.y) * h);
                    p.endShape();
                }

                // Draw points
                p.noStroke();
                p.fill('red');
                data.points.forEach(pt => {
                    p.ellipse(pt.x * w, (1 - pt.y) * h, 8, 8);
                });
            };
        };
    };

    // --- EVENT LISTENERS ---
    btnRandomSet.addEventListener('click', () => {
        seedInput.value = Math.floor(Math.random() * 99999) + 1;
        runSimulation();
    });

    btnResetSeed.addEventListener('click', () => {
        seedInput.value = '0';
        runSimulation();
    });

    sliderPoints.addEventListener('input', runSimulation);

    mathMethodRadios.forEach(radio => {
        radio.addEventListener('change', updatePlots);
    });

    // --- INITIALIZATION ---
    leftSketch = new p5(createPlotSketch('canvas-left-wrapper'));
    rightSketch = new p5(createPlotSketch('canvas-right-wrapper'));
    runSimulation();
});
</script>

</body>
</html>
