<script>
// --- p5.js Sketch ---

const sketch = (p) => {
    // --- State Variables ---
    let dom = {};
    let state = {
        initialization: 462,
        maxSteps: 9,
        showCycle4: false,
        showCycle2: false,
        showCycle1: false,
        showAllPoints: false,
        modifyInitialization: false,
    };
    let path = [];
    let infoText = "";

    const CANVAS_SIZE = 512;
    const LOGICAL_GRID_SIZE = 256;

    // --- Hardcoded Cycle Data ---
    const cycle2Pairs = [[10922, 21845], [13107, 26214], [13653, 27306], [15339, 30678], [21844, 43691], [26213, 52427], [27305, 54611], [30677, 61355], [43690, 54613]];
    const cycle4Points = [43690, 54612, 32768, 49152];
    const cycle1Points = [0, 1]; // f(n)=n if n=0 or n=1

    // --- Core Automaton Logic ---
    function BitReverse15(num) {
        let reversed = 0;
        for (let i = 0; i < 15; i++) {
            reversed = (reversed << 1) | (num & 1);
            num >>= 1;
        }
        return reversed;
    }

    const automatonFunc = (n) => (n + BitReverse15(Math.floor(n / 2))) % 65536;

    const intToCoord = (n) => ({
        x: n & 255,
        y: (n >> 8) & 255,
    });

    const mapCoordToCanvas = (coord) => ({
        x: p.map(coord.x, 0, LOGICAL_GRID_SIZE - 1, 0, CANVAS_SIZE),
        y: p.map(coord.y, 0, LOGICAL_GRID_SIZE - 1, 0, CANVAS_SIZE),
    });

    // --- p5.js Setup ---
    p.setup = () => {
        const canvas = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
        canvas.parent('canvas-container');
        p.noLoop(); // We will manually redraw

        // Get DOM elements
        dom.sliderInit = p.select('#slider-initialization');
        dom.valueInit = p.select('#value-initialization');
        dom.sliderSteps = p.select('#slider-max-steps');
        dom.valueSteps = p.select('#value-max-steps');
        dom.checkCycle4 = p.select('#checkbox-cycle4');
        dom.checkCycle2 = p.select('#checkbox-cycle2');
        dom.checkCycle1 = p.select('#checkbox-cycle1');
        dom.checkAllPoints = p.select('#checkbox-all-points');
        dom.checkModify = p.select('#checkbox-modify');
        dom.infoText = p.select('#info-text');

        // Setup event listeners
        dom.sliderInit.input(handleSliderChange);
        dom.sliderSteps.input(handleSliderChange);
        dom.checkCycle4.changed(handleCheckboxChange);
        dom.checkCycle2.changed(handleCheckboxChange);
        dom.checkCycle1.changed(handleCheckboxChange);
        dom.checkAllPoints.changed(handleCheckboxChange);
        dom.checkModify.changed(handleCheckboxChange);

        // Initial simulation and draw
        updateSimulation();
    };

    // --- Event Handlers ---
    function handleSliderChange() {
        updateSimulation();
    }

    function handleCheckboxChange() {
        state.showCycle4 = dom.checkCycle4.checked();
        state.showCycle2 = dom.checkCycle2.checked();
        state.showCycle1 = dom.checkCycle1.checked();
        state.showAllPoints = dom.checkAllPoints.checked();
        state.modifyInitialization = dom.checkModify.checked();
        p.redraw();
    }

    // --- Simulation Logic ---
    function updateSimulation() {
        // 1. Read values from controls
        state.initialization = dom.sliderInit.value();
        state.maxSteps = dom.sliderSteps.value();
        dom.valueInit.html(`+ ${state.initialization}`);
        dom.valueSteps.html(`+ ${state.maxSteps}`);

        // 2. Generate path and detect cycles
        path = [state.initialization];
        const seen = { [state.initialization]: 0 };
        let cycleFound = false;

        for (let i = 0; i < state.maxSteps; i++) {
            const nextN = automatonFunc(path[i]);
            if (seen.hasOwnProperty(nextN)) {
                const j = seen[nextN];
                const k = i - j + 1;
                infoText = `result of initial condition ${state.initialization} repeats in ${k} steps`;
                cycleFound = true;
                path.push(nextN);
                break;
            }
            seen[nextN] = i + 1;
            path.push(nextN);
        }

        if (!cycleFound) {
            path.push(automatonFunc(path[path.length - 1]));
            infoText = `result of initial condition ${state.initialization} does not repeat in ${state.maxSteps + 1} steps`;
        }

        // 3. Update info text
        dom.infoText.html(infoText);

        // 4. Trigger a redraw
        p.redraw();
    }

    // --- p5.js Draw Function (called by p.redraw()) ---
    p.draw = () => {
        // 1. Background
        p.background('#e0f0e0');

        // 2. "All Map Points" Overlay
        if (state.showAllPoints) drawAllPoints();

        // 3. Automaton Path
        drawAutomatonPath();

        // 4. "Length 1 Cycle" Overlay
        if (state.showCycle1) drawCycle1Overlay();

        // 5. "Length 2 Cycles" Overlay
        if (state.showCycle2) drawCycle2Overlay();

        // 6. "Length 4 Cycle" Overlay
        if (state.showCycle4) drawCycle4Overlay();
    };

    // --- Drawing Helper Functions ---

    function drawAllPoints() {
        p.stroke('#808080');
        p.strokeWeight(1.5);
        for (let n = 0; n < 65536; n++) {
            const nextN = automatonFunc(n);
            const coord = intToCoord(nextN);
            const canvasCoord = mapCoordToCanvas(coord);
            p.point(canvasCoord.x, canvasCoord.y);
        }
    }

    function drawAutomatonPath() {
        if (path.length < 2) return;

        // Draw polyline
        p.stroke('#800080');
        p.strokeWeight(2);
        p.noFill();
        p.beginShape();
        for (const n of path) {
            const canvasCoord = mapCoordToCanvas(intToCoord(n));
            p.vertex(canvasCoord.x, canvasCoord.y);
        }
        p.endShape();

        // Draw start point (red)
        const startCoord = mapCoordToCanvas(intToCoord(path[0]));
        p.fill('#FF0000');
        p.noStroke();
        p.ellipse(startCoord.x, startCoord.y, 8, 8);

        // Draw end point (blue)
        const endCoord = mapCoordToCanvas(intToCoord(path[path.length - 1]));
        p.fill('#0000FF');
        p.noStroke();
        p.ellipse(endCoord.x, endCoord.y, 8, 8);
    }

    function drawCycle1Overlay() {
        p.fill('#00FFFF');
        p.noStroke();
        for (const n of cycle1Points) {
            const canvasCoord = mapCoordToCanvas(intToCoord(n));
            p.ellipse(canvasCoord.x, canvasCoord.y, 6, 6);
        }
    }

    function drawCycle2Overlay() {
        p.stroke('#008000');
        p.strokeWeight(1);
        for (const pair of cycle2Pairs) {
            const p1 = mapCoordToCanvas(intToCoord(pair[0]));
            const p2 = mapCoordToCanvas(intToCoord(pair[1]));
            p.line(p1.x, p1.y, p2.x, p2.y);
        }
    }

    function drawCycle4Overlay() {
        const coords = cycle4Points.map(n => mapCoordToCanvas(intToCoord(n)));

        // Draw cycle path (green lines)
        p.stroke('#008000');
        p.strokeWeight(1);
        p.noFill();
        p.beginShape();
        coords.forEach(c => p.vertex(c.x, c.y));
        p.endShape(p.CLOSE);

        // Draw bounding box (orange rectangle)
        let minX = CANVAS_SIZE, minY = CANVAS_SIZE, maxX = 0, maxY = 0;
        coords.forEach(c => {
            if (c.x < minX) minX = c.x;
            if (c.y < minY) minY = c.y;
            if (c.x > maxX) maxX = c.x;
            if (c.y > maxY) maxY = c.y;
        });
        p.stroke('#FFA500');
        p.strokeWeight(1);
        p.noFill();
        p.rect(minX, minY, maxX - minX, maxY - minY);

        // Draw perpendicular bisectors (green lines)
        p.stroke('#008000');
        for (let i = 0; i < coords.length; i++) {
            const p1 = coords[i];
            const p2 = coords[(i + 1) % coords.length];

            const mid = { x: (p1.x + p2.x) / 2, y: (p1.y + p2.y) / 2 };
            const vec = { x: p2.x - p1.x, y: p2.y - p1.y };

            // Perpendicular vector is (-y, x)
            let perp = { x: -vec.y, y: vec.x };
            const mag = Math.sqrt(perp.x * perp.x + perp.y * perp.y);
            if (mag > 0) {
                perp.x /= mag;
                perp.y /= mag;
            }

            const BISECTOR_LEN = 15; // half-length of the bisector line
            p.line(
                mid.x - perp.x * BISECTOR_LEN,
                mid.y - perp.y * BISECTOR_LEN,
                mid.x + perp.x * BISECTOR_LEN,
                mid.y + perp.y * BISECTOR_LEN
            );
        }
    }
};

new p5(sketch);
</script>

</body>
</html>
