            northDiv.className = 'label';
            northDiv.textContent = 'N';
            const northLabel = new CSS2DObject(northDiv);
            northLabel.position.set(0, SPHERE_RADIUS + 5, 0);

            const southDiv = document.createElement('div');
            southDiv.className = 'label';
            southDiv.textContent = 'S';
            const southLabel = new CSS2DObject(southDiv);
            southLabel.position.set(0, -SPHERE_RADIUS - 5, 0);

            poleLabels = new THREE.Group();
            poleLabels.add(northLabel, southLabel);
            scene.add(polarAxis);
            scene.add(poleLabels);
        }

        function buildConstellationObjects() {
            // Lines
            const lineMat = new THREE.LineBasicMaterial({ color: 0xff0000, linewidth: 2 });
            const lineGeom = new THREE.BufferGeometry();
            selectedConstellationLines = new THREE.LineSegments(lineGeom, lineMat);
            scene.add(selectedConstellationLines);

            // Highlighted stars
            const starMat = new THREE.PointsMaterial({
                color: 0xffdd88,
                size: 6,
                map: createStarTexture(),
                transparent: true,
                blending: THREE.AdditiveBlending,
                depthWrite: false,
                sizeAttenuation: true
            });
            const starGeom = new THREE.BufferGeometry();
            selectedConstellationStars = new THREE.Points(starGeom, starMat);
            scene.add(selectedConstellationStars);
        }

        // --- Update & Interaction Logic ---

        function updateZenith() {
            const ra = parseFloat(raSlider.value);
            const dec = parseFloat(decSlider.value);
            const pos = raDecToDegreesToCartesian(ra, dec, SPHERE_RADIUS);

            // Update the point
            zenithGroup.children[0].position.copy(pos);

            // Update the line
            const positions = zenithGroup.children[1].geometry.attributes.position;
            positions.setXYZ(1, pos.x, pos.y, pos.z);
            positions.needsUpdate = true;
        }

        function updateConstellation() {
            const conAbbr = constellationSelect.value;
            const lineVertices = [];
            const starVertices = [];

            if (conAbbr !== "None") {
                // Update lines
                const lines = CONSTELLATION_LINES[conAbbr] || [];
                lines.forEach(pair => {
                    const pos1 = starPosMap.get(pair[0]);
                    const pos2 = starPosMap.get(pair[1]);
                    if (pos1 && pos2) {
                        lineVertices.push(pos1.x, pos1.y, pos1.z);
                        lineVertices.push(pos2.x, pos2.y, pos2.z);
                    }
                });

                // Update stars
                const stars = CONSTELLATION_STARS[conAbbr] || [];
                stars.forEach(hip => {
                    const pos = starPosMap.get(hip);
                    if (pos) {
                        starVertices.push(pos.x, pos.y, pos.z);
                    }
                });
            }

            selectedConstellationLines.geometry.setAttribute('position', new THREE.Float32BufferAttribute(lineVertices, 3));
            selectedConstellationStars.geometry.setAttribute('position', new THREE.Float32BufferAttribute(starVertices, 3));
        }

        function populateConstellationSelect() {
            const selectedFamily = familySelect.value;
            const currentConstellation = constellationSelect.value;

            constellationSelect.innerHTML = '<option value="None">None</option>';

            let constellations = [];
            if (selectedFamily === 'all') {
                const allConstellations = new Set();
                Object.values(CONSTELLATION_FAMILIES).forEach(family => {
                    family.forEach(con => allConstellations.add(con));
                });
                constellations = Array.from(allConstellations).sort();
            } else {
                constellations = CONSTELLATION_FAMILIES[selectedFamily] || [];
            }

            constellations.forEach(con => {
                const option = document.createElement('option');
                option.value = con;
                option.textContent = con;
                constellationSelect.appendChild(option);
            });

            // Try to preserve selection if possible, otherwise default
            if (constellations.includes(currentConstellation)) {
                constellationSelect.value = currentConstellation;
            } else if (selectedFamily === 'all') {
                constellationSelect.value = 'None';
            } else {
                 if(constellations.length > 0) constellationSelect.value = constellations[0];
            }
            updateConstellation();
        }

        function setupControls() {
            // Populate family dropdown
            familySelect.innerHTML = '<option value="all">All</option>';
            Object.keys(CONSTELLATION_FAMILIES).forEach(family => {
                const option = document.createElement('option');
                option.value = family;
                option.textContent = family;
                familySelect.appendChild(option);
            });
            familySelect.value = 'Zodiac';

            // Event Listeners
            familySelect.addEventListener('change', populateConstellationSelect);
            constellationSelect.addEventListener('change', updateConstellation);
            raSlider.addEventListener('input', updateZenith);
            decSlider.addEventListener('input', updateZenith);

            document.getElementById('check-sphere').addEventListener('input', (e) => sphere.visible = e.target.checked);
            document.getElementById('check-stars').addEventListener('input', (e) => stars.visible = e.target.checked);
            document.getElementById('check-equator').addEventListener('input', (e) => equator.visible = e.target.checked);
            document.getElementById('check-ecliptic').addEventListener('input', (e) => ecliptic.visible = e.target.checked);
            document.getElementById('check-zenith').addEventListener('input', (e) => zenithGroup.visible = e.target.checked);
            document.getElementById('check-aries').addEventListener('input', (e) => ariesPoint.visible = e.target.checked);

            // Initial population
            populateConstellationSelect();
            constellationSelect.value = "CYG"; // Default as per plan (Cygnus is not in Zodiac, but it's a good demo)
            updateConstellation();
        }

        // --- Animation & Resize ---

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
            labelRenderer.render(scene, camera);
        }

        function onWindowResize() {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
            labelRenderer.setSize(container.clientWidth, container.clientHeight);
        }

        // --- Helpers ---
        function raDecToCartesian(ra_hours, dec_deg, radius) {
            const ra_rad = ra_hours * 15 * Math.PI / 180;
            const dec_rad = dec_deg * Math.PI / 180;
            return new THREE.Vector3(
                radius * Math.cos(dec_rad) * Math.cos(ra_rad),
                radius * Math.sin(dec_rad),
                radius * Math.cos(dec_rad) * Math.sin(ra_rad)
            );
        }

        function raDecToDegreesToCartesian(ra_deg, dec_deg, radius) {
            const ra_rad = ra_deg * Math.PI / 180;
            const dec_rad = dec_deg * Math.PI / 180;
            return new THREE.Vector3(
                radius * Math.cos(dec_rad) * Math.cos(ra_rad),
                radius * Math.sin(dec_rad),
                radius * Math.cos(dec_rad) * Math.sin(ra_rad)
            );
        }

        // --- Go! ---
        init();

    </script>
</body>
</html>
