    <script>
        document.addEventListener('DOMContentLoaded', function () {
            // --- FFT Implementation (Radix-2 Cooley-Tukey) ---
            function fft(real, imag) {
                const n = real.length;
                if (n === 0) return;

                // Bit-reversal permutation
                for (let i = 1, j = 0; i < n; i++) {
                    let bit = n >> 1;
                    for (; (j & bit) !== 0; bit >>= 1) {
                        j ^= bit;
                    }
                    j ^= bit;
                    if (i < j) {
                        [real[i], real[j]] = [real[j], real[i]];
                        [imag[i], imag[j]] = [imag[j], imag[i]];
                    }
                }

                // Cooley-Tukey FFT
                for (let len = 2; len <= n; len <<= 1) {
                    const halfLen = len >> 1;
                    const angle = -2 * Math.PI / len;
                    const w_real = Math.cos(angle);
                    const w_imag = Math.sin(angle);
                    for (let i = 0; i < n; i += len) {
                        let cos_val = 1;
                        let sin_val = 0;
                        for (let j = 0; j < halfLen; j++) {
                            const offset = i + j;
                            const u_real = real[offset];
                            const u_imag = imag[offset];
                            const v_real = real[offset + halfLen] * cos_val - imag[offset + halfLen] * sin_val;
                            const v_imag = real[offset + halfLen] * sin_val + imag[offset + halfLen] * cos_val;
                            real[offset] = u_real + v_real;
                            imag[offset] = u_imag + v_imag;
                            real[offset + halfLen] = u_real - v_real;
                            imag[offset + halfLen] = u_imag - v_imag;

                            const next_cos = cos_val * w_real - sin_val * w_imag;
                            sin_val = cos_val * w_imag + sin_val * w_real;
                            cos_val = next_cos;
                        }
                    }
                }
            }

            // --- DOM Element References ---
            const sliderC1 = document.getElementById('slider-c1');
            const c1ValueDisplay = document.getElementById('c1-value-display');
            const resetC1 = document.getElementById('reset-c1');
            const sliderC2 = document.getElementById('slider-c2');
            const c2ValueDisplay = document.getElementById('c2-value-display');
            const resetC2 = document.getElementById('reset-c2');
            const checkboxClipping = document.getElementById('checkbox-clipping');
            const levelControlGroup = document.getElementById('level-control-group');
            const sliderLevel = document.getElementById('slider-level');
            const levelValueDisplay = document.getElementById('level-value-display');
            const btnLinear = document.getElementById('btn-linear');
            const btnDecibel = document.getElementById('btn-decibel');

            // --- Simulation Constants ---
            const f = 1000;  // Input signal frequency (Hz)
            const A = 1;     // Input signal amplitude
            const N = 2048;  // Number of samples
            const fs = 32000; // Sampling frequency (Hz)

            // --- Global State ---
            let normalizedMagnitudeSpectrum = [];
            const timeVector = Array.from({ length: N }, (_, i) => i / fs);
            const freqVector = Array.from({ length: N / 2 }, (_, k) => k * fs / N);

            // --- Plotly Layouts ---
            const commonLayout = {
                margin: { l: 40, r: 20, b: 40, t: 40 },
                xaxis: { gridcolor: '#eee' },
                yaxis: { gridcolor: '#eee' },
                plot_bgcolor: '#fff',
                paper_bgcolor: '#fff',
            };

            const signalLayout = {
                ...commonLayout,
                title: 'Signal',
                xaxis: { ...commonLayout.xaxis, title: 'time [s]', range: [0, 0.005], autorange: false },
                yaxis: { ...commonLayout.yaxis, title: 'amplitude', range: [-2, 2], autorange: false },
            };

            const spectrumLayout = {
                ...commonLayout,
                title: 'Magnitude Spectrum (DFT)',
                xaxis: { ...commonLayout.xaxis, title: 'frequency [Hz]', range: [0, 8000], autorange: false },
                yaxis: { ...commonLayout.yaxis, title: 'linear', range: [0, 1.2], autorange: false },
            };

            // --- Core Functions ---
            function updateSimulation() {
                // 1. Read control values
                const c1 = parseFloat(sliderC1.value);
                const c2 = parseFloat(sliderC2.value);
                const isClipping = checkboxClipping.checked;
                const level = parseFloat(sliderLevel.value);

                // 2. Generate input signal
                const x = timeVector.map(ti => A * Math.sin(2 * Math.PI * f * ti));

                // 3. Calculate output signal
                let y = x.map(xi => xi + c1 * xi ** 2 + c2 * xi ** 3);

                // 4. Apply clipping
                if (isClipping) {
                    y = y.map(yi => Math.max(-level, Math.min(level, yi)));
                }

                // 5. Update Signal Plot
                const signalTrace = {
                    x: timeVector,
                    y: y,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#007bff', width: 2 }
                };
                Plotly.react('plot-signal', [signalTrace], signalLayout);

                // 6. Perform DFT
                const real = new Float64Array(y);
                const imag = new Float64Array(N).fill(0);
                fft(real, imag);

                // 7. Calculate and normalize magnitude spectrum
                const magnitude = real.map((r, i) => Math.sqrt(r * r + imag[i] * imag[i]));
                normalizedMagnitudeSpectrum = magnitude.slice(0, N / 2).map((mag, k) => {
                    if (k === 0) return mag / N; // DC component
                    return 2 * mag / N; // AC components
                });

                // 8. Update Spectrum Plot via scale function
                updateSpectrumScale();

                // 9. Update value displays
                c1ValueDisplay.textContent = c1.toFixed(1);
                c2ValueDisplay.textContent = c2.toFixed(1);
                levelValueDisplay.textContent = level.toFixed(2);
            }

            function updateSpectrumScale() {
                const isLinear = btnLinear.classList.contains('active');

                let yData, yAxisTitle, yAxisRange;

                if (isLinear) {
                    yData = normalizedMagnitudeSpectrum;
                    yAxisTitle = 'linear';
                    yAxisRange = [0, 1.2];
                } else { // Decibel
                    const epsilon = 1e-10; // To avoid log(0)
                    yData = normalizedMagnitudeSpectrum.map(val => {
                        const db = 20 * Math.log10(val + epsilon);
                        return Math.max(-100, db); // Floor at -100 dB
                    });
                    yAxisTitle = 'decibel [dB]';
                    yAxisRange = [-100, 0];
                }

                const spectrumTrace = {
                    x: freqVector,
                    y: yData,
                    type: 'bar',
                    width: (fs / N) * 0.8, // Bar width relative to frequency resolution
                    marker: { color: '#4682B4' }
                };

                const newSpectrumLayout = {
                    ...spectrumLayout,
                    yaxis: { ...spectrumLayout.yaxis, title: yAxisTitle, range: yAxisRange, autorange: false }
                };

                Plotly.react('plot-spectrum', [spectrumTrace], newSpectrumLayout);
            }

            // --- Event Listeners ---
            sliderC1.addEventListener('input', updateSimulation);
            sliderC2.addEventListener('input', updateSimulation);
            sliderLevel.addEventListener('input', updateSimulation);

            resetC1.addEventListener('click', () => {
                sliderC1.value = 0;
                updateSimulation();
            });

            resetC2.addEventListener('click', () => {
                sliderC2.value = 0;
                updateSimulation();
            });

            checkboxClipping.addEventListener('change', () => {
                levelControlGroup.style.display = checkboxClipping.checked ? 'flex' : 'none';
                updateSimulation();
            });

            btnLinear.addEventListener('click', () => {
                if (!btnLinear.classList.contains('active')) {
                    btnLinear.classList.add('active');
                    btnDecibel.classList.remove('active');
                    updateSpectrumScale();
                }
            });

            btnDecibel.addEventListener('click', () => {
                if (!btnDecibel.classList.contains('active')) {
                    btnDecibel.classList.add('active');
                    btnLinear.classList.remove('active');
                    updateSpectrumScale();
                }
            });

            // --- Initial Setup ---
            Plotly.newPlot('plot-signal', [], signalLayout, {responsive: true});
            Plotly.newPlot('plot-spectrum', [], spectrumLayout, {responsive: true});
            updateSimulation(); // Run initial simulation on page load
        });
    </script>

</body>
</html>
