                </div>
            </div>
        </div>
        <div id="canvas-container">
            <div id="p5-canvas"></div>
        </div>
    </div>

    <script>
        const sketch = (p) => {
            // Control elements
            let controls = {};
            let labels = {};

            // p5.js canvas
            let canvas;

            p.setup = () => {
                const container = document.getElementById('canvas-container');
                const size = Math.min(container.clientWidth, container.clientHeight) * 0.9;
                canvas = p.createCanvas(size, size);
                canvas.parent('p5-canvas');
                p.angleMode(p.DEGREES);

                // Get all controls and labels
                const sliderIds = ["ray-count", "beam-width", "incident-angle", "refraction-ratio"];
                const checkboxIds = ["injected", "refracted", "reflected", "ejected", "normals", "intersections", "beam-top", "beam-bottom"];

                sliderIds.forEach(id => {
                    controls[id] = document.getElementById(`slider-${id}`);
                    labels[id] = document.getElementById(`label-${id}`);
                    controls[id].addEventListener('input', () => {
                        labels[id].textContent = parseFloat(controls[id].value).toFixed(id.includes('angle') ? 0 : 2);
                        p.redraw();
                    });
                });

                checkboxIds.forEach(id => {
                    controls[id] = document.getElementById(`checkbox-${id}`);
                    controls[id].addEventListener('change', () => p.redraw());
                });

                p.noLoop();
                p.redraw();
            };

            p.windowResized = () => {
                const container = document.getElementById('canvas-container');
                const size = Math.min(container.clientWidth, container.clientHeight) * 0.9;
                p.resizeCanvas(size, size);
                p.redraw();
            };

            p.draw = () => {
                const rayCount = parseInt(controls["ray-count"].value);
                const beamWidth = parseFloat(controls["beam-width"].value);
                const incidentAngle = parseFloat(controls["incident-angle"].value);
                const n2 = parseFloat(controls["refraction-ratio"].value);
                const n1 = 1.0;

                const show = {};
                Object.keys(controls).forEach(key => {
                    if (controls[key].type === 'checkbox') {
                        show[key] = controls[key].checked;
                    }
                });

                // --- Coordinate System Setup ---
                p.background(255);
                p.translate(p.width / 2, p.height / 2);
                const scaleFactor = p.width / 5.0; // Show from -2.5 to 2.5
                p.scale(scaleFactor, -scaleFactor); // Flip Y-axis
                p.strokeWeight(1.5 / scaleFactor);

                // --- Draw Static Elements ---
                drawAxes();
                drawSphere();

                // --- Ray Tracing ---
                const angleRad = p.radians(incidentAngle);
                const beamDir = p5.Vector.fromAngle(angleRad);
                const perpDir = p.createVector(-beamDir.y, beamDir.x);

                for (let i = 0; i < rayCount; i++) {
                    const isTopHalf = (rayCount > 1) ? (i >= (rayCount - 1) / 2) : true;
                    const isBottomHalf = (rayCount > 1) ? (i <= (rayCount - 1) / 2) : true;

                    if (!((isTopHalf && show['beam-top']) || (isBottomHalf && show['beam-bottom']))) {
                        continue;
                    }

                    const offset = (rayCount > 1) ? (-beamWidth / 2) + (i / (rayCount - 1)) * beamWidth : 0;

                    const startPos = p5.Vector.mult(beamDir, -2.5);
                    startPos.add(p5.Vector.mult(perpDir, offset));

                    traceRay(startPos, beamDir, n1, n2, show);
                }
            };

            function traceRay(origin, dir, n1, n2, show) {
                // 1. Injected Ray
                const hit1 = intersectSphere(origin, dir, false);
                if (!hit1) return;

                if (show.injected) {
                    p.stroke(255, 0, 0); // Red
                    p.line(origin.x, origin.y, hit1.x, hit1.y);
                }
                if (show.intersections) {
                    p.fill(255, 0, 0); p.noStroke();
                    p.ellipse(hit1.x, hit1.y, 0.05, 0.05);
                }

                // 2. Refracted Ray
                const normal1 = hit1.copy().normalize();
                if (show.normals) drawNormal(hit1, normal1);
                const dir1 = refract(dir, normal1, n1, n2);
                if (!dir1) return; // Total Internal Reflection

                const hit2 = intersectSphere(hit1, dir1, true);
                if (!hit2) return;

                if (show.refracted) {
                    p.stroke(0, 255, 0); // Green
                    p.line(hit1.x, hit1.y, hit2.x, hit2.y);
                }
                if (show.intersections) {
                    p.fill(0, 255, 0); p.noStroke();
                    p.ellipse(hit2.x, hit2.y, 0.05, 0.05);
                }

                // 3. Reflected Ray
                const normal2 = hit2.copy().normalize();
                if (show.normals) drawNormal(hit2, normal2);
                const dir2 = reflect(dir1, normal2);

                const hit3 = intersectSphere(hit2, dir2, true);
                if (!hit3) return;

                if (show.reflected) {
                    p.stroke(0, 0, 255); // Blue
                    p.line(hit2.x, hit2.y, hit3.x, hit3.y);
                }
                if (show.intersections) {
                    p.fill(0, 0, 255); p.noStroke();
                    p.ellipse(hit3.x, hit3.y, 0.05, 0.05);
                }

                // 4. Ejected Ray
                const normal3 = hit3.copy().normalize();
                if (show.normals) drawNormal(hit3, normal3);
                const dir3 = refract(dir2, normal3, n2, n1);
                if (!dir3) return; // TIR on exit

                const endPos = p5.Vector.add(hit3, p5.Vector.mult(dir3, 5)); // Project ray outwards
                if (show.ejected) {
                    p.stroke(0, 0, 0); // Black
                    p.line(hit3.x, hit3.y, endPos.x, endPos.y);
                }
            }

            function intersectSphere(origin, dir, inside) {
                const r = 1.0;
                const oc = origin; // Sphere is at origin
                const a = dir.dot(dir);
                const b = 2.0 * oc.dot(dir);
                const c = oc.dot(oc) - r * r;
                const discriminant = b * b - 4 * a * c;

                if (discriminant < 0) return null;

                const t1 = (-b - p.sqrt(discriminant)) / (2.0 * a);
                const t2 = (-b + p.sqrt(discriminant)) / (2.0 * a);

                let t = inside ? p.max(t1, t2) : p.min(t1, t2);

                // Pick the smallest positive t for rays starting outside
                if (!inside && t < 1e-4) {
                    t = p.max(t1, t2);
                }
                // Pick the smallest t > epsilon for rays starting inside
                if (inside && t < 1e-4) {
                    return null;
                }
                if (t < 1e-4) return null;

                return p5.Vector.add(origin, p5.Vector.mult(dir, t));
            }

            function refract(incident, normal, n1, n2) {
                const eta = n1 / n2;
                const cosI = -normal.dot(incident);
                const sinT2 = eta * eta * (1.0 - cosI * cosI);
                if (sinT2 > 1.0) return null; // Total internal reflection
                const cosT = p.sqrt(1.0 - sinT2);
                const refracted = p5.Vector.mult(incident, eta);
                refracted.add(p5.Vector.mult(normal, eta * cosI - cosT));
                return refracted.normalize();
            }

            function reflect(incident, normal) {
                return p5.Vector.sub(incident, p5.Vector.mult(normal, 2 * incident.dot(normal)));
            }

            function drawAxes() {
                p.push();
                const scaleFactor = p.width / 5.0;
                p.stroke(200);
                p.strokeWeight(1 / scaleFactor);
                p.line(-2.5, 0, 2.5, 0); // X-axis
                p.line(0, -2.5, 0, 2.5); // Y-axis

                p.fill(150);
                p.textSize(12 / scaleFactor);
                p.textAlign(p.CENTER, p.TOP);
                for (let i = -2; i <= 2; i++) {
                    if (i === 0) continue;
                    p.line(i, -0.05, i, 0.05);
                    p.push();
                    p.scale(1, -1);
                    p.text(i, i, 0.1);
                    p.pop();
                }

                p.textAlign(p.LEFT, p.CENTER);
                for (let i = -2; i <= 2; i++) {
                    if (i === 0) continue;
                    p.line(-0.05, i, 0.05, i);
                    p.push();
                    p.scale(1, -1);
                    p.text(i, 0.1, -i);
                    p.pop();
                }
                p.pop();
            }

            function drawSphere() {
                p.push();
                p.noFill();
                p.stroke(0);
                p.ellipse(0, 0, 2, 2); // Radius 1 circle
                p.pop();
            }

            function drawNormal(pos, normal) {
                p.push();
                const scaleFactor = p.width / 5.0;
                p.stroke(180);
                p.strokeWeight(1 / scaleFactor);
                p.drawingContext.setLineDash([5 / scaleFactor, 5 / scaleFactor]);
                const end = p5.Vector.add(pos, p5.Vector.mult(normal, 0.25));
                p.line(pos.x, pos.y, end.x, end.y);
                p.pop();
            }
        };

        new p5(sketch);
    </script>

</body>
</html>
