        </div>
        <span>algorithm step</span>
        <div id="step-controls"></div>
        <button id="reset-button" title="Generate new graph">(+)</button>
    </div>

    <div id="visualization-container">
        <p id="status-text"></p>
        <div id="canvas-wrapper">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script>
        const sketch = (p) => {
            // --- STATE VARIABLES ---
            let graphSize = 18;
            let maxDegree = 3;
            let currentStep = 0;
            let history = []; // Stores the state at each step of the algorithm
            let graph = { U: [], V: [], edges: [] };
            let vertexPositions = new Map();

            // --- DOM ELEMENTS ---
            const sizeControls = document.getElementById('size-controls');
            const degreeControls = document.getElementById('degree-controls');
            const stepControls = document.getElementById('step-controls');
            const resetButton = document.getElementById('reset-button');
            const statusText = document.getElementById('status-text');

            // --- p5.js LIFECYCLE FUNCTIONS ---
            p.setup = () => {
                const canvasWrapper = document.getElementById('canvas-wrapper');
                const w = Math.min(1400, canvasWrapper.offsetWidth * 0.95);
                const h = Math.min(800, canvasWrapper.offsetHeight * 0.95);
                const cnv = p.createCanvas(w, h);
                cnv.id('main-canvas');
                p.textAlign(p.CENTER, p.CENTER);
                p.noLoop();

                setupEventListeners();
                updateActiveButtons();
                regenerateAndRunAlgorithm();
            };

            // --- EVENT HANDLING ---
            function setupEventListeners() {
                sizeControls.addEventListener('click', (e) => {
                    if (e.target.tagName === 'BUTTON') {
                        graphSize = parseInt(e.target.id.split('-')[1]);
                        updateActiveButtons();
                        regenerateAndRunAlgorithm();
                    }
                });

                degreeControls.addEventListener('click', (e) => {
                    if (e.target.tagName === 'BUTTON') {
                        maxDegree = parseInt(e.target.id.split('-')[1]);
                        updateActiveButtons();
                        regenerateAndRunAlgorithm();
                    }
                });

                resetButton.addEventListener('click', () => {
                    regenerateAndRunAlgorithm();
                });
            }

            function updateActiveButtons() {
                // Size controls
                document.querySelectorAll('#size-controls button').forEach(btn => {
                    btn.classList.toggle('active', btn.id === `size-${graphSize}`);
                });
                // Degree controls
                document.querySelectorAll('#degree-controls button').forEach(btn => {
                    btn.classList.toggle('active', btn.id === `degree-${maxDegree}`);
                });
                // Step controls
                 document.querySelectorAll('#step-controls button').forEach(btn => {
                    btn.classList.toggle('active', parseInt(btn.dataset.step) === currentStep);
                });
            }

            function updateStepControls() {
                stepControls.innerHTML = '';
                for (let i = 0; i < history.length; i++) {
                    const btn = document.createElement('button');
                    btn.id = `step-${i + 1}`;
                    btn.innerText = i + 1;
                    btn.dataset.step = i;
                    btn.addEventListener('click', () => {
                        currentStep = i;
                        updateActiveButtons();
                        redrawCanvas();
                    });
                    stepControls.appendChild(btn);
                }
            }

            // --- CORE ALGORITHM & VISUALIZATION LOGIC ---

            function regenerateAndRunAlgorithm() {
                generateRandomGraph();
                calculateVertexPositions();
                runHungarianAlgorithm();
                updateStepControls();
                currentStep = 0;
                updateActiveButtons();
                redrawCanvas();
            }

            function redrawCanvas() {
                p.background(255);
                if (history.length > 0 && history[currentStep]) {
                    const state = history[currentStep];
                    drawBipartiteGraph(state);
                    drawBfsTree(state);
                    statusText.innerText = state.statusText;
                } else {
                    statusText.innerText = 'Initializing...';
                }
            }

            // --- GRAPH GENERATION ---
            function generateRandomGraph() {
                const n = graphSize / 2;
                graph = { U: [], V: [], edges: [] };
                for (let i = 1; i <= n; i++) {
                    graph.U.push(`u${i}`);
                    graph.V.push(`v${i}`);
                }

                // Ensure every vertex in V has a degree between 1 and maxDegree
                const vDegrees = new Array(n).fill(0);
                for (let i = 0; i < n; i++) { // For each v_i
                    const u_source = p.random(graph.U);
                    graph.edges.push({ u: u_source, v: graph.V[i] });
                    vDegrees[i]++;
                }

                // Add more random edges
                const numEdges = n * Math.ceil((maxDegree+1)/2);
                for (let i = 0; i < numEdges; i++) {
                    const u = p.random(graph.U);
                    const v_idx = Math.floor(p.random(n));
                    if (vDegrees[v_idx] < maxDegree) {
                        const v = graph.V[v_idx];
                        const exists = graph.edges.some(e => e.u === u && e.v === v);
                        if (!exists) {
                            graph.edges.push({ u, v });
                            vDegrees[v_idx]++;
                        }
                    }
                }
            }

            // --- HUNGARIAN ALGORITHM IMPLEMENTATION ---
            function runHungarianAlgorithm() {
                history = [];
                let matching = [];
                const matchedV = new Set();

                const captureState = (bfsState, path, cover, message) => {
                    const matchedUCount = new Set(matching.map(e => e.u)).size;
                    const matchedVCount = new Set(matching.map(e => e.v)).size;
                    const status = `Matching size: ${matching.length}. ${cover.length > 0 ? `Vertex cover size: ${cover.length}.` : ''} ${message || ''}`;

                    history.push({
                        matching: [...matching],
                        cover: [...cover],
                        bfsTree: bfsState ? JSON.parse(JSON.stringify(bfsState.tree)) : { nodes: {}, edges: [] },
                        bfsRoot: bfsState ? bfsState.root : null,
                        highlightedPath: path ? [...path] : [],
                        statusText: status,
                    });
                };

                captureState(null, [], [], 'Initial graph.');

                let augmented = true;
                while (augmented) {
                    augmented = false;
                    const unmatchedU = graph.U.filter(u => !matching.some(e => e.u === u));
                    let bfsState = null;

                    for (const startNode of unmatchedU) {
                        bfsState = findAugmentingPath(startNode, matching);
                         // Push all intermediate BFS states to history
                        bfsState.history.forEach(s => captureState(s.bfsState, [], [], s.message));

                        const path = bfsState.path;
                        if (path.length > 0) {
                            captureState(bfsState, path, [], `Augmenting path found from ${startNode}.`);

                            // Augment matching
                            for (let i = 0; i < path.length; i += 2) {
                                const u = path[i];
                                const v = path[i+1];
                                const edgeInMatching = matching.findIndex(e => e.u === u && e.v === v);
                                if (edgeInMatching > -1) {
                                    matching.splice(edgeInMatching, 1);
                                } else {
                                    matching.push({u, v});
                                }
                            }

                            augmented = true;
                            captureState(null, [], [], 'Matching updated.');
                            break; 
                        }
                    }
                    if(!augmented && bfsState) {
                        // All paths explored, no more augmentations. Find vertex cover.
                        const cover = calculateVertexCover(graph, bfsState.visited);
                        captureState(bfsState, [], cover, 'Maximum matching found. Final vertex cover calculated.');
                    }
                }
                if (history.length === 2 && history[1].matching.length === 0) { // No matching possible
                    captureState(null, [], calculateVertexCover(graph, new Set()), 'No augmenting paths exist. Final vertex cover calculated.');
                }
            }

            function findAugmentingPath(startNode, matching) {
                const q = [startNode];
                const visited = new Set([startNode]);
                const parent = new Map();
                const bfsHistory = [];
                const tree = { nodes: { [startNode]: { id: startNode, level: 0, posInLevel: 0 } }, edges: [] };
                let levelCounts = {0:1};

                const addToTree = (node, pNode, level) => {
                    if (!levelCounts[level]) levelCounts[level] = 0;
                    tree.nodes[node] = { id: node, level: level, posInLevel: levelCounts[level]++ };
                    if (pNode) tree.edges.push({ from: pNode, to: node });
                };

                const logBfsStep = (message) => {
                    bfsHistory.push({
                        bfsState: { root: startNode, tree: JSON.parse(JSON.stringify(tree)) },
                        message
                    });
                };

                logBfsStep(`Starting BFS from unmatched vertex ${startNode}.`);

                while (q.length > 0) {
                    const u = q.shift();

                    // Is u in U or V?
                    if (graph.U.includes(u)) { // u is in U
                        // Look for unmatched edges
                        for (const edge of graph.edges) {
                            if (edge.u === u && !visited.has(edge.v)) {
                                const v = edge.v;
                                if (!matching.some(m => m.u === edge.u && m.v === edge.v)) {
                                    visited.add(v);
                                    parent.set(v, u);
                                    addToTree(v, u, tree.nodes[u].level + 1);
                                    logBfsStep(`Exploring edge (${u}, ${v}).`);

                                    // Check if v is unmatched
                                    if (!matching.some(m => m.v === v)) {
                                        return { path: reconstructPath(v, parent), visited, history: bfsHistory };
                                    }
                                    q.push(v);
                                }
                            }
                        }
                    } else { // u is in V
                        // Look for the matched edge
                        const matchedEdge = matching.find(m => m.v === u);
                        if (matchedEdge && !visited.has(matchedEdge.u)) {
                            const v = matchedEdge.u;
                            visited.add(v);
                            parent.set(v, u);
                            q.push(v);
                            addToTree(v, u, tree.nodes[u].level + 1);
                            logBfsStep(`Traversing matched edge (${v}, ${u}).`);
                        }
                    }
                }
                return { path: [], visited, history: bfsHistory }; // No path found
            }

            function reconstructPath(endNode, parent) {
                const path = [endNode];
                let current = endNode;
                while (parent.has(current)) {
                    current = parent.get(current);
                    path.unshift(current);
                }
                return path;
            }

            function calculateVertexCover(graph, reachable) {
                // Cover = (U \ R) U (V intersect R)
                // R is the set of reachable vertices from the failed BFS
                const unreachableU = graph.U.filter(u => !reachable.has(u));
                const reachableV = graph.V.filter(v => reachable.has(v));
                return [...unreachableU, ...reachableV];
            }


            // --- DRAWING FUNCTIONS ---

            function calculateVertexPositions() {
                vertexPositions.clear();
                const n = graph.U.length;
                const rightPartX = p.width / 3;
                const graphWidth = p.width - rightPartX - 100;
                const startX = rightPartX + 50;

                const yU = p.height * 0.2;
                const yV = p.height * 0.8;

                for (let i = 0; i < n; i++) {
                    const xU = startX + (i / (n - 1)) * graphWidth;
                    const xV = startX + (i / (n - 1)) * graphWidth;
                    vertexPositions.set(graph.U[i], { x: xU, y: yU });
                    vertexPositions.set(graph.V[i], { x: xV, y: yV });
                }
            }

            function drawBipartiteGraph(state) {
                const { matching, cover, bfsRoot, highlightedPath } = state;
                const r = 15; // vertex radius

                // Draw edges
                p.push();
                graph.edges.forEach(edge => {
                    const posU = vertexPositions.get(edge.u);
                    const posV = vertexPositions.get(edge.v);
                    p.stroke('#808080');
                    p.strokeWeight(1);
                    p.line(posU.x, posU.y, posV.x, posV.y);
                });

                // Draw matching edges
                matching.forEach(edge => {
                    const posU = vertexPositions.get(edge.u);
                    const posV = vertexPositionsget(edge.v);
                    p.strokeWeight(4);
                    p.stroke('#A0A0FF'); // Light blue
                    p.line(posU.x, posU.y, posV.x, posV.y);
                });

                // Draw highlighted path
                if (highlightedPath.length > 0) {
                    for (let i = 0; i < highlightedPath.length - 1; i++) {
                         const pos1 = vertexPositions.get(highlightedPath[i]);
                         const pos2 = vertexPositions.get(highlightedPath[i+1]);
                         p.strokeWeight(3);
                         p.stroke('#FF0000'); // Red
                         p.line(pos1.x, pos1.y, pos2.x, pos2.y);
                    }
                }
                p.pop();

                // Draw vertices
                for (const [id, pos] of vertexPositions.entries()) {
                    p.push();
                    let fillColor = '#333333';
                    if (cover.includes(id)) {
                        fillColor = '#00C000'; // Green
                    } else if (id === bfsRoot) {
                        fillColor = '#FF0000'; // Red
                    }

                    p.stroke(fillColor);
                    p.fill(fillColor);
                    p.ellipse(pos.x, pos.y, r * 2, r * 2);

                    // Draw labels
                    p.noStroke();
                    p.fill(255);
                    p.textSize(14);
                    p.text(id.substring(1), pos.x, pos.y);
                    p.pop();
                }
            }

            function drawBfsTree(state) {
                const { bfsTree } = state;
                if (!bfsTree || Object.keys(bfsTree.nodes).length === 0) return;

                const treeWidth = p.width / 3 - 40;
                const nodeSize = 30;
                const levelHeight = 70;

                const nodePositions = new Map();
                const levelWidths = {};
                Object.values(bfsTree.nodes).forEach(node => {
                    if (!levelWidths[node.level]) levelWidths[node.level] = 0;
                    levelWidths[node.level]++;
                });

                Object.values(bfsTree.nodes).forEach(node => {
                    const x = 20 + (node.posInLevel + 0.5) * (treeWidth / levelWidths[node.level]);
                    const y = 50 + node.level * levelHeight;
                    nodePositions.set(node.id, {x, y});
                });

                // Draw edges
                p.push();
                p.stroke('#FF0000'); // Red
                p.strokeWeight(1.5);
                bfsTree.edges.forEach(edge => {
                    const pos1 = nodePositions.get(edge.from);
                    const pos2 = nodePositions.get(edge.to);
                    if(pos1 && pos2) p.line(pos1.x, pos1.y, pos2.x, pos2.y);
                });
                p.pop();

                // Draw nodes and labels
                p.push();
                p.rectMode(p.CENTER);
                p.textSize(12);
                for (const [id, pos] of nodePositions.entries()) {
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.fill('#FFA500'); // Orange
                    p.rect(pos.x, pos.y, nodeSize, nodeSize);
                    p.noStroke();
                    p.fill('#8080C0'); // Light gray/blue
                    p.text(id.substring(1), pos.x, pos.y);
                }
                p.pop();
            }

        };

        new p5(sketch, 'canvas-wrapper');
    </script>
</body>
</html>
