<script>
document.addEventListener('DOMContentLoaded', function() {

    // --- DOM Element References ---
    const plotContainer = document.getElementById('plot-container');
    const sliderElements = document.getElementById('slider-elements');
    const displayElements = document.getElementById('display-elements');
    const controls = {
        scaleLinear: document.getElementById('radio-scale-linear'),
        scaleLogarithmic: document.getElementById('radio-scale-logarithmic'),
        rangeFixed: document.getElementById('radio-range-fixed'),
        rangeAutomatic: document.getElementById('radio-range-automatic'),
        bubble: document.getElementById('check-bubble'),
        selection: document.getElementById('check-selection'),
        insertion: document.getElementById('check-insertion'),
        merge: document.getElementById('check-merge'),
        quick: document.getElementById('check-quick'),
        heap: document.getElementById('check-heap'),
        bogo: document.getElementById('check-bogo')
    };

    // --- Helper Functions ---
    const factorialCache = {};
    function factorial(n) {
        if (n < 0) return NaN;
        if (n === 0 || n === 1) return 1;
        if (factorialCache[n]) return factorialCache[n];
        let result = 1;
        for (let i = 2; i <= n; i++) {
            result *= i;
            factorialCache[i] = result;
        }
        return result;
    }

    function generateData(n) {
        const x = Array.from({ length: n }, (_, i) => i + 1);
        return {
            x: x,
            bubble: x.map(val => val * val),
            selection: x.map(val => 0.25 * val * val),
            insertionBogo: x.map(val => val * factorial(val)),
            merge: x.map(val => val <= 1 ? 0 : val * Math.log2(val)),
            quick: x.map(val => val <= 1 ? 0 : 1.2 * val * Math.log2(val)),
            heap: x.map(val => val <= 1 ? 0 : 1.5 * val * Math.log2(val)),
        };
    }

    // --- Plotting Logic ---
    let traces = [];
    let layout = {};

    function initializePlot() {
        const n = parseInt(sliderElements.value, 10);
        const data = generateData(n);

        traces = [
            {
                x: data.x, y: data.bubble, name: 'bubble', mode: 'lines',
                line: { color: 'green', width: 5 },
                visible: controls.bubble.checked
            },
            {
                x: data.x, y: data.selection, name: 'selection', mode: 'lines',
                line: { color: 'orange', dash: 'dot', width: 4 },
                visible: controls.selection.checked
            },
            {
                x: data.x, y: data.insertionBogo, name: 'insertion', mode: 'lines',
                line: { color: 'red', width: 2 },
                visible: controls.insertion.checked || controls.bogo.checked
            },
            {
                x: data.x, y: data.merge, name: 'merge', mode: 'lines',
                line: { color: 'blue', width: 2 },
                visible: controls.merge.checked
            },
            {
                x: data.x, y: data.quick, name: 'quick', mode: 'lines',
                line: { color: 'purple', width: 2 },
                visible: controls.quick.checked
            },
            {
                x: data.x, y: data.heap, name: 'heap', mode: 'lines',
                line: { color: 'cyan', width: 2 },
                visible: controls.heap.checked
            }
        ];

        layout = {
            title: 'time complexity',
            xaxis: {
                title: 'number of elements',
                range: [0, 20]
            },
            yaxis: {
                title: 'operations',
                type: 'linear',
                range: [0, 500]
            },
            legend: {
                orientation: 'h',
                y: -0.2,
                x: 0.5,
                xanchor: 'center'
            },
            margin: { b: 100 }
        };

        Plotly.newPlot(plotContainer, traces, layout, {responsive: true});
    }

    function updatePlot() {
        // 1. Get current state from controls
        const n = parseInt(sliderElements.value, 10);
        const scaleType = controls.scaleLinear.checked ? 'linear' : 'log';
        const isRangeFixed = controls.rangeFixed.checked;
        const visibility = {
            bubble: controls.bubble.checked,
            selection: controls.selection.checked,
            insertion: controls.insertion.checked || controls.bogo.checked,
            merge: controls.merge.checked,
            quick: controls.quick.checked,
            heap: controls.heap.checked
        };

        // 2. Generate new data
        const newData = generateData(n);

        // 3. Prepare traces update object for Plotly.react
        const dataUpdate = {
            x: [newData.x, newData.x, newData.x, newData.x, newData.x, newData.x],
            y: [newData.bubble, newData.selection, newData.insertionBogo, newData.merge, newData.quick, newData.heap],
            visible: [visibility.bubble, visibility.selection, visibility.insertion, visibility.merge, visibility.quick, visibility.heap]
        };

        // 4. Prepare layout update object
        const layoutUpdate = {
            'yaxis.type': scaleType,
            'xaxis.autorange': !isRangeFixed,
            'yaxis.autorange': !isRangeFixed
        };

        if (isRangeFixed) {
            layoutUpdate['xaxis.range'] = [0, 20];
            layoutUpdate['yaxis.range'] = [0, 500];
        }

        // 5. Update plot using Plotly.react
        Plotly.react(plotContainer, dataUpdate, layoutUpdate);
    }

    // --- Event Listeners ---
    sliderElements.addEventListener('input', () => {
        const value = sliderElements.value;
        displayElements.textContent = `+ ${value}`;
        updatePlot();
    });

    Object.values(controls).forEach(control => {
        control.addEventListener('change', updatePlot);
    });

    // --- Initial Call ---
    initializePlot();
});
</script>

</body>
</html>
