            white-space: nowrap;
        }
        #slider-xD {
            flex-grow: 1;
            margin: 0;
        }
        #label-xD {
            font-weight: bold;
            font-family: 'Courier New', Courier, monospace;
            width: 40px; /* Prevents layout shift */
            text-align: right;
        }
        #plot {
            border: 1px solid #ccc;
            background-color: white;
        }
    </style>
</head>
<body>

    <div id="main-container">
        <div id="control-panel">
            <label for="slider-xD">distillate composition</label>
            <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
            <span id="label-xD">0.84</span>
        </div>
        <div id="plot"></div>
    </div>

    <script>
        // --- 1. DOM Element References ---
        const slider = document.getElementById('slider-xD');
        const valueLabel = document.getElementById('label-xD');
        const plotDiv = document.getElementById('plot');

        // --- 2. Core Logic: updatePlot function ---
        function updatePlot(xD) {
            // --- Constants and VLE Function ---
            const a = 9;
            const b = -0.6;

            const y = (x) => (a * x) / (1 + (a - 1) * x + b * x * (1 - x));
            const r = (x, xD_val) => (xD_val - y(x)) / (y(x) - x);

            // --- Data Generation for Bifurcation Curve ---
            const x_vals = [];
            const r_vals = [];
            const step = 0.001;

            for (let x = 0.001; x < xD; x += step) {
                const r_val = r(x, xD);
                // Filter for physically meaningful, plottable values
                if (r_val >= 0 && r_val < 5 && isFinite(r_val)) {
                    x_vals.push(x);
                    r_vals.push(r_val);
                }
            }

            // --- Tangent Pinch Point Calculation ---
            const pinch_x = [];
            const pinch_y = [];
            // Search for a local maximum in the generated r_vals
            for (let i = 1; i < r_vals.length - 1; i++) {
                if (r_vals[i-1] < r_vals[i] && r_vals[i] > r_vals[i+1]) {
                    pinch_x.push(x_vals[i]);
                    pinch_y.push(r_vals[i]);
                    break; // Assume only one tangent pinch point
                }
            }

            // --- Plotly Traces and Layout ---
            const curveTrace = {
                x: x_vals,
                y: r_vals,
                mode: 'lines',
                type: 'scatter',
                name: 'Bifurcation Curve',
                line: {
                    color: '#8B0000',
                    width: 2.5
                }
            };

            const pinchTrace = {
                x: pinch_x,
                y: pinch_y,
                mode: 'markers',
                type: 'scatter',
                name: 'Tangent Pinch',
                marker: {
                    color: 'royalblue',
                    size: 10
                }
            };

            const layout = {
                xaxis: {
                    range: [0, 1.0],
                    gridcolor: '#e0e0e0',
                    dtick: 0.2,
                    title: 'pinch composition (x)'
                },
                yaxis: {
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0',
                    dtick: 0.5,
                    title: 'reflux ratio (r)'
                },
                showlegend: false,
                margin: { l: 50, r: 20, b: 50, t: 20 },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            // --- Render the plot ---
            Plotly.react(plotDiv, [curveTrace, pinchTrace], layout, {responsive: true});
        }

        // --- 3. Event Listeners and Initial Load ---
        function initialize() {
            // Set initial state from slider
            const initialXD = parseFloat(slider.value);
            valueLabel.textContent = initialXD.toFixed(2);

            // Draw the initial plot
            updatePlot(initialXD);

            // Add event listener for slider interaction
            slider.addEventListener('input', (event) => {
                const newXD = parseFloat(event.target.value);
                valueLabel.textContent = newXD.toFixed(2);
                updatePlot(newXD);
            });
        }

        // Run initialization on page load
        document.addEventListener('DOMContentLoaded', initialize);
    </script>

</body>
</html>
