            <div class="center-group">
                <select id="select-structure" size="4">
                    <option value="bond" selected>—</option>
                    <option value="water">Water</option>
                    <option value="cyclopropyl_ethane">Cyclopropyl-ethane</option>
                    <option value="borazine">Borazine</option>
                    <option value="isopropylamine">Isopropylamine</option>
                </select>
            </div>
            <button id="btn-add">add</button>
        </div>
    </div>

<script>
let sceneObjects = [];
let selectedTool = null;
let activeObject = null;
let interactionMode = null; // 'translate' or 'rotate'
let dragOffset = { x: 0, y: 0 };
let initialAngle = 0;

const ROTATE_THRESHOLD = 25; // pixels

// --- p5.js Sketch ---
function setup() {
    const canvasContainer = document.getElementById('canvas-container');
    const canvas = createCanvas(canvasContainer.offsetWidth - 4, canvasContainer.offsetHeight - 4);
    canvas.parent('canvas-container');
    canvas.id('drawing-canvas');

    textAlign(CENTER, CENTER);
    textSize(16);
    textFont('Arial');

    // --- Event Listeners ---
    const toolbarButtons = document.querySelectorAll('#toolbar button');
    toolbarButtons.forEach(btn => {
        btn.addEventListener('click', () => {
            if (btn.classList.contains('selected')) {
                // Deselect if clicked again
                btn.classList.remove('selected');
                selectedTool = null;
            } else {
                toolbarButtons.forEach(b => b.classList.remove('selected'));
                btn.classList.add('selected');
                selectedTool = {
                    type: btn.dataset.type,
                    value: btn.dataset.value
                };
            }
        });
    });

    document.getElementById('btn-undo').addEventListener('click', () => {
        if (sceneObjects.length > 0) {
            sceneObjects.pop();
        }
    });

    document.getElementById('btn-add').addEventListener('click', () => {
        const selector = document.getElementById('select-structure');
        const selectedValue = selector.value;
        if (selectedValue) {
            sceneObjects.push({
                type: 'structure',
                value: selectedValue,
                x: width / 2,
                y: height / 2,
                rotation: 0
            });
        }
    });
}

function draw() {
    background(255);

    // Draw all objects on the canvas
    sceneObjects.forEach(obj => {
        push();
        translate(obj.x, obj.y);
        rotate(obj.rotation);
        drawObject(obj);
        pop();
    });

    // Draw preview/ghost element at cursor
    if (selectedTool && mouseX > 0 && mouseX < width && mouseY > 0 && mouseY < height) {
        push();
        fill(0, 0, 0, 100); // semi-transparent
        noStroke();
        drawObject({ ...selectedTool, type: selectedTool.type, value: selectedTool.value });
        pop();
    }
}

// --- Interaction Handlers (p5.js specific) ---
function mousePressed() {
    // Prevent interaction outside canvas
    if (mouseX < 0 || mouseX > width || mouseY < 0 || mouseY > height) {
        return;
    }

    // 1. Placing a new object
    if (selectedTool) {
        sceneObjects.push({
            ...selectedTool,
            x: mouseX,
            y: mouseY,
            rotation: 0
        });
        // Reset tool after placement
        const selectedBtn = document.querySelector('#toolbar button.selected');
        if (selectedBtn) selectedBtn.classList.remove('selected');
        selectedTool = null;
        return;
    }

    // 2. Selecting an existing object for manipulation
    // Iterate backwards to select the top-most object
    for (let i = sceneObjects.length - 1; i >= 0; i--) {
        const obj = sceneObjects[i];
        const d = dist(mouseX, mouseY, obj.x, obj.y);

        // Use a generous hit-box for structures
        const hitThreshold = (obj.type === 'structure') ? 80 : 20;

        if (d < hitThreshold) {
            activeObject = obj;
            // Determine if we are rotating or translating
            if (d < ROTATE_THRESHOLD) {
                interactionMode = 'rotate';
                initialAngle = atan2(mouseY - activeObject.y, mouseX - activeObject.x) - activeObject.rotation;
            } else {
                interactionMode = 'translate';
                dragOffset.x = activeObject.x - mouseX;
                dragOffset.y = activeObject.y - mouseY;
            }
            // Bring the active object to the top of the render stack
            sceneObjects.splice(i, 1);
            sceneObjects.push(activeObject);
            return; // Stop after finding one object
        }
    }
}

function mouseDragged() {
    if (activeObject) {
        if (interactionMode === 'translate') {
            activeObject.x = mouseX + dragOffset.x;
            activeObject.y = mouseY + dragOffset.y;
        } else if (interactionMode === 'rotate') {
            const currentAngle = atan2(mouseY - activeObject.y, mouseX - activeObject.x);
            activeObject.rotation = currentAngle - initialAngle;
        }
    }
}

function mouseReleased() {
    activeObject = null;
    interactionMode = null;
}

// --- Drawing Helper Functions ---
function drawObject(obj) {
    stroke(0);
    strokeWeight(1.5);
    fill(0);
    textSize(16);

    switch(obj.type) {
        case 'atom':
        case 'charge':
            noStroke();
            text(obj.value, 0, 0);
            break;
        case 'structure':
            drawStructure(obj.value);
            break;
    }
}

function drawStructure(value) {
    switch(value) {
        case 'bond':
            line(-25, 0, 25, 0);
            break;
        case 'water':
            drawWater();
            break;
        case 'cyclopropyl_ethane':
            drawCyclopropylEthane();
            break;
        case 'borazine':
            drawBorazine();
            break;
        case 'isopropylamine':
            drawIsopropylamine();
            break;
    }
}

function drawWater() {
    // H2O
    noStroke();
    text('O', 0, 0);
    text('H', -25, -20);
    text('H', 25, -20);
    stroke(0);
    line(0, 0, -25, -20);
    line(0, 0, 25, -20);
}

function drawCyclopropylEthane() {
    const r = 30; // radius of cyclopropyl
    const p1 = { x: r * cos(PI/2), y: r * sin(PI/2) };       // top vertex
    const p2 = { x: r * cos(7*PI/6), y: r * sin(7*PI/6) };   // bottom-left
    const p3 = { x: r * cos(11*PI/6), y: r * sin(11*PI/6) }; // bottom-right

    line(p1.x, p1.y, p2.x, p2.y);
    line(p2.x, p2.y, p3.x, p3.y);
    line(p3.x, p3.y, p1.x, p1.y);

    const ethaneAttach = {x: p3.x + 25, y: p3.y};
    const ethaneEnd = {x: ethaneAttach.x + 25, y: ethaneAttach.y};
    line(p3.x, p3.y, ethaneAttach.x, ethaneAttach.y);
    line(ethaneAttach.x, ethaneAttach.y, ethaneEnd.x, ethaneEnd.y);

    noStroke();
    text('C', p1.x, p1.y);
    text('C', p2.x, p2.y);
    text('C', p3.x, p3.y);
    text('C', ethaneAttach.x, ethaneAttach.y);
    text('C', ethaneEnd.x, ethaneEnd.y);
}

function drawBorazine() {
    const r = 35;
    for (let i = 0; i < 6; i++) {
        const angle1 = i * PI / 3;
        const angle2 = (i + 1) * PI / 3;
        line(r * cos(angle1), r * sin(angle1), r * cos(angle2), r * sin(angle2));

        const atomX = r * cos(angle1);
        const atomY = r * sin(angle1);
        const atomType = (i % 2 === 0) ? 'N' : 'B';
        noStroke();
        text(atomType, atomX, atomY);
        stroke(0);

        const h_r = r + 20;
        const h_x = h_r * cos(angle1);
        const h_y = h_r * sin(angle1);
        line(atomX, atomY, h_x, h_y);
        noStroke();
        text('H', h_x, h_y);
        stroke(0);
    }
}

function drawIsopropylamine() {
    const centralC = {x: 0, y: 0};
    const n = {x: 30, y: 0};
    const c1 = {x: -20, y: -30};
    const c2 = {x: -20, y: 30};

    line(centralC.x, centralC.y, n.x, n.y);
    line(centralC.x, centralC.y, c1.x, c1.y);
    line(centralC.x, centralC.y, c2.x, c2.y);

    noStroke();
    text('C', centralC.x, centralC.y);
    text('N', n.x, n.y);
    text('C', c1.x, c1.y);
    text('C', c2.x, c2.y);
}

// Adjust canvas size on window resize
function windowResized() {
    const canvasContainer = document.getElementById('canvas-container');
    resizeCanvas(canvasContainer.offsetWidth - 4, canvasContainer.offsetHeight - 4);
}
</script>

</body>
</html>
