</head>
<body>

    <div id="controls-container">
        <div class="control-row">
            <label for="slider-amplitude">Amplitude \(a\)</label>
            <input type="range" id="slider-amplitude" min="0.1" max="2.0" step="0.01" value="1.5">
            <span id="value-amplitude">1.50</span>
        </div>
        <div class="control-row">
            <label for="slider-frequency">Frequency \(f\)</label>
            <input type="range" id="slider-frequency" min="0.1" max="1.0" step="0.01" value="0.3">
            <span id="value-frequency">0.30</span>
        </div>
        <div class="control-row">
            <label for="slider-wavelength">Wavelength \(\lambda\)</label>
            <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
            <span id="value-wavelength">4.5</span>
        </div>
        <div class="control-row">
            <label for="slider-time">Time \(t\)</label>
            <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
            <span id="value-time">2.50</span>
        </div>
        <div class="control-row">
            <label for="select-phase">Initial phase \(\phi_0\)</label>
            <select id="select-phase">
                <option value="-3.141592653589793">-π</option>
                <option value="-2.0943951023931953">-2π/3</option>
                <option value="-1.5707963267948966" selected>-π/2</option>
                <option value="-1.0471975511965976">-π/3</option>
                <option value="0">0</option>
                <option value="1.0471975511965976">π/3</option>
                <option value="1.5707963267948966">π/2</option>
                <option value="2.0943951023931953">2π/3</option>
                <option value="3.141592653589793">π</option>
            </select>
        </div>
    </div>

    <div id="formula-display"></div>

    <div id="canvas-container"></div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Element References ---
            const sliderAmplitude = document.getElementById('slider-amplitude');
            const valueAmplitude = document.getElementById('value-amplitude');
            const sliderFrequency = document.getElementById('slider-frequency');
            const valueFrequency = document.getElementById('value-frequency');
            const sliderWavelength = document.getElementById('slider-wavelength');
            const valueWavelength = document.getElementById('value-wavelength');
            const sliderTime = document.getElementById('slider-time');
            const valueTime = document.getElementById('value-time');
            const selectPhase = document.getElementById('select-phase');
            const formulaDisplay = document.getElementById('formula-display');

            // --- State Object ---
            const state = {
                A: 0, f: 0, lambda: 0, t: 0, phi0: 0,
                k: 0, omega: 0
            };

            // --- Update Logic ---
            function updateStateAndUI() {
                // Read values from controls
                state.A = parseFloat(sliderAmplitude.value);
                state.f = parseFloat(sliderFrequency.value);
                state.lambda = parseFloat(sliderWavelength.value);
                state.t = parseFloat(sliderTime.value);
                state.phi0 = parseFloat(selectPhase.value);

                // Update UI display values
                valueAmplitude.textContent = state.A.toFixed(2);
                valueFrequency.textContent = state.f.toFixed(2);
                valueWavelength.textContent = state.lambda.toFixed(1);
                valueTime.textContent = state.t.toFixed(2);

                // Recalculate derived parameters
                state.k = (2 * Math.PI) / state.lambda;
                state.omega = (2 * Math.PI) * state.f;

                updateFormula();

                // Trigger p5.js redraw if it's initialized
                if (window.p5sketch) {
                    window.p5sketch.redraw();
                }
            }

            function updateFormula() {
                const A_str = state.A.toFixed(2);
                const k_str = state.k.toFixed(2);
                const omega_str = state.omega.toFixed(2);
                const phi0_str = (state.phi0 / Math.PI).toFixed(2);

                let formula = '';
                const epsilon = 0.001;

                // Simplification Logic
                if (Math.abs(state.phi0 - Math.PI / 2) < epsilon) {
                    formula = `${A_str} \\sin(${k_str}x - ${omega_str}t)`;
                } else if (Math.abs(state.phi0 + Math.PI / 2) < epsilon) {
                    formula = `-${A_str} \\sin(${k_str}x - ${omega_str}t)`;
                } else if (Math.abs(state.phi0 - Math.PI) < epsilon || Math.abs(state.phi0 + Math.PI) < epsilon) {
                    formula = `-${A_str} \\cos(${k_str}x - ${omega_str}t)`;
                } else if (Math.abs(state.phi0) < epsilon) {
                    formula = `${A_str} \\cos(${k_str}x - ${omega_str}t)`;
                } else {
                    const sign = state.phi0 > 0 ? '+' : '-';
                    const abs_phi0_str = Math.abs(state.phi0).toFixed(2);
                    formula = `${A_str} \\cos(${omega_str}t - ${k_str}x ${sign} ${abs_phi0_str})`;
                }

                formulaDisplay.innerHTML = `\\( y(x,t) = ${formula} \\)`;

                // Tell MathJax to re-render the div
                if (window.MathJax && MathJax.typesetPromise) {
                    MathJax.typesetPromise([formulaDisplay]);
                }
            }

            // --- Event Listeners ---
            const controls = [sliderAmplitude, sliderFrequency, sliderWavelength, sliderTime, selectPhase];
            controls.forEach(control => {
                control.addEventListener('input', updateStateAndUI);
                control.addEventListener('change', updateStateAndUI); // For select
            });


            // --- P5.js Sketch ---
            const sketch = (p) => {
                const PIXELS_PER_UNIT = 40;
                const X_OFFSET = 30;
                let Y_OFFSET;

                p.setup = () => {
                    const canvasContainer = document.getElementById('canvas-container');
                    const w = canvasContainer.clientWidth - 20;
                    const h = Math.min(canvasContainer.clientHeight - 20, 500);
                    const canvas = p.createCanvas(w, h);
                    canvas.parent('canvas-container');

                    Y_OFFSET = p.height / 2;
                    p.noLoop(); // We will redraw manually
                    updateStateAndUI(); // Initial draw
                };

                p.draw = () => {
                    p.background(255);
                    p.translate(X_OFFSET, Y_OFFSET);

                    drawGridAndAxes();

                    p.scale(1, -1); // Invert Y-axis for standard math coordinates
                    drawWave();
                    drawPointsAndVectors();
                    p.scale(1, -1); // Revert for next draw cycle if needed
                };

                function drawGridAndAxes() {
                    // Draw Axes
                    p.stroke(0);
                    p.strokeWeight(1.5);
                    // X-Axis
                    p.line(0, 0, p.width - X_OFFSET, 0);
                    p.triangle(p.width - X_OFFSET, -5, p.width - X_OFFSET, 5, p.width - X_OFFSET + 10, 0);
                    // Y-Axis
                    p.line(0, -p.height/2 + 20, 0, p.height/2 - 20);
                    p.triangle(-5, p.height/2 - 20, 5, p.height/2 - 20, 0, p.height/2 - 10);

                    // Axis labels
                    p.noStroke();
                    p.fill(0);
                    p.text('x', p.width - X_OFFSET - 10, -15);
                    p.text('y', 10, -p.height/2 + 25);

                    // Draw Grid and Ticks
                    p.strokeWeight(1);
                    const numTicks = Math.floor((p.width - X_OFFSET) / PIXELS_PER_UNIT);
                    for (let i = 1; i <= numTicks; i++) {
                        const x = i * PIXELS_PER_UNIT;
                        // Tick mark
                        p.stroke(0);
                        p.line(x, -5, x, 5);
                        // Grid line
                        p.stroke(var(--grid-color));
                        p.drawingContext.setLineDash([5, 5]);
                        p.line(x, -p.height/2, x, p.height/2);
                        p.drawingContext.setLineDash([]);
                        // Tick label
                        p.noStroke();
                        p.fill(0);
                        p.textAlign(p.CENTER);
                        p.text(i, x, 20);
                    }
                }

                function drawWave() {
                    p.beginShape();
                    p.noFill();
                    p.stroke(var(--wave-color));
                    p.strokeWeight(2);

                    for (let px = 0; px < p.width - X_OFFSET; px++) {
                        const x = px / PIXELS_PER_UNIT;
                        const y = state.A * Math.cos(state.omega * state.t - state.k * x + state.phi0);
                        p.vertex(px, y * PIXELS_PER_UNIT);
                    }
                    p.endShape();
                }

                function drawPointsAndVectors() {
                    const numPoints = Math.floor((p.width - X_OFFSET) / PIXELS_PER_UNIT);
                    for (let i = 1; i <= numPoints; i++) {
                        const x = i;
                        // Calculate particle state
                        const phase_at_x = state.omega * state.t - state.k * x + state.phi0;
                        const y = state.A * Math.cos(phase_at_x);
                        const v = -state.A * state.omega * Math.sin(phase_at_x); // v = dy/dt
                        const a = -state.A * state.omega * state.omega * Math.cos(phase_at_x); // a = dv/dt

                        const x_px = x * PIXELS_PER_UNIT;
                        const y_px = y * PIXELS_PER_UNIT;

                        // Draw particle point
                        p.fill(0);
                        p.noStroke();
                        p.circle(x_px, y_px, 8);

                        // Draw vectors
                        // Velocity vector (scaled by 15)
                        drawArrow(x_px, y_px, v * 15, var(--velocity-color), 'v');
                        // Acceleration vector (scaled by 5)
                        drawArrow(x_px, y_px, a * 5, var(--acceleration-color), 'a');
                    }
                }

                function drawArrow(baseX, baseY, vectorY, color, label) {
                    if (Math.abs(vectorY) < 1) return; // Don't draw tiny vectors

                    p.push();
                    p.stroke(color);
                    p.fill(color);
                    p.strokeWeight(2);

                    // Line
                    p.line(baseX, baseY, baseX, baseY + vectorY);

                    // Arrowhead
                    const headSize = 8;
                    const tipX = baseX;
                    const tipY = baseY + vectorY;
                    p.translate(tipX, tipY);
                    p.rotate(vectorY < 0 ? 0 : p.PI);
                    p.triangle(0, 0, -headSize/2, headSize, headSize/2, headSize);

                    // Revert transformations for text
                    p.pop();

                    // Text Label (drawn without transformations)
                    p.push();
                    p.noStroke();
                    p.fill(color);
                    p.textAlign(p.LEFT, p.CENTER);

                    // We need to un-invert the y-axis for text drawing
                    p.translate(baseX, baseY);
                    p.scale(1, -1);
                    p.text(label, 10, -vectorY - (vectorY > 0 ? 10 : -10));
                    p.pop();
                }

                // Make sketch instance accessible globally to trigger redraw
                window.p5sketch = p;
            };

            // Initialize P5
            new p5(sketch);
        });
    </script>
</body>
