            font-size: 16px;
        }
        #energy-value {
            font-weight: bold;
            font-family: 'Courier New', Courier, monospace;
        }
    </style>
</head>
<body>

    <div class="main-container">
        <!-- Control Panel Section -->
        <div class="control-panel">
            <label for="slider-eccentricity">eccentricity ε</label>
            <input type="range" id="slider-eccentricity" min="0" max="1.5" step="0.01" value="0">
            <span id="value-eccentricity">0.00</span>
        </div>

        <!-- Visualization Area Section -->
        <div class="visualization-container">
            <h3>Kepler orbits</h3>
            <div id="canvas-container">
                <div id="energy-display">
                    energy = <span id="energy-value"></span>
                </div>
            </div>
        </div>
    </div>

    <script>
        // --- P5.js Sketch ---
        const sketch = (p) => {
            let slider;
            let valueDisplay;
            let energyValueDisplay;

            const canvasWidth = 700;
            const canvasHeight = 500;
            const scaleFactor = 30; // pixels per unit
            const originX = canvasWidth * 0.7;
            const originY = canvasHeight / 2;

            p.setup = () => {
                let canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
                p.noLoop(); // We only redraw on slider input

                // Get DOM elements
                slider = p.select('#slider-eccentricity');
                valueDisplay = p.select('#value-eccentricity');
                energyValueDisplay = document.getElementById('energy-value'); // Use direct DOM for MathJax compatibility

                // Attach event listener
                slider.input(() => {
                    p.redraw();
                });

                // Initial draw
                p.redraw();
            };

            p.draw = () => {
                const epsilon = parseFloat(slider.value());

                // 1. Update UI elements
                updateUI(epsilon);

                // 2. Clear and set up canvas
                p.background('#f0f0f0');

                // 3. Set up coordinate system
                p.push(); // Save default drawing state
                p.translate(originX, originY);
                p.scale(scaleFactor, -scaleFactor); // Scale and flip Y-axis

                // 4. Draw visualization elements
                drawAxes();
                drawCentralBody();
                drawOrbit(epsilon);

                p.pop(); // Restore default drawing state
            };

            function updateUI(epsilon) {
                // Update eccentricity value display
                valueDisplay.html(epsilon.toFixed(2));

                // Update energy display
                if (epsilon === 0) {
                    energyValueDisplay.innerHTML = `\\(-\\frac{1}{4}\\)`;
                    // Re-render MathJax for the new formula
                    if (window.MathJax) {
                        MathJax.typesetPromise([energyValueDisplay]);
                    }
                } else {
                    const energy = -0.25 * (1 - epsilon * epsilon);
                    energyValueDisplay.innerHTML = energy.toFixed(2);
                }
            }

            function drawAxes() {
                p.stroke(0);
                p.strokeWeight(1 / scaleFactor);

                // Axis lines
                p.line(-15, 0, 5, 0); // X-axis
                p.line(0, -7, 0, 7);  // Y-axis

                // Tick marks and labels
                p.push();
                p.scale(1, -1); // Un-flip Y for text rendering
                p.noStroke();
                p.fill(0);
                p.textAlign(p.CENTER, p.TOP);

                // X-axis ticks
                for (let i = -15; i <= 5; i++) {
                    if (i !== 0) {
                        p.text(i, i, 5 / scaleFactor);
                        p.stroke(0);
                        p.line(i, -3 / scaleFactor, i, 3 / scaleFactor);
                        p.noStroke();
                    }
                }

                // Y-axis ticks
                p.textAlign(p.LEFT, p.CENTER);
                for (let i = -7; i <= 7; i++) {
                     if (i !== 0) {
                        p.text(i, 5 / scaleFactor, -i);
                        p.stroke(0);
                        p.line(-3 / scaleFactor, i, 3 / scaleFactor, i);
                        p.noStroke();
                    }
                }

                // Axis labels
                p.textAlign(p.RIGHT, p.CENTER);
                p.text("Global'x", 5, 15 / scaleFactor);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text("Global'y", 15 / scaleFactor, -7);

                p.pop();
            }

            function drawCentralBody() {
                p.fill(0);
                p.noStroke();
                const diameter = 6 / scaleFactor;
                p.circle(0, 0, diameter);
            }

            function drawOrbit(epsilon) {
                p.noFill();
                p.stroke('#4a80b5');
                p.strokeWeight(2 / scaleFactor);

                const p_const = 2; // Semi-latus rectum

                p.beginShape();

                // Case: Circle (ε = 0)
                if (epsilon === 0) {
                    for (let theta = 0; theta < p.TWO_PI; theta += 0.01) {
                        const x = p_const * p.cos(theta) - p_const;
                        const y = p_const * p.sin(theta);
                        p.vertex(x, y);
                    }
                // Case: Parabola (ε = 1)
                } else if (epsilon === 1.0) {
                    // Limit angle to keep plot contained
                    for (let theta = -2.2; theta <= 2.2; theta += 0.01) {
                        const r = p_const / (1 + epsilon * p.cos(theta));
                        if (r > 0 && r < 100) { // Safety check
                            const x = r * p.cos(theta);
                            const y = r * p.sin(theta);
                            p.vertex(x, y);
                        }
                    }
                // Case: Hyperbola (ε > 1)
                } else if (epsilon > 1.0) {
                    const angleLimit = p.acos(-1 / epsilon);
                    // Iterate from -limit to +limit to draw one branch
                    for (let theta = -angleLimit + 0.01; theta < angleLimit - 0.01; theta += 0.01) {
                        const r = p_const / (1 + epsilon * p.cos(theta));
                        const x = r * p.cos(theta);
                        const y = r * p.sin(theta);
                        p.vertex(x, y);
                    }
                // Case: Ellipse (0 < ε < 1)
                } else {
                     for (let theta = 0; theta <= p.TWO_PI + 0.01; theta += 0.01) {
                        const r = p_const / (1 + epsilon * p.cos(theta));
                        const x = r * p.cos(theta);
                        const y = r * p.sin(theta);
                        p.vertex(x, y);
                    }
                }

                p.endShape();
            }
        };

        // Initialize p5 sketch
        new p5(sketch);

    </script>
</body>
</html>
