    }

    function updateValueDisplays() {
        for (const key in values) {
            const value = parseFloat(sliders[key].value);
            values[key].textContent = sliderConfig[key].format(value);
        }
    }

    // --- DATA CALCULATION LOGIC ---

    function calculatePlot1Data(poc, eh, hg_m) {
        const srb = Array.from({ length: 100 }, (_, i) => i + 1);
        const C = 125 * (-eh + 200) * poc * hg_m;
        const mi = srb.map(s => C / s);
        return { x: srb, y: mi };
    }

    function calculatePlot2Data(eh, hg_m) {
        const srb = Array.from({ length: 100 }, (_, i) => i + 1);
        const pocValues = { river: 56, estuarine: 6, seawater: 0.3 };
        const data = {};
        for (const env in pocValues) {
            const poc = pocValues[env];
            const C = 125 * (-eh + 200) * poc * hg_m;
            const mi = srb.map(s => C / s);
            data[env] = { x: srb, y: mi };
        }
        return data;
    }

    function calculatePlot3Data(k, a) {
        const hgT = Array.from({ length: 201 }, (_, i) => i * 0.01); // 0 to 2.0 with 0.01 steps
        const meHg = hgT.map(h => (a * h) / (k + h));
        return { x: hgT, y: meHg };
