        }], {
            ...commonLayout,
            title: 'MeHg index: variable POC',
            xaxis: { ...commonLayout.xaxis, title: 'SRB (dSrB)', range: [0, 100] },
            yaxis: { ...commonLayout.yaxis, title: 'methyl mercury index (MI)', range: [0, 2.0] },
        }, plotlyConfig);

        // Plot 2
        const plot2Data = calculatePlot2Data(eh, hg_m);
        Plotly.newPlot('plot2', [
            { ...plot2Data.river, name: 'River (POC=56)', mode: 'lines', line: { color: '#1f77b4' } },
            { ...plot2Data.estuarine, name: 'Estuarine (POC=6)', mode: 'lines', line: { color: '#aec7e8' } },
            { ...plot2Data.seawater, name: 'Seawater (POC=0.3)', mode: 'lines', line: { color: '#d1e0f0' } }
