                font: { size: 14 },
                bgcolor: 'rgba(255, 255, 255, 0.7)'
            }]
        }, plotlyConfig);
    }

    // --- UPDATE LOGIC ---
    function updatePlots() {
        // Get current slider values
        const poc = parseFloat(sliders.poc.value);
        const eh = parseFloat(sliders.eh.value);
        const hg_m = parseFloat(sliders.hg_m.value);
        const k = parseFloat(sliders.k.value);
        const a = parseFloat(sliders.a.value);

        // Update Plot 1
        const plot1Data = calculatePlot1Data(poc, eh, hg_m);
