        // Update Plot 2
        const plot2Data = calculatePlot2Data(eh, hg_m);
        Plotly.restyle('plot2', {
            y: [plot2Data.river.y, plot2Data.estuarine.y, plot2Data.seawater.y]
        }, [0, 1, 2]);

        // Update Plot 3
        const plot3Data = calculatePlot3Data(k, a);
        Plotly.restyle('plot3', { y: [plot3Data.y] }, [0]);
        Plotly.relayout('plot3', {
            'annotations[0].text': `[MeHg] = ${a.toFixed(3)}`
        });
