                    const I = math.identity(N);
                    // Oracle U_w
                    const Uw_diag = Array(N).fill(1).map((_, i) => solutionStates.includes(i) ? -1 : 1);
                    const Uw = math.diag(math.matrix(Uw_diag));
                    // Diffusion U_s = 2*P_0 - I
                    const Us = math.subtract(math.multiply(P0_matrix, 2), I);
                    groverOperator = math.multiply(Us, Uw);
                }
                let psi = initialPsi;
                for (let i = 0; i < currentStep; i++) {
                    psi = math.multiply(groverOperator, psi);
                }
                return psi;

            case 'adiabatic':
                const H_adiabatic = math.add(
                    math.multiply(H0, 1 - s),
                    math.multiply(Hp, s)
                );
                const eigs_adiabatic = math.eigs(H_adiabatic);
                const minEigenvalueIndex = eigs_adiabatic.values.indexOf(Math.min(...eigs_adiabatic.values));
                return eigs_adiabatic.eigenvectors[minEigenvalueIndex].vector;

            case 'discrete-adiabatic':
            case 'heuristic':
                let psi_discrete = initialPsi;
                if (currentStep > 0) {
                    for (let k = 0; k < currentStep; k++) {
                        const sk = k / totalSteps;
                        const H_k = math.add(math.multiply(H0, 1 - sk), math.multiply(Hp, sk));
                        const Uk = math.expm(math.multiply(H_k, math.complex(0, -1)));
                        psi_discrete = math.multiply(Uk, psi_discrete);
                    }
                }
                return psi_discrete;

            default:
                return initialPsi;
        }
    };

    // --- PLOTTING FUNCTIONS ---
    const plotAmplitudes = (psi) => {
        const amplitudes = psi.toArray();
        const colors = conflictNumbers.map(c => c / Math.max(...conflictNumbers));

        const trace = {
            x: amplitudes.map(c => c.re),
            y: amplitudes.map(c => c.im),
            mode: 'markers',
            type: 'scattergl',
            marker: {
                color: conflictNumbers,
                colorscale: 'Viridis',
                reversescale: true,
                size: 8,
                opacity: 0.8,
                colorbar: {
                    title: 'Conflicts'
                }
            },
            text: Array(N).fill(0).map((_, i) => `State: ${i}<br>Conflicts: ${conflictNumbers[i]}`),
            hoverinfo: 'text'
        };

        const solutionsTrace = {
            x: solutionStates.map(i => amplitudes[i].re),
            y: solutionStates.map(i => amplitudes[i].im),
            mode: 'markers',
            type: 'scattergl',
            marker: {
                color: 'black',
                size: 15,
                symbol: 'star'
            },
            text: solutionStates.map(i => `SOLUTION<br>State: ${i}`),
            hoverinfo: 'text'
        };

        let data = [trace, solutionsTrace];

        // Highlight initial state point
        if(state.currentStep === 0) {
           const nonSolutionIdx = conflictNumbers.findIndex(c => c > 0);
           if(nonSolutionIdx !== -1){
              const initialTrace = {
                  x: [amplitudes[nonSolutionIdx].re],
                  y: [amplitudes[nonSolutionIdx].im],
                  mode: 'markers',
                  type: 'scattergl',
                  marker: { color: 'red', size: 12 },
                  text: ['Initial State'],
                  hoverinfo: 'text'
              };
              data.push(initialTrace);
           }
        }

        const layout = {
            xaxis: { title: 'Re', zeroline: true, range: [-0.3, 0.3]},
            yaxis: { title: 'Im', zeroline: true, scaleanchor: 'x', scaleratio: 1, range: [-0.3, 0.3]},
            margin: { l: 40, r: 20, b: 40, t: 20 },
            showlegend: false,
            hovermode: 'closest',
            autosize: true
        };

        Plotly.react(controls.plots.amplitudes, data, layout, {responsive: true});
    };

    const plotProbability = (psi) => {
        const { algorithm, probPlotMode } = state;
        const probabilities = psi.toArray().map(c => math.abs(c) ** 2);
        let data, layout;

        if (probPlotMode === 'conflicts' || (probPlotMode === 'eigenvalues' && algorithm !== 'unstructured')) {
            const probByConflict = {};
            conflictNumbers.forEach((c, i) => {
                if (!probByConflict[c]) probByConflict[c] = 0;
                probByConflict[c] += probabilities[i];
            });

            const x = Object.keys(probByConflict).map(Number).sort((a, b) => a - b);
            const y = x.map(c => probByConflict[c]);

            data = [{
                x: x,
                y: y,
                type: 'bar',
                width: 0.1,
                marker: { color: '#007bff' }
            }];

            layout = {
                xaxis: { title: probPlotMode === 'conflicts' ? 'number of conflicts' : 'eigenvalue (conflict number)' },
                yaxis: { title: 'probability', range: [0, 1] },
                margin: { l: 50, r: 20, b: 40, t: 20 },
                autosize: true
            };

        } else { // Eigenvalues mode for Unstructured search
            if (!groverEigs) { // Cache eigs
                if (!groverOperator) calculateStateVector(); // Ensure operator exists
                groverEigs = math.eigs(groverOperator);
            }
            const evecs = groverEigs.eigenvectors;

            // Project current state onto eigenvectors
            const projections = evecs.map(evec => math.multiply(math.transpose(math.conj(evec.vector)), psi));
            const probs = projections.map(p => math.abs(p)**2);

            // Find two most significant eigenvectors
            const sortedProbs = probs.map((p, i) => ({p, i})).sort((a, b) => b.p - a.p);
            const significantIndices = [sortedProbs[0].i, sortedProbs[1].i];

            const x = significantIndices.map(i => math.atan2(groverEigs.values[i].im, groverEigs.values[i].re));
            const y = significantIndices.map(i => probs[i]);

            data = [{
                x: x,
                y: y,
                type: 'bar',
                width: 0.1,
                marker: { color: '#007bff' },
                text: x.map(val => `arg(λ) ≈ ${val.toFixed(2)}`),
                hoverinfo: 'y+text'
            }];

            layout = {
                xaxis: { title: 'arg(eigenvalue)' },
                yaxis: { title: 'probability', range: [0, 1] },
                margin: { l: 50, r: 20, b: 40, t: 20 },
                autosize: true
            };
        }

        Plotly.react(controls.plots.probability, data, layout, {responsive: true});
    };

    // --- MAIN UPDATE FUNCTION ---
    const update = () => {
        // 1. Read state from controls (already in `state` object)
        // 2. Calculate state vector
        const psi = calculateStateVector();
        // 3. Generate plot data and redraw
        plotAmplitudes(psi);
        plotProbability(psi);
    };

    // --- EVENT HANDLERS ---
    const handleAlgorithmChange = (e) => {
        const newAlgorithm = e.target.id.replace('btn-', '').replace('-', '_');
        state.algorithm = newAlgorithm === 'discrete_adiabatic' ? 'discrete-adiabatic' : newAlgorithm;

        const defaults = {
            unstructured: 5,
            adiabatic: 200,
            'discrete-adiabatic': 200,
            heuristic: 10,
        };
        state.totalSteps = defaults[state.algorithm];
        state.currentStep = 0;

        groverOperator = null; // Clear cache
        groverEigs = null;

        updateUI();
        update();
    };

    const handleTotalStepsChange = (e) => {
        state.totalSteps = Number(e.target.value);
        if (state.currentStep > state.totalSteps) {
            state.currentStep = state.totalSteps;
        }
        updateUI();
        update();
    };

    const handleCurrentStepChange = (e) => {
        state.currentStep = Number(e.target.value);
        updateUI();
        update();
    };

    const handleProbPlotModeChange = (e) => {
        state.probPlotMode = e.target.id.replace('btn-', '');
        updateUI();
        update();
    };

    // --- UI UPDATE FUNCTION ---
    const updateUI = () => {
        // Algorithm buttons
        for (const key in controls.algorithm) {
            const btnKey = key.replace('_', '-');
            controls.algorithm[key].classList.toggle('active', state.algorithm === key);
        }

        // Total steps slider
        controls.totalSteps.slider.value = state.totalSteps;
        controls.totalSteps.display.textContent = state.totalSteps;

        // Current step slider
        controls.currentStep.slider.max = state.totalSteps;
        controls.currentStep.slider.value = state.currentStep;
        controls.currentStep.label.textContent = `current step (0 to ${state.totalSteps})`;
        controls.currentStep.display.textContent = state.currentStep;

        // Probability plot buttons
        controls.probPlot.conflicts.classList.toggle('active', state.probPlotMode === 'conflicts');
        controls.probPlot.eigenvalues.classList.toggle('active', state.probPlotMode === 'eigenvalues');
    };

    // --- INITIALIZATION ---
    const init = () => {
        // Setup problem
        initializeProblem();

        // Attach event listeners
        Object.values(controls.algorithm).forEach(btn => btn.addEventListener('click', handleAlgorithmChange));
        controls.totalSteps.slider.addEventListener('input', handleTotalStepsChange);
        controls.currentStep.slider.addEventListener('input', handleCurrentStepChange);
        Object.values(controls.probPlot).forEach(btn => btn.addEventListener('click', handleProbPlotModeChange));

        // Initial UI state and plot
        updateUI();
        update();
    };

    init();
});
</script>

</body>
</html>
