                    yaxis: { gridcolor: '#e0e0e0', zerolinecolor: '#cccccc', tickfont: { size: 12 } },
                    showlegend: false,
                    margin: { t: 50, b: 60, l: 70, r: 30 }
                };

                if (plotType === 'vs_temp') {
                    // --- Plot 1: Fugacity vs. Temperature ---
                    const P = parseFloat(sliderPressure.value); // pressure in bar
                    const Tsat = 5818 / (8.65 - Math.log10(P)) - 230; // T in °C

                    const T_values = Array.from({ length: (400 - 280) * 2 + 1 }, (_, i) => 280 + i / 2);
                    const f_sat_func = T => Math.pow(10, 8.65 - 5818 / (230 + T));

                    // Traces
                    const stableLiquid_x = T_values.filter(t => t <= Tsat);
                    const stableVapor_x = T_values.filter(t => t >= Tsat);
                    const metastableLiquid_x = T_values.filter(t => t >= Tsat);
                    const metastableVapor_x = T_values.filter(t => t <= Tsat);

                    data = [
