                        { x: stableLiquid_x, y: stableLiquid_x.map(f_sat_func), type: 'scatter', mode: 'lines', line: { color: '#0000FF', width: 2.5 } },
                        { x: stableVapor_x, y: stableVapor_x.map(() => P), type: 'scatter', mode: 'lines', line: { color: '#0000FF', width: 2.5 } },
                        { x: [Tsat], y: [P], type: 'scatter', mode: 'markers', marker: { color: '#000000', size: 8 } },
                        { x: [Tsat, Tsat], y: [0, P], type: 'scatter', mode: 'lines', line: { color: '#000000', dash: 'dash' } }
                    ];

                    layout = {
                        ...generalLayout,
                        xaxis: { ...generalLayout.xaxis, title: 'temperature (°C)', range: [280, 400] },
