                            { x: 390, y: P + 0.008, text: 'vapor', showarrow: false, font: { color: '#0000FF', size: 16 } },
                            { x: Tsat, y: P, text: 'T<sup>sat</sup>', showarrow: true, ax: 0, ay: -35, font: { color: '#000000', size: 14 } }
                        ]
                    };
                } else { // 'vs_press'
                    // --- Plot 2: Fugacity vs. Pressure ---
                    const T = parseFloat(sliderTemperature.value); // temperature in K
                    const isHighPressure = checkboxHighPressure.checked;

                    // P^sat is calculated in bar, then converted to MPa for plotting
                    const Psat_bar = Math.pow(10, 10.83 - 5047 / T);
                    const Psat_MPa = Psat_bar / 10.0;

                    // This function assumes P is in MPa and T is in K. The constant 25 has units K/MPa.
                    const f_V_func = (P_MPa, T_K) => {
                        return isHighPressure ? P_MPa * Math.exp(-25 * P_MPa / T_K) : P_MPa;
                    };

                    const fsat_MPa = f_V_func(Psat_MPa, T);

                    const P_values = Array.from({ length: 3 * 100 + 1 }, (_, i) => i / 100);

                    // Traces
