                            { x: Psat_MPa, y: fsat_MPa, text: 'P<sup>sat</sup>', showarrow: true, ax: 0, ay: -35, font: { color: '#000000', size: 14 } }
                        ]
                    };
                }

                Plotly.react(plotContainer, data, layout, { responsive: true });
            }

            // Initial setup and first plot render
            setupControls();
            updatePlot();
        };
