let p5_instance;

// p5.js Sketch
const sketch = (p) => {
    // --- STATE VARIABLES ---
    let R; // Radius of the Bloch sphere slice
    let canvasSize = 400;

    // Vectors (using p5.Vector)
    let vec_a, vec_b, vec_c;
    let vec_d_opt;
    let vec_c_opt, vec_d_opt_for_c_opt; // For optimal pair

    // Control values
    let theta;
    let showMetricCircle, showMetricRegion, showFirstOrderVec, showFirstOrderRegion, showOptimalPair, showOptimalRegion;

    // Interaction state
    let isDraggingC = false;

    // DOM Elements
    const sliderTheta = document.getElementById('slider-theta');
    const spanTheta = document.getElementById('span-theta-value');
    const spanSinSqTheta = document.getElementById('span-sinsq-theta-value');
    const cbMetricCircle = document.getElementById('checkbox-metric-circle');
    const cbMetricRegion = document.getElementById('checkbox-metric-region');
    const cbFirstOrderVec = document.getElementById('checkbox-first-order-vec');
    const cbFirstOrderRegion = document.getElementById('checkbox-first-order-region');
    const cbOptimalPair = document.getElementById('checkbox-optimal-pair');
    const cbOptimalRegion = document.getElementById('checkbox-optimal-region');


    // --- P5 SETUP & DRAW ---
    p.setup = () => {
        let canvas = p.createCanvas(canvasSize, canvasSize);
        canvas.parent('canvas-bloch');
        p.pixelDensity(2); // For crisp rendering on high-DPI screens

        R = canvasSize * 0.4; // Set radius

        // Initial state
        vec_c = p.createVector(R * 0.3, R * 0.7);

        // Attach event listeners
        sliderTheta.addEventListener('input', updateAll);
        cbMetricCircle.addEventListener('change', updateAll);
        cbMetricRegion.addEventListener('change', updateAll);
        cbFirstOrderVec.addEventListener('change', updateAll);
        cbFirstOrderRegion.addEventListener('change', updateAll);
        cbOptimalPair.addEventListener('change', updateAll);
        cbOptimalRegion.addEventListener('change', updateAll);

        // Initial setup
        initPlot();
        updateAll();
    };

    p.draw = () => {
        p.background(255);

        // --- Coordinate System Transformation ---
        // Move origin to bottom-center and flip Y-axis
        p.translate(p.width / 2, p.height * 0.85);
        p.scale(1, -1);

        // --- Draw Scene ---
        drawBase();

        // Draw regions based on checkboxes
        if (showMetricRegion || showFirstOrderRegion) {
            drawDashedArc(vec_c.mag(), 0, p.PI, 'green');
            if(vec_d_opt) drawDashedArc(vec_d_opt.mag(), 0, p.PI, '#A52A2A'); // Brown
        }

        if (showOptimalRegion && showOptimalPair) {
            drawDashedArc(vec_c_opt.mag(), 0, p.PI, '#90ee90'); // Light green
            drawDashedArc(vec_d_opt_for_c_opt.mag(), 0, p.PI, '#ffcccb'); // Light red
        }

        // Draw vectors
        drawVector(vec_a, 'a', '#007BFF'); // Blue
        drawVector(vec_b, 'b', '#DC3545'); // Red

        if (showOptimalPair) {
            drawVector(vec_c_opt, 'c_opt', '#28a745', 'dashed');
            drawVector(vec_d_opt_for_c_opt, 'd_opt', '#fd7e14', 'dashed');
        }

        if (showFirstOrderVec && vec_d_opt) {
            drawErrorVector(vec_b, vec_d_opt, 'd_err', '#fd7e14'); // Orange
        }

        drawVector(vec_c, 'c', '#28a745'); // Green

        // Draw metric circle
        if (showMetricCircle) {
            drawDashedCircle(vec_a, vec_c.dist(vec_a), '#007BFF');
        }
    };

    // --- INTERACTION HANDLERS ---
    p.mousePressed = () => {
        const mouseVec = screenToWorld(p.mouseX, p.mouseY);
        if (mouseVec.dist(vec_c) < 15) {
            isDraggingC = true;
        }
    };

    p.mouseDragged = () => {
        if (isDraggingC) {
            const mouseVec = screenToWorld(p.mouseX, p.mouseY);
            // Constrain to be inside the semi-circle
            if (mouseVec.mag() <= R && mouseVec.y >= -1e-3) {
                vec_c.set(mouseVec);
                updateAll();
            }
        }
    };

    p.mouseReleased = () => {
        isDraggingC = false;
    };


    // --- UPDATE & CALCULATION LOGIC ---
    function updateAll() {
        // Read controls
        theta = parseFloat(sliderTheta.value);
        showMetricCircle = cbMetricCircle.checked;
        showMetricRegion = cbMetricRegion.checked;
        showFirstOrderVec = cbFirstOrderVec.checked;
        showFirstOrderRegion = cbFirstOrderRegion.checked;
        showOptimalPair = cbOptimalPair.checked;
        showOptimalRegion = cbOptimalRegion.checked;

        // Update UI displays
        spanTheta.textContent = (theta / p.PI).toFixed(3) + 'π';
        spanSinSqTheta.textContent = p.pow(p.sin(theta), 2).toFixed(6);

        // --- Recalculate everything ---
        // 1. Calculate a and b
        vec_a = p5.Vector.fromAngle(p.HALF_PI - theta).mult(R);
        vec_b = p5.Vector.fromAngle(p.HALF_PI + theta).mult(R);

        // 2. Calculate d_opt for the current c
        vec_d_opt = calculate_d_opt(vec_c, vec_b);

        // 3. Calculate optimal pair (c_opt, d_opt_for_c_opt)
        const D_opt = p.pow(p.sin(2 * theta) / 2, 2);
        const sin_sq_th = p.pow(p.sin(theta), 2);
        const cos_th = p.cos(theta);
        // 0.5 * (sin^2(th) + (y_c - cos(th))^2) = D_opt
        // (y_c - cos(th))^2 = 2*D_opt - sin^2(th)
        let y_c_val_sq = 2 * D_opt - sin_sq_th;
        if(y_c_val_sq < 0) y_c_val_sq = 0;
        const y_c = cos_th + p.sqrt(y_c_val_sq);

        // Scale to canvas coordinates
        vec_c_opt = p.createVector(0, y_c * R);
        // By symmetry, d_opt for c_opt is the same vector
        vec_d_opt_for_c_opt = vec_c_opt.copy();


        // 4. Update the plot
        updatePlot();
    }

    // This finds the point on the specified ellipse closest to target_vec
    function calculate_d_opt(c, target_vec) {
        const c_norm = c.mag() / R;
        if (c_norm >= 1.0) { // Unphysical region, d must be 0
            return p.createVector(0, 0);
        }

        const A = p.sqrt(1 - c_norm * c_norm); // semi-axis parallel to c
        const B = 1.0; // semi-axis perpendicular to c

        // Basis vectors
        const c_hat = c.mag() > 1e-6 ? c.copy().normalize() : p.createVector(1, 0);
        const c_hat_perp = p.createVector(-c_hat.y, c_hat.x);

        // Transform target_vec to the ellipse's frame
        const target_norm = target_vec.copy().div(R); // work with vectors of length <= 1

        const target_coords = p.createVector(
            target_norm.dot(c_hat),
            target_norm.dot(c_hat_perp)
        );

        // Check if target is already inside the ellipse
        if (p.pow(target_coords.x / A, 2) + p.pow(target_coords.y / B, 2) <= 1) {
            return target_vec.copy();
        }

        // Numerical search for the closest point on the ellipse boundary
        let min_dist_sq = Infinity;
        let best_t = 0;
        const steps = 100;
        for (let i = 0; i < steps; i++) {
            const t = p.TWO_PI * i / steps;
            const pt_on_ellipse = p.createVector(A * p.cos(t), B * p.sin(t));
            const dist_sq = pt_on_ellipse.dist(target_coords);
            if (dist_sq < min_dist_sq) {
                min_dist_sq = dist_sq;
                best_t = t;
            }
        }

        // Convert the best point back to world coordinates
        const best_pt_coords = p.createVector(A * p.cos(best_t), B * p.sin(best_t));
        const d_opt_norm = p5.Vector.add(c_hat.copy().mult(best_pt_coords.x), c_hat_perp.copy().mult(best_pt_coords.y));

        return d_opt_norm.mult(R);
    }

    // --- DRAWING HELPERS ---
    function drawBase() {
        p.stroke(0);
        p.strokeWeight(1.5);
        p.noFill();
        // Semi-circle
        p.arc(0, 0, R * 2, R * 2, p.PI, 0);
        // Axis
        p.line(-R, 0, R, 0);
    }

    function drawVector(v, label, col, style = 'solid') {
        p.push();
        p.stroke(col);
        p.fill(col);
        p.strokeWeight(2);

        if (style === 'dashed') {
            p.drawingContext.setLineDash([5, 5]);
        }

        p.line(0, 0, v.x, v.y);

        // Arrowhead
        const angle = v.heading();
        p.translate(v.x, v.y);
        p.rotate(angle);
        p.triangle(0, 0, -8, 4, -8, -4);

        // Label
        p.pop();
        p.push();
        p.noStroke();
        p.fill(col);
        p.textSize(16);
        p.scale(1, -1); // Flip text back
        const labelPos = v.copy().mult(1.15);
        p.text(label, labelPos.x, -labelPos.y);
        p.pop();
    }

    function drawErrorVector(v_start, v_end, label, col) {
        p.push();
        p.stroke(col);
        p.fill(col);
        p.strokeWeight(2);
        p.drawingContext.setLineDash([5, 5]);

        p.line(v_start.x, v_start.y, v_end.x, v_end.y);

        // Arrowhead at v_end
        const v_line = p5.Vector.sub(v_end, v_start);
        const angle = v_line.heading();
        p.translate(v_end.x, v_end.y);
        p.rotate(angle);
        p.drawingContext.setLineDash([]);
        p.triangle(0, 0, -8, 4, -8, -4);
        p.pop();
    }

    function drawDashedCircle(center, radius, col) {
        p.push();
        p.stroke(col);
        p.strokeWeight(1.5);
        p.noFill();
        p.drawingContext.setLineDash([5, 5]);
        p.ellipse(center.x, center.y, radius * 2, radius * 2);
        p.pop();
    }

    function drawDashedArc(radius, start, end, col) {
        p.push();
        p.stroke(col);
        p.strokeWeight(1.5);
        p.noFill();
        p.drawingContext.setLineDash([5, 5]);
        p.arc(0, 0, radius * 2, radius * 2, start, end);
        p.pop();
    }

    // --- COORDINATE TRANSFORMATION ---
    function screenToWorld(mx, my) {
        let mat = p.drawingContext.getTransform().inverse();
        return p.createVector(mx * mat.a + my * mat.c + mat.e, mx * mat.b + my * mat.d + mat.f);
    }

    // --- PLOTLY ---
    function initPlot() {
        const layout = {
            xaxis: { title: 'D(C, A)', range: [0, 1.1] },
            yaxis: { title: 'D(D, B)', range: [0, 1.1] },
            showlegend: false,
            margin: { l: 50, r: 20, b: 50, t: 20 },
            width: canvasSize,
            height: canvasSize,
        };

        const traces = [
            { x: [], y: [], mode: 'lines', name: 'Boundary', line: { color: '#007BFF' } },
            { x: [], y: [], mode: 'markers', name: 'Current', marker: { color: '#DC3545', size: 10 } },
            { x: [], y: [], mode: 'markers', name: 'Optimal', marker: { color: '#28a745', size: 10 }, visible: false }
        ];

        Plotly.newPlot('plot-error', traces, layout, {responsive: true});
    }

    function updatePlot() {
        // Calculate error values (should be normalized, so divide by R^2)
        const D_CA = 0.5 * vec_c.dist(vec_a) * vec_c.dist(vec_a) / (R * R);
        const D_DB = 0.5 * vec_d_opt.dist(vec_b) * vec_d_opt.dist(vec_b) / (R * R);

        // Calculate boundary curve
        const sin2th = p.sin(2 * theta);
        const x_max = p.pow(sin2th, 2);
        const boundary_x = [];
        const boundary_y = [];
        for (let i = 0; i <= 100; i++) {
            const x = x_max * i / 100;
            boundary_x.push(x);
            boundary_y.push(p.pow(sin2th - p.sqrt(x), 2));
        }

        // Calculate optimal point
        const opt_error = p.pow(sin2th / 2, 2);

        Plotly.react('plot-error', [
            { x: boundary_x, y: boundary_y, mode: 'lines', line: { color: '#007BFF' } }, // Boundary
            { x: [D_CA], y: [D_DB], mode: 'markers', marker: { color: '#DC3545', size: 10 } }, // Current error
            { x: [opt_error], y: [opt_error], mode: 'markers', marker: { color: '#28a745', size: 10 }, visible: showOptimalPair } // Optimal error
        ]);
    }

    // Assign p5 instance to global scope to allow external control if needed
    p5_instance = p;
};

// Start the p5 sketch
new p5(sketch);

</script>

</body>
</html>
