        </div>
        <div id="canvas-container">
            <!-- p5.js will create the canvas here -->
        </div>
    </div>

<script>
const sketch = (p) => {
    // --- STATE VARIABLES ---
    let numRegularPoints = 4;
    let numSteinerPoints = 2;
    let pointMode = 'steiner'; // 'regular' or 'steiner'
    let showAngles = true;

    let regularPoints = [];
    let steinerPoints = [];
    let allPoints = [];
    let mstEdges = [];
    let totalLength = 0;

    let draggedPoint = null;
    let canvasSize;

    // --- DOM ELEMENTS ---
    const textTotalLength = document.getElementById('text-total-length');
    const regularButtons = {
        3: document.getElementById('btn-regular-3'),
        4: document.getElementById('btn-regular-4'),
        5: document.getElementById('btn-regular-5'),
    };
    const steinerButtons = {
        0: document.getElementById('btn-steiner-0'),
        1: document.getElementById('btn-steiner-1'),
        2: document.getElementById('btn-steiner-2'),
        3: document.getElementById('btn-steiner-3'),
    };
    const radioRegular = document.getElementById('radio-regular');
    const radioSteiner = document.getElementById('radio-steiner');
    const checkboxShowAngles = document.getElementById('checkbox-show-angles');

    // --- P5.JS SETUP & DRAW ---
    p.setup = () => {
        const container = document.getElementById('canvas-container');
        const w = container.offsetWidth;
        const h = container.offsetHeight;
        canvasSize = Math.min(w, h) - 40;
        const canvas = p.createCanvas(canvasSize, canvasSize);
        canvas.id('main-canvas');
        canvas.parent('canvas-container');
        p.pixelDensity(2); // For crisp rendering on high-DPI screens

        setupControls();
        resetPoints();
        calculateAndRedraw();
    };

    p.draw = () => {
        p.background('#EEEEEE');

        // Draw all possible connections (light brown)
        p.strokeWeight(1);
        p.stroke(190, 160, 130, 128);
        for (let i = 0; i < allPoints.length; i++) {
            for (let j = i + 1; j < allPoints.length; j++) {
                p.line(allPoints[i].pos.x, allPoints[i].pos.y, allPoints[j].pos.x, allPoints[j].pos.y);
            }
        }

        // Draw MST edges (thick dark grey)
        p.strokeWeight(3);
        p.stroke(51, 51, 51);
        mstEdges.forEach(edge => {
            p.line(edge.p1.pos.x, edge.p1.pos.y, edge.p2.pos.x, edge.p2.pos.y);
        });

        // Draw points
        regularPoints.forEach(pt => drawPoint(pt));
        steinerPoints.forEach(pt => drawPoint(pt));

        // Draw angles if enabled
        if (showAngles) {
            drawAngles();
        }

        // Update length display
        textTotalLength.textContent = (totalLength / (canvasSize / 10)).toFixed(5);
    };

    const drawPoint = (pt) => {
        p.push();
        p.noFill();
        p.strokeWeight(2);

        if (pt.type === 'regular') {
            p.fill('#FFFFFF');
            p.stroke('#0000FF');
            p.ellipse(pt.pos.x, pt.pos.y, 16, 16);
        } else if (pt.type === 'steiner') {
            p.fill('#FFFFFF');
            p.stroke('#FF0000');
            p.ellipse(pt.pos.x, pt.pos.y, 16, 16);
            p.ellipse(pt.pos.x, pt.pos.y, 8, 8);
        }
        p.pop();
    };

    const drawAngles = () => {
        p.push();
        p.fill(0);
        p.noStroke();
        p.textSize(14);
        p.textAlign(p.CENTER, p.CENTER);

        steinerPoints.forEach(sPt => {
            const neighbors = mstEdges
                .filter(edge => edge.p1 === sPt || edge.p2 === sPt)
                .map(edge => (edge.p1 === sPt ? edge.p2 : edge.p1));

            if (neighbors.length < 2) return;

            // Calculate angles of vectors to neighbors
            const neighborAngles = neighbors.map(nPt => {
                const vec = p5.Vector.sub(nPt.pos, sPt.pos);
                return { point: nPt, angle: vec.heading() };
            });

            // Sort neighbors by angle
            neighborAngles.sort((a, b) => a.angle - b.angle);

            // Calculate and draw angles between adjacent neighbors
            for (let i = 0; i < neighborAngles.length; i++) {
                const current = neighborAngles[i];
                const next = neighborAngles[(i + 1) % neighborAngles.length];

                let angleDiff = next.angle - current.angle;
                if (angleDiff < 0) angleDiff += p.TWO_PI;

                const angleDeg = p.degrees(angleDiff);

                // Find the bisector to position the text
                const bisectorAngle = current.angle + angleDiff / 2;
                const textPos = p5.Vector.fromAngle(bisectorAngle).mult(30).add(sPt.pos);

                p.text(`${Math.round(angleDeg)}°`, textPos.x, textPos.y);
            }
        });
        p.pop();
    };

    // --- INTERACTION LOGIC ---
    p.mousePressed = () => {
        const pointsToCheck = (pointMode === 'regular') ? regularPoints : steinerPoints;
        for (const pt of pointsToCheck) {
            if (p.dist(p.mouseX, p.mouseY, pt.pos.x, pt.pos.y) < 10) {
                draggedPoint = pt;
                return;
            }
        }
    };

    p.mouseDragged = () => {
        if (draggedPoint) {
            draggedPoint.pos.x = p.constrain(p.mouseX, 0, p.width);
            draggedPoint.pos.y = p.constrain(p.mouseY, 0, p.height);
            calculateAndRedraw();
        }
    };

    p.mouseReleased = () => {
        draggedPoint = null;
    };

    // --- CONTROL HANDLERS ---
    const setupControls = () => {
        // Regular points buttons
        Object.values(regularButtons).forEach(btn => {
            btn.addEventListener('click', (e) => {
                handleRegularPointsChange(parseInt(e.target.textContent));
            });
        });

        // Steiner points buttons
        Object.values(steinerButtons).forEach(btn => {
            btn.addEventListener('click', (e) => {
                handleSteinerPointsChange(parseInt(e.target.textContent));
            });
        });

        // Point mode radio
        radioRegular.addEventListener('change', handlePointModeChange);
        radioSteiner.addEventListener('change', handlePointModeChange);

        // Show angles checkbox
        checkboxShowAngles.addEventListener('change', handleAngleToggle);

        // Set initial state for controls
        updateActiveButton(regularButtons, numRegularPoints);
        updateActiveButton(steinerButtons, numSteinerPoints);
        radioSteiner.checked = true;
        checkboxShowAngles.checked = true;
    };

    const updateActiveButton = (buttonGroup, activeKey) => {
        Object.values(buttonGroup).forEach(btn => btn.classList.remove('active'));
        if (buttonGroup[activeKey]) {
            buttonGroup[activeKey].classList.add('active');
        }
    };

    const handleRegularPointsChange = (num) => {
        if (num === numRegularPoints) return;
        numRegularPoints = num;
        updateActiveButton(regularButtons, num);
        resetPoints();
        calculateAndRedraw();
    };

    const handleSteinerPointsChange = (num) => {
        if (num === numSteinerPoints) return;
        numSteinerPoints = num;
        updateActiveButton(steinerButtons, num);

        // Adjust Steiner points array
        const center = p.createVector(p.width / 2, p.height / 2);
        while (steinerPoints.length < numSteinerPoints) {
            steinerPoints.push({
                type: 'steiner',
                pos: center.copy()
            });
        }
        while (steinerPoints.length > numSteinerPoints) {
            steinerPoints.pop();
        }
        calculateAndRedraw();
    };

    const handlePointModeChange = () => {
        pointMode = radioRegular.checked ? 'regular' : 'steiner';
    };

    const handleAngleToggle = () => {
        showAngles = checkboxShowAngles.checked;
        p.redraw();
    };

    // --- CORE CALCULATION LOGIC ---
    const resetPoints = () => {
        const center = p.createVector(p.width / 2, p.height / 2);
        const radius = p.width * 0.35;

        // Reset regular points
        regularPoints = [];
        for (let i = 0; i < numRegularPoints; i++) {
            const angle = p.map(i, 0, numRegularPoints, -p.HALF_PI, p.TWO_PI - p.HALF_PI);
            regularPoints.push({
                type: 'regular',
                pos: p.createVector(center.x + radius * p.cos(angle), center.y + radius * p.sin(angle))
            });
        }

        // Reset Steiner points to center
        steinerPoints = [];
        for (let i = 0; i < numSteinerPoints; i++) {
            steinerPoints.push({
                type: 'steiner',
                pos: center.copy()
            });
        }
    };

    const calculateAndRedraw = () => {
        calculateMST();
        p.redraw();
    };

    // Kruskal's algorithm for MST
    const calculateMST = () => {
        allPoints = [...regularPoints, ...steinerPoints];
        if (allPoints.length < 2) {
            mstEdges = [];
            totalLength = 0;
            return;
        }

        // 1. Create list of all edges
        let edges = [];
        for (let i = 0; i < allPoints.length; i++) {
            for (let j = i + 1; j < allPoints.length; j++) {
                const p1 = allPoints[i];
                const p2 = allPoints[j];
                const weight = p.dist(p1.pos.x, p1.pos.y, p2.pos.x, p2.pos.y);
                edges.push({ p1, p2, weight });
            }
        }

        // 2. Sort edges by weight
        edges.sort((a, b) => a.weight - b.weight);

        // 3. Kruskal's algorithm using Disjoint Set Union (DSU)
        mstEdges = [];
        totalLength = 0;
        let parent = Array.from(Array(allPoints.length).keys());
        const find = i => (parent[i] === i) ? i : (parent[i] = find(parent[i]));
        const union = (i, j) => {
            const rootI = find(i);
            const rootJ = find(j);
            if (rootI !== rootJ) {
                parent[rootI] = rootJ;
                return true;
            }
            return false;
        };

        for (const edge of edges) {
            const idx1 = allPoints.indexOf(edge.p1);
            const idx2 = allPoints.indexOf(edge.p2);
            if (union(idx1, idx2)) {
                mstEdges.push(edge);
                totalLength += edge.weight;
                if (mstEdges.length === allPoints.length - 1) break;
            }
        }
    };

    // Ensure canvas redraws on window resize
    p.windowResized = () => {
        const container = document.getElementById('canvas-container');
        const w = container.offsetWidth;
        const h = container.offsetHeight;
        canvasSize = Math.min(w, h) - 40;
        p.resizeCanvas(canvasSize, canvasSize);
        resetPoints();
        calculateAndRedraw();
    };
};

// Start p5.js in instance mode
new p5(sketch);

</script>
</body>
</html>
