</head>
<body>

    <div id="app-container">
        <div id="control-panel">
            <h3>Means</h3>
            <div class="control-group">
                <div class="control-row">
                    <label for="slider-mean-w">W</label>
                    <input type="range" id="slider-mean-w" min="-10" max="10" step="0.1" value="0">
                    <span id="value-mean-w">0.0</span>
                </div>
                <div class="control-row">
                    <label for="slider-mean-x">X</label>
                    <input type="range" id="slider-mean-x" min="-10" max="10" step="0.1" value="0">
                    <span id="value-mean-x">0.0</span>
                </div>
                <div class="control-row">
                    <label for="slider-mean-y">Y</label>
                    <input type="range" id="slider-mean-y" min="-10" max="10" step="0.1" value="0">
                    <span id="value-mean-y">0.0</span>
                </div>
                <div class="control-row">
                    <label for="slider-mean-z">Z</label>
                    <input type="range" id="slider-mean-z" min="-10" max="10" step="0.1" value="0">
                    <span id="value-mean-z">0.0</span>
                </div>
            </div>

            <h3>Standard Deviations</h3>
            <div class="control-group">
                <div class="control-row">
                    <label for="slider-sd-w">W</label>
                    <input type="range" id="slider-sd-w" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-w">1.0</span>
                </div>
                <div class="control-row">
                    <label for="slider-sd-x">X</label>
                    <input type="range" id="slider-sd-x" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-x">1.0</span>
                </div>
                <div class="control-row">
                    <label for="slider-sd-y">Y</label>
                    <input type="range" id="slider-sd-y" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-y">1.0</span>
                </div>
                <div class="control-row">
                    <label for="slider-sd-z">Z</label>
                    <input type="range" id="slider-sd-z" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-z">1.0</span>
                </div>
            </div>
        </div>
        <div id="visualization-panel">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>

    <script>
        // --- DOM Elements ---
        const sliders = {
            mean: {
                w: document.getElementById('slider-mean-w'),
                x: document.getElementById('slider-mean-x'),
                y: document.getElementById('slider-mean-y'),
                z: document.getElementById('slider-mean-z'),
            },
            sd: {
                w: document.getElementById('slider-sd-w'),
                x: document.getElementById('slider-sd-x'),
                y: document.getElementById('slider-sd-y'),
                z: document.getElementById('slider-sd-z'),
            }
        };

        const values = {
            mean: {
                w: document.getElementById('value-mean-w'),
                x: document.getElementById('value-mean-x'),
                y: document.getElementById('value-mean-y'),
                z: document.getElementById('value-mean-z'),
            },
            sd: {
                w: document.getElementById('value-sd-w'),
                x: document.getElementById('value-sd-x'),
                y: document.getElementById('value-sd-y'),
                z: document.getElementById('value-sd-z'),
            }
        };

        // --- ANOVA Constants ---
        const k = 4; // number of groups
        const n = 10; // subjects per group
        const groups = ['w', 'x', 'y', 'z'];

        // --- State Variables ---
        let currentMeans = {};
        let currentSDs = {};
        let anovaResults = {};

        // --- P5.js Sketch ---
        const sketch = (p) => {
            // --- Visualization Constants ---
            const colors = {
                groupBar: '#F57F29',
                msBetween: '#4691A5',
                msWithin: '#D49D3C',
                fRatio: '#8C3A8D',
                axis: '#333333',
                text: '#000000',
            };

            p.setup = () => {
                const visPanel = document.getElementById('visualization-panel');
                const canvas = p.createCanvas(visPanel.clientWidth, visPanel.clientHeight);
                canvas.parent(visPanel);
                p.noLoop(); // We will manually redraw on input

                // Add event listeners to all sliders
                for (const group of groups) {
                    sliders.mean[group].addEventListener('input', updateAndRedraw);
                    sliders.sd[group].addEventListener('input', updateAndRedraw);
                }

                // Initial calculation and draw
                updateAndRedraw();
            };

            p.windowResized = () => {
                const visPanel = document.getElementById('visualization-panel');
                p.resizeCanvas(visPanel.clientWidth, visPanel.clientHeight);
                p.redraw();
            };

            function updateAndRedraw() {
                // 1. Read Inputs & Update UI Text
                for (const group of groups) {
                    currentMeans[group] = parseFloat(sliders.mean[group].value);
                    currentSDs[group] = parseFloat(sliders.sd[group].value);
                    values.mean[group].textContent = currentMeans[group].toFixed(1);
                    values.sd[group].textContent = currentSDs[group].toFixed(1);
                }

                // 2. Perform ANOVA Calculation
                const meanValues = Object.values(currentMeans);
                const grandMean = meanValues.reduce((sum, val) => sum + val, 0) / k;

                const ssBetween = n * meanValues.reduce((sum, mean) => sum + Math.pow(mean - grandMean, 2), 0);
                const msBetween = ssBetween / (k - 1);

                const sdValues = Object.values(currentSDs);
                const ssWithin = (n - 1) * sdValues.reduce((sum, sd) => sum + Math.pow(sd, 2), 0);
                const msWithin = ssWithin / (k * n - k);

                const fRatio = (msWithin > 0) ? (msBetween / msWithin) : Infinity;

                anovaResults = { grandMean, msBetween, msWithin, fRatio };

                // 3. Trigger Redraw
                p.redraw();
            }

            p.draw = () => {
                p.background(255);
                drawGroupDataPlot();
                drawAnovaResultsPlot();
            };

            function drawGroupDataPlot() {
                const topMargin = 60;
                const bottomMargin = 40;
                const leftMargin = 50;
                const rightMargin = 20;

                const plotHeight = p.height / 2 - topMargin - bottomMargin;
                const plotWidth = p.width - leftMargin - rightMargin;

                p.push();
                p.translate(leftMargin, topMargin);

                // Y-Axis
                const yMin = -10, yMax = 10;
                p.stroke(colors.axis);
                p.strokeWeight(1);
                p.line(0, 0, 0, plotHeight);

                p.noStroke();
                p.fill(colors.text);
                p.textAlign(p.RIGHT, p.CENTER);
                p.textSize(12);
                for (let v = yMin; v <= yMax; v += 5) {
                    const y = p.map(v, yMin, yMax, plotHeight, 0);
                    p.text(v, -10, y);
                    p.stroke('#eee');
                    p.line(0, y, plotWidth, y);
                    p.noStroke();
                }

                // F-Ratio Text
                p.fill(colors.text);
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(18);
                let fRatioDisplay = anovaResults.fRatio.toFixed(2);
                if (anovaResults.fRatio < 0.01 && anovaResults.fRatio > 0) {
                    fRatioDisplay = anovaResults.fRatio.toExponential(2);
                }
                p.text(`F ratio = ${fRatioDisplay}`, plotWidth / 2, -40);

                // Grand Mean Line
                const grandMeanY = p.map(anovaResults.grandMean, yMin, yMax, plotHeight, 0);
                p.stroke(colors.axis);
                p.strokeWeight(1.5);
                drawDashedLine(0, grandMeanY, plotWidth, grandMeanY, 5);

                // Group Bars
                const numGroups = groups.length;
                const barWidth = plotWidth / (numGroups * 2);

                groups.forEach((group, i) => {
                    const mean = currentMeans[group];
                    const sd = currentSDs[group];

                    const centerX = p.map(i, -0.5, numGroups - 0.5, 0, plotWidth);

                    // Draw SD rectangle
                    const yTop = p.map(p.constrain(mean + sd, yMin, yMax), yMin, yMax, plotHeight, 0);
                    const yBottom = p.map(p.constrain(mean - sd, yMin, yMax), yMin, yMax, plotHeight, 0);

                    p.fill(colors.groupBar);
                    p.noStroke();
                    p.rect(centerX - barWidth / 2, yTop, barWidth, yBottom - yTop);

                    // Draw Mean line
                    const yMean = p.map(p.constrain(mean, yMin, yMax), yMin, yMax, plotHeight, 0);
                    p.stroke(colors.axis);
                    p.strokeWeight(2);
                    p.line(centerX - barWidth / 2, yMean, centerX + barWidth / 2, yMean);

                    // X-Axis labels
                    p.noStroke();
                    p.fill(colors.text);
                    p.textAlign(p.CENTER, p.TOP);
                    p.textSize(14);
                    p.text(group.toUpperCase(), centerX, plotHeight + 10);
                });

                p.pop();
            }

            function drawAnovaResultsPlot() {
                const topMargin = 40;
                const bottomMargin = 40;
                const leftMargin = 120; // More space for labels
                const rightMargin = 40;

                const plotAreaY = p.height / 2;
                const plotHeight = p.height / 2 - topMargin - bottomMargin;
                const plotWidth = p.width - leftMargin - rightMargin;

                p.push();
                p.translate(leftMargin, plotAreaY + topMargin);

                // Title
                p.noStroke();
                p.fill(colors.text);
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(16);
                p.text("Analysis of the F-Ratio", plotWidth / 2, -25);

                // X-Axis
                const xMax = 200;
                p.stroke(colors.axis);
                p.strokeWeight(1);
                p.line(0, plotHeight, plotWidth, plotHeight);

                p.noStroke();
                p.fill(colors.text);
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(12);
                for (let v = 0; v <= xMax; v += 50) {
                    const x = p.map(v, 0, xMax, 0, plotWidth);
                    p.text(v, x, plotHeight + 5);
                }

                // Bars
                const barHeight = plotHeight / 5;
                const barData = [
                    { label: "MS Between", value: anovaResults.msBetween, color: colors.msBetween },
                    { label: "MS Within", value: anovaResults.msWithin, color: colors.msWithin },
                    { label: "F-Ratio", value: anovaResults.fRatio, color: colors.fRatio }
                ];

                barData.forEach((item, i) => {
                    const barY = p.map(i, 0, barData.length, 0, plotHeight - barHeight);
                    const barWidth = p.map(p.constrain(item.value, 0, xMax), 0, xMax, 0, plotWidth);

                    // Draw bar
                    p.fill(item.color);
                    p.noStroke();
                    p.rect(0, barY, barWidth, barHeight);

                    // Draw label
                    p.fill(colors.text);
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.textSize(14);
                    p.text(item.label, -15, barY + barHeight / 2);
                });

                p.pop();
            }

            function drawDashedLine(x1, y1, x2, y2, dashLength) {
                p.drawingContext.setLineDash([dashLength, dashLength]);
                p.line(x1, y1, x2, y2);
                p.drawingContext.setLineDash([]); // Reset to solid
            }
        };

        new p5(sketch);

    </script>
</body>
