    <script>
        window.onload = function() {
            // --- Get DOM elements ---
            const sliders = {
                mu1: document.getElementById('slider-mu1'),
                sigma1: document.getElementById('slider-sigma1'),
                xi1: document.getElementById('slider-xi1'),
                mu2: document.getElementById('slider-mu2'),
                sigma2: document.getElementById('slider-sigma2'),
                xi2: document.getElementById('slider-xi2'),
                pmax: document.getElementById('slider-pmax')
            };

            const displays = {
                mu1: document.getElementById('display-mu1'),
                sigma1: document.getElementById('display-sigma1'),
                xi1: document.getElementById('display-xi1'),
                mu2: document.getElementById('display-mu2'),
                sigma2: document.getElementById('display-sigma2'),
                xi2: document.getElementById('display-xi2'),
                pmax: document.getElementById('display-pmax')
            };

            const plotPDFDiv = 'plot-pdf';
            const plotReturnLevelDiv = 'plot-return-level';

            // --- GEV Math Functions ---
            function gevPDF(x, mu, sigma, xi) {
                if (sigma <= 0) return 0;
                let t;
                if (Math.abs(xi) < 1e-6) { // Gumbel case (xi -> 0)
                    const z = (x - mu) / sigma;
                    t = Math.exp(-z);
                } else { // Frechet or Weibull case
                    const z = (x - mu) / sigma;
                    const arg = 1 + xi * z;
                    if (arg <= 0) return 0;
                    t = Math.pow(arg, -1 / xi);
                }
                return (1 / sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
            }

            function gevReturnLevel(p, mu, sigma, xi) {
                if (sigma <= 0 || p <= 1) return NaN;
                const prob = 1 - 1 / p;
                if (Math.abs(xi) < 1e-6) { // Gumbel case (xi -> 0)
                    return mu - sigma * Math.log(-Math.log(prob));
                } else { // Frechet or Weibull case
                    return mu - (sigma / xi) * (1 - Math.pow(-Math.log(prob), -xi));
                }
            }

            // --- Initial Plot Setup ---
            const pdfLayout = {
                title: 'PDFs of GEV Distributions 1 and 2',
                xaxis: { range: [2.5, 7] }, // Fixed range, no label
                yaxis: { autorange: true }, // Dynamic range, no label
                margin: { t: 50, l: 40, b: 40, r: 20 },
                legend: { x: 0.05, y: 0.95 }
            };
            Plotly.newPlot(plotPDFDiv, [], pdfLayout, { responsive: true });

            const returnLayout = {
                xaxis: { title: 'Return Period (years)' },
                yaxis: { title: 'Maximum Sea Level (meters)', autorange: true },
                margin: { t: 20, l: 60, b: 50, r: 20 },
                showlegend: false
            };
            Plotly.newPlot(plotReturnLevelDiv, [], returnLayout, { responsive: true });

            // --- Main Update Function ---
            function update() {
                // 1. Read current values from sliders
                const params = {
                    mu1: parseFloat(sliders.mu1.value),
                    sigma1: parseFloat(sliders.sigma1.value),
                    xi1: parseFloat(sliders.xi1.value),
                    mu2: parseFloat(sliders.mu2.value),
                    sigma2: parseFloat(sliders.sigma2.value),
                    xi2: parseFloat(sliders.xi2.value),
                    pmax: parseInt(sliders.pmax.value)
                };

                // 2. Update display readouts
                displays.mu1.value = params.mu1.toFixed(2);
                displays.sigma1.value = params.sigma1.toFixed(3);
                displays.xi1.value = params.xi1.toFixed(2);
                displays.mu2.value = params.mu2.toFixed(2);
                displays.sigma2.value = params.sigma2.toFixed(3);
                displays.xi2.value = params.xi2.toFixed(2);
                displays.pmax.value = params.pmax;

                // 3. Recalculate PDF data
                const x_pdf = [];
                for (let i = 2.5; i <= 7; i += 0.01) {
                    x_pdf.push(i);
                }
                const y_pdf1 = x_pdf.map(x => gevPDF(x, params.mu1, params.sigma1, params.xi1));
                const y_pdf2 = x_pdf.map(x => gevPDF(x, params.mu2, params.sigma2, params.xi2));

                // 4. Recalculate Return Level data
                const x_ret = [];
                const step = params.pmax / 500; // Create 500 points for a smooth curve
                for (let p = 1.01; p <= params.pmax; p += step) {
                    x_ret.push(p);
                }
                if (x_ret[x_ret.length - 1] < params.pmax) x_ret.push(params.pmax);

                const y_ret1 = x_ret.map(p => gevReturnLevel(p, params.mu1, params.sigma1, params.xi1));
                const y_ret2 = x_ret.map(p => gevReturnLevel(p, params.mu2, params.sigma2, params.xi2));

                // 5. Update plots using Plotly.react
                Plotly.react(plotPDFDiv, [
                    { x: x_pdf, y: y_pdf1, type: 'scatter', mode: 'lines', name: 'GEV 1', line: { color: 'blue' }, fill: 'tozeroy', fillcolor: 'rgba(0, 0, 255, 0.2)' },
                    { x: x_pdf, y: y_pdf2, type: 'scatter', mode: 'lines', name: 'GEV 2', line: { color: 'red' }, fill: 'tozeroy', fillcolor: 'rgba(255, 0, 0, 0.2)' }
                ], pdfLayout);

                Plotly.react(plotReturnLevelDiv, [
                    { x: x_ret, y: y_ret1, type: 'scatter', mode: 'lines', name: 'GEV 1', line: { color: 'blue' } },
                    { x: x_ret, y: y_ret2, type: 'scatter', mode: 'lines', name: 'GEV 2', line: { color: 'red' } }
                ], {
                    ...returnLayout,
                    xaxis: { ...returnLayout.xaxis, range: [0, params.pmax] }
                });
            }

            // --- Attach Event Listeners ---
            for (const key in sliders) {
                sliders[key].addEventListener('input', update);
            }

            // --- Initial call to draw plots on page load ---
            update();
        };
    </script>

</body>
</html>
