<script>
document.addEventListener('DOMContentLoaded', function () {
    // --- DOM ELEMENT REFERENCES ---
    const plotTopDiv = document.getElementById('plot-top');
    const plotBottomDiv = document.getElementById('plot-bottom');

    const signalButtons = {
        rampdown: document.getElementById('btn-rampdown'),
        tophat: document.getElementById('btn-tophat'),
        stairstep: document.getElementById('btn-stairstep'),
        triangle: document.getElementById('btn-triangle'),
        uh: document.getElementById('btn-uh'),
    };

    const sliders = {
        a: document.getElementById('slider-a'),
        b: document.getElementById('slider-b'),
        t: document.getElementById('slider-t'),
    };

    const sliderValues = {
        a: document.getElementById('slider-a-value'),
        b: document.getElementById('slider-b-value'),
        t: document.getElementById('slider-t-value'),
    };

    const checkboxes = {
        x: document.getElementById('check-x'),
        y: document.getElementById('check-y'),
        z: document.getElementById('check-z'),
        w: document.getElementById('check-w'),
    };

    // --- STATE VARIABLES ---
    let activeSignal = 'triangle';
    let a = 2.0;
    let b = 4.0;
    let T = 2.0;

    // --- SIGNAL DEFINITIONS (Vertices of Polylines) ---
    const signalPoints = {
        rampdown:  [ [[0,0], [0,4], [6,0], [6,0]] ],
        tophat:    [ [[-10,0], [1,0], [1,4], [5,4], [5,0], [20,0]] ],
        stairstep: [ [[-10,0], [1,0], [1,1], [2,1], [2,4], [4,4], [4,1], [5,1], [5,0], [20,0]] ],
        triangle:  [ [[-10,0], [0,0], [3,4], [6,0], [20,0]] ],
        uh: [
            [[-10,0], [1,0], [1,4], [1.5,4], [1.5,1], [2.5,1], [2.5,4], [3,4], [3,0], [20,0]],
            [[-10,0], [4,0], [4,4], [4.5,4], [4.5,2], [5.5,2], [5.5,4], [6,4], [6,0], [20,0]]
        ]
    };

    // --- PLOT LAYOUTS ---
    const commonLayout = {
        xaxis: { title: 'time (s)', range: [-10, 20], zeroline: true, zerolinewidth: 2 },
        yaxis: { range: [-0.5, 4.5], zeroline: true, zerolinewidth: 2 },
        margin: { t: 50, r: 20, b: 40, l: 40 },
        showlegend: true,
    };

    const topLayout = {
        ...commonLayout,
        title: 'Original Signal',
        legend: { x: 0.01, y: 0.98, bgcolor: 'rgba(255,255,255,0.6)' },
    };

    const bottomLayout = {
        ...commonLayout,
        title: 'Transformed Signals',
        legend: { x: 0.01, y: 0.98, bgcolor: 'rgba(255,255,255,0.6)' },
    };

    // --- HELPER FUNCTIONS ---

    /**
     * Transforms a set of points for a given transformation type.
     * @param {Array} points - Array of [t, val] points.
     * @param {string} type - 'x', 'y', 'z', or 'w'.
     * @param {number} a - Scaling factor.
     * @param {number} b - Shift factor.
     * @returns {{x: Array, y: Array}} - Plotly-compatible x and y arrays.
     */
    function transformPoints(points, type, a, b) {
        const x_coords = [];
        const y_coords = [];
        points.forEach(([t_orig, y_orig]) => {
            let t_new;
            switch (type) {
                case 'y': t_new = (t_orig + b) / a; break; // y(t) = x(at - b)
                case 'z': t_new = t_orig + b; break;       // z(t) = x(t - b)
                case 'w': t_new = t_orig / a; break;       // w(t) = x(at)
                case 'x': default: t_new = t_orig; break;  // x(t)
            }
            x_coords.push(t_new);
            y_coords.push(y_orig);
        });
        return { x: x_coords, y: y_coords };
    }

    /**
     * Generates all traces for both plots based on the current state.
     */
    function updatePlots() {
        // --- DATA GENERATION ---
        const baseSignalTraces = signalPoints[activeSignal];

        // --- TOP PLOT (x(t) only) ---
        const topPlotData = baseSignalTraces.map((points, index) => {
            const {x, y} = transformPoints(points, 'x', a, b);
            return {
                x: x,
                y: y,
                mode: 'lines',
                line: { color: 'rgb(70, 130, 180)', width: 2.5 },
                name: '\\(x(t)\\)',
                showlegend: index === 0, // Only show legend for the first part of a multi-part signal
            };
        });

        // --- BOTTOM PLOT (all transformations) ---
        const traceConfigs = [
            { type: 'x', color: 'rgb(70, 130, 180)', name: '\\(x(t)\\)', visible: checkboxes.x.checked },
            { type: 'y', color: 'red', name: '\\(y(t) = x(at-b)\\)', visible: checkboxes.y.checked },
            { type: 'z', color: 'olivedrab', name: '\\(z(t) = x(t-b)\\)', visible: checkboxes.z.checked },
            { type: 'w', color: 'saddlebrown', name: '\\(w(t) = x(at)\\)', visible: checkboxes.w.checked },
        ];

        const bottomPlotData = [];
        traceConfigs.forEach(config => {
            baseSignalTraces.forEach((points, index) => {
                const {x, y} = transformPoints(points, config.type, a, b);
                bottomPlotData.push({
                    x: x,
                    y: y,
                    mode: 'lines',
                    line: { color: config.color, width: 2.5 },
                    name: config.name,
                    visible: config.visible,
                    showlegend: index === 0,
                });
            });
        });

        // --- DYNAMIC LAYOUT (Annotations and Shapes for b and T) ---
        const dynamicLayoutAdditions = {
            shapes: [
                { type: 'circle', xref: 'x', yref: 'y', x0: T - 0.2, y0: -0.15, x1: T + 0.2, y1: 0.15, fillcolor: 'red', line: { color: 'red' } },
                { type: 'circle', xref: 'x', yref: 'y', x0: b - 0.2, y0: -0.15, x1: b + 0.2, y1: 0.15, fillcolor: 'green', line: { color: 'green' } },
            ],
            annotations: [
                { x: T, y: 0.4, xref: 'x', yref: 'y', text: 'T', showarrow: false, font: { color: 'red', size: 16 } },
                { x: b, y: 0.4, xref: 'x', yref: 'y', text: 'b', showarrow: false, font: { color: 'green', size: 16 } },
            ]
        };

        Plotly.react(plotTopDiv, topPlotData, { ...topLayout, ...dynamicLayoutAdditions });
        Plotly.react(plotBottomDiv, bottomPlotData, { ...bottomLayout, ...dynamicLayoutAdditions });
    }

    // --- EVENT LISTENERS ---

    // Signal Selection Buttons
    Object.keys(signalButtons).forEach(key => {
        signalButtons[key].addEventListener('click', () => {
            Object.values(signalButtons).forEach(btn => btn.classList.remove('active'));
            signalButtons[key].classList.add('active');
            activeSignal = key;
            updatePlots();
        });
    });

    // Slider 'a'
    sliders.a.addEventListener('input', () => {
        a = parseFloat(sliders.a.value);
        T = b / a;
        sliderValues.a.textContent = a.toFixed(1);
        sliders.t.value = T;
        sliderValues.t.textContent = T.toFixed(1);
        updatePlots();
    });

    // Slider 'b'
    sliders.b.addEventListener('input', () => {
        b = parseFloat(sliders.b.value);
        T = b / a;
        sliderValues.b.textContent = b.toFixed(1);
        sliders.t.value = T;
        sliderValues.t.textContent = T.toFixed(1);
        updatePlots();
    });

    // Slider 'T'
    sliders.t.addEventListener('input', () => {
        T = parseFloat(sliders.t.value);
        b = a * T;
        sliderValues.t.textContent = T.toFixed(1);
        sliders.b.value = b;
        sliderValues.b.textContent = b.toFixed(1);
        updatePlots();
    });

    // Checkboxes
    Object.keys(checkboxes).forEach((key, i) => {
        checkboxes[key].addEventListener('change', (e) => {
            const isVisible = e.target.checked;
            const traceIndices = [];
            // Each signal type can have multiple traces (e.g., for UH signal)
            const numTracesPerSignal = signalPoints[activeSignal].length;
            for (let j = 0; j < numTracesPerSignal; j++) {
                traceIndices.push(i * numTracesPerSignal + j);
            }
            Plotly.restyle(plotBottomDiv, { visible: isVisible }, traceIndices);
        });
    });

    // --- INITIALIZATION ---
    function init() {
        // Set initial active button
        signalButtons[activeSignal].classList.add('active');

        // Initial call to render plots
        updatePlots();

        // Ensure MathJax typesets all formulas
        MathJax.typesetPromise();
    }

    init();
});
</script>

</body>
</html>
