    <div id="main-container">
        <h1>Time-Encoding Signal Visualization</h1>

        <button id="info-button">+</button>
        <div id="description-panel" style="display: none;">
            <p>
                This interactive demo visualizes two different time-encoding methods for analog signals: <strong>Asynchronous Sigma-Delta Modulation (ASDM)</strong> and <strong>Integrate-and-Fire (IAF)</strong>.
                Use the controls to change the input signal function, adjust the time window (start time and duration), and select the encoding algorithm to see how they affect the resulting encoded output. The analog signal is shown in blue, and the encoded signal is shown in red.
            </p>
        </div>

        <div id="control-panel">
            <label for="slider-start-time">start time</label>
            <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
            <span id="start-time-value" class="value-display">-1.0</span>

            <label for="slider-total-time">total time</label>
            <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
            <span id="total-time-value" class="value-display">2.2</span>

            <label>function</label>
            <div id="function-buttons" class="button-group">
                <button id="btn-f">f</button>
                <button id="btn-g" class="active">g</button>
                <button id="btn-sin">sin</button>
                <button id="btn-cos">cos</button>
                <button id="btn-tan">tan</button>
            </div>

            <label>encoding method</label>
            <div id="encoding-buttons" class="button-group">
                <button id="btn-asdm" class="active">ASDM</button>
                <button id="btn-iaf">IAF</button>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {

            // --- STATE MANAGEMENT ---
            let state = {
                startTime: -1.0,
                totalTime: 2.2,
                selectedFunction: 'g',
                selectedEncoding: 'asdm'
            };

            // --- DOM ELEMENT REFERENCES ---
            const infoButton = document.getElementById('info-button');
            const descriptionPanel = document.getElementById('description-panel');

            const sliderStartTime = document.getElementById('slider-start-time');
            const sliderTotalTime = document.getElementById('slider-total-time');
            const startTimeValueDisplay = document.getElementById('start-time-value');
            const totalTimeValueDisplay = document.getElementById('total-time-value');

            const functionButtons = document.getElementById('function-buttons');
            const encodingButtons = document.getElementById('encoding-buttons');

            let p5Instance; // To hold the p5 sketch instance

            // --- EVENT LISTENERS ---
            infoButton.addEventListener('click', () => {
                const isHidden = descriptionPanel.style.display === 'none';
                descriptionPanel.style.display = isHidden ? 'block' : 'none';
                infoButton.textContent = isHidden ? '−' : '+';
            });

            sliderStartTime.addEventListener('input', (e) => {
                state.startTime = parseFloat(e.target.value);
                startTimeValueDisplay.textContent = state.startTime.toFixed(1);
                p5Instance.redraw();
            });

            sliderTotalTime.addEventListener('input', (e) => {
                state.totalTime = parseFloat(e.target.value);
                totalTimeValueDisplay.textContent = state.totalTime.toFixed(1);
                p5Instance.redraw();
            });

            functionButtons.addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    handleButtonGroupClick(functionButtons, e.target);
                    state.selectedFunction = e.target.id.replace('btn-', '');
                    p5Instance.redraw();
                }
            });

            encodingButtons.addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    handleButtonGroupClick(encodingButtons, e.target);
                    state.selectedEncoding = e.target.id.replace('btn-', '');
                    p5Instance.redraw();
                }
            });

            function handleButtonGroupClick(group, clickedButton) {
                Array.from(group.children).forEach(btn => btn.classList.remove('active'));
                clickedButton.classList.add('active');
            }


            // --- P5.JS SKETCH ---
            const sketch = (p) => {
                const margin = { top: 40, right: 40, bottom: 40, left: 40 };
                let canvasWidth, canvasHeight;
                let graphWidth, graphHeight;
                const yRange = [-1.2, 1.2];

                // --- ANALOG FUNCTIONS ---
                const analogFunctions = {
                    f: (t) => 0.5 * t,
                    g: (t) => p.sin(2 * p.PI * t) + 0.3 * p.cos(3 * 2 * p.PI * t),
                    sin: (t) => p.sin(2 * p.PI * t),
                    cos: (t) => p.cos(2 * p.PI * t),
                    tan: (t) => p.tan(p.PI * t)
                };

                p.setup = () => {
                    const container = document.getElementById('canvas-container');
                    canvasWidth = container.offsetWidth;
                    canvasHeight = container.offsetHeight;
                    graphWidth = canvasWidth - margin.left - margin.right;
                    graphHeight = canvasHeight - margin.top - margin.bottom;

                    p.createCanvas(canvasWidth, canvasHeight);
                    p.noLoop();
                };

                p.windowResized = () => {
                    const container = document.getElementById('canvas-container');
                    canvasWidth = container.offsetWidth;
                    canvasHeight = container.offsetHeight;
                    graphWidth = canvasWidth - margin.left - margin.right;
                    graphHeight = canvasHeight - margin.top - margin.bottom;
                    p.resizeCanvas(canvasWidth, canvasHeight);
                    p.redraw();
                }

                p.draw = () => {
                    p.background(varNameToValue('--canvas-bg-color'));
                    p.push();
                    p.translate(margin.left, margin.top);

                    drawAxes();
                    drawAnalogSignal();
                    drawEncodedSignal();

                    p.pop();
                };

                // --- MAPPING FUNCTIONS ---
                const mapX = (t) => p.map(t, state.startTime, state.startTime + state.totalTime, 0, graphWidth);
                const mapY = (y) => p.map(y, yRange[0], yRange[1], graphHeight, 0);

                // --- DRAWING HELPERS ---
                function drawAxes() {
                    p.stroke(varNameToValue('--border-color'));
                    p.strokeWeight(1);
                    p.noFill();

                    // Axes lines
                    p.line(0, mapY(0), graphWidth, mapY(0)); // X-axis
                    p.line(mapX(0), 0, mapX(0), graphHeight); // Y-axis

                    // Ticks and labels
                    p.textAlign(p.CENTER, p.TOP);
                    p.fill(varNameToValue('--text-color'));
                    p.noStroke();
                    for (let t = Math.ceil(state.startTime / 0.5) * 0.5; t <= state.startTime + state.totalTime; t += 0.5) {
                        const x = mapX(t);
                        if (x >= 0 && x <= graphWidth) {
                            p.text(t.toFixed(1), x, mapY(0) + 5);
                            p.stroke(varNameToValue('--border-color'));
                            p.line(x, mapY(0) - 3, x, mapY(0) + 3);
                            p.noStroke();
                        }
                    }

                    p.textAlign(p.RIGHT, p.CENTER);
                    for (let y = -1.0; y <= 1.0; y += 0.5) {
                        const yPos = mapY(y);
                        if (yPos >=0 && yPos <= graphHeight){
                           p.text(y.toFixed(1), mapX(0) - 5, yPos);
                           p.stroke(varNameToValue('--border-color'));
                           p.line(mapX(0) - 3, yPos, mapX(0) + 3, yPos);
                           p.noStroke();
                        }
                    }
                }

                function drawAnalogSignal() {
                    const func = analogFunctions[state.selectedFunction];
                    p.stroke(varNameToValue('--blue-signal'));
                    p.strokeWeight(2);
                    p.noFill();

                    p.beginShape();
                    let prevY = null;
                    const numPoints = graphWidth * 2;
                    for (let i = 0; i <= numPoints; i++) {
                        const t = state.startTime + (i / numPoints) * state.totalTime;
                        const y = func(t);

                        // Handle tan() discontinuities
                        if (prevY !== null && state.selectedFunction === 'tan' && Math.abs(y - prevY) > 5) {
                            p.endShape();
                            p.beginShape();
                        } else if (y >= yRange[0] && y <= yRange[1]) {
                           p.vertex(mapX(t), mapY(y));
                        }
                        prevY = y;
                    }
                    p.endShape();
                }

                function drawEncodedSignal() {
                    if (state.selectedEncoding === 'asdm') {
                        drawASDM();
                    } else if (state.selectedEncoding === 'iaf') {
                        drawIAF();
                    }
                }

                function drawASDM() {
                    // Simulation
                    const B = 0.25;
                    const delta = 0.05;
                    const dt = state.totalTime / (graphWidth * 4); // Small time step for simulation
                    const func = analogFunctions[state.selectedFunction];

                    let integral = 0;
                    let outputLevel = B;
                    const flipTimes = [];

                    for (let t = state.startTime; t < state.startTime + state.totalTime; t += dt) {
                        integral += (func(t) - outputLevel) * dt;
                        if ((outputLevel === B && integral < -delta) || (outputLevel === -B && integral > delta)) {
                            flipTimes.push(t);
                            outputLevel *= -1;
                            integral = 0;
                        }
                    }

                    // Drawing
                    p.stroke(varNameToValue('--red-signal'));
                    p.strokeWeight(1.5);
                    p.noFill();

                    let lastT = state.startTime;
                    let lastLevel = B;
                    flipTimes.forEach(flipTime => {
                        p.line(mapX(lastT), mapY(lastLevel), mapX(flipTime), mapY(lastLevel)); // Horizontal line
                        p.line(mapX(flipTime), mapY(lastLevel), mapX(flipTime), mapY(-lastLevel)); // Vertical line
                        lastT = flipTime;
                        lastLevel *= -1;
                    });
                    // Draw the final segment
                    p.line(mapX(lastT), mapY(lastLevel), mapX(state.startTime + state.totalTime), mapY(lastLevel));
                }

                function drawIAF() {
                    // Simulation
                    const theta = 0.05;
                    const y_spike = 0.3;
                    const dt = state.totalTime / (graphWidth * 4);
                    const func = analogFunctions[state.selectedFunction];

                    let integral = 0;
                    const spikeTimes = [];

                    for (let t = state.startTime; t < state.startTime + state.totalTime; t += dt) {
                        integral += func(t) * dt;
                        if (integral >= theta) {
                            spikeTimes.push(t);
                            integral = 0;
                        }
                    }

                    // Drawing
                    p.stroke(varNameToValue('--red-signal'));
                    p.strokeWeight(1);

                    spikeTimes.forEach(t_spike => {
                        const x = mapX(t_spike);
                        p.line(x, mapY(0), x, mapY(y_spike)); // Vertical line
                        p.fill(varNameToValue('--red-signal'));
                        p.noStroke();
                        p.ellipse(x, mapY(y_spike), 6, 6); // Dot on top
                        p.stroke(varNameToValue('--red-signal'));
                    });
                }

                // Helper to get CSS var values
                function varNameToValue(name) {
                    return getComputedStyle(document.documentElement).getPropertyValue(name).trim();
                }

            };

            // --- INITIALIZE P5.JS ---
            p5Instance = new p5(sketch, 'canvas-container');

        });
    </script>
</body>
</html>
