    <script type="importmap">
        {
            "imports": {
                "three": "https://cdn.jsdelivr.net/npm/three@0.164.1/build/three.module.js",
                "three/addons/": "https://cdn.jsdelivr.net/npm/three@0.164.1/examples/jsm/"
            }
        }
    </script>
    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // --- STATE ---
        const state = {
            time: 0,
            isPlaying: false,
            plotPoints: 40,
            pWave: {
                type: 'sine',
                amplitude: 0.1,
                wavelength: 2.0
            },
            sWave: {
                type: 'sine',
                amplitude: 0.1,
                wavelength: 2.5,
                direction: 'xy'
            },
            viewpoint: 'default'
        };

        // --- THREE.JS SETUP ---
        let scene, renderer, perspectiveCamera, orthographicCamera, activeCamera;
        let mesh, wireframe, controls;
        let originalPositions;

        const container = document.getElementById('visualization-container');

        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xE0E0E0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            container.appendChild(renderer.domElement);

            // Cameras
            const aspect = container.clientWidth / container.clientHeight;
            perspectiveCamera = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
            perspectiveCamera.position.set(10, 10, 10);
            perspectiveCamera.lookAt(0, 0, 0);

            const frustumSize = 12;
            orthographicCamera = new THREE.OrthographicCamera(frustumSize * aspect / -2, frustumSize * aspect / 2, frustumSize / 2, frustumSize / -2, 0.1, 100);
            orthographicCamera.position.set(0, 0, 10);
            orthographicCamera.lookAt(0, 0, 0);

            activeCamera = perspectiveCamera;

            // Lights
            scene.add(new THREE.AmbientLight(0xcccccc, 1.0));
            const dirLight = new THREE.DirectionalLight(0xffffff, 1.5);
            dirLight.position.set(5, 10, 7.5);
            scene.add(dirLight);

            // Controls
            controls = new OrbitControls(perspectiveCamera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Initial Grid
            createGrid();

            // Event Listeners
            window.addEventListener('resize', onWindowResize);

            // Start Animation Loop
            animate();
        }

        function createGrid() {
            // Clean up old geometry
            if (mesh) {
                scene.remove(mesh);
                mesh.geometry.dispose();
                mesh.material.dispose();
            }
            if (wireframe) {
                scene.remove(wireframe);
                wireframe.geometry.dispose();
                wireframe.material.dispose();
            }

            // Create new geometry
            const geometry = new THREE.PlaneGeometry(10, 10, state.plotPoints, state.plotPoints);
            geometry.setAttribute('color', new THREE.BufferAttribute(new Float32Array(geometry.attributes.position.count * 3), 3));
            originalPositions = geometry.attributes.position.clone();

            // Surface Mesh
            const material = new THREE.MeshLambertMaterial({ vertexColors: true, side: THREE.DoubleSide });
            mesh = new THREE.Mesh(geometry, material);
            scene.add(mesh);

            // Wireframe
            const wireframeGeom = new THREE.WireframeGeometry(geometry);
            const wireframeMat = new THREE.LineBasicMaterial({ color: new THREE.Color(getComputedStyle(document.documentElement).getPropertyValue('--wireframe-color')), linewidth: 1 });
            wireframe = new THREE.LineSegments(wireframeGeom, wireframeMat);
            scene.add(wireframe);
        }

        function onWindowResize() {
            const aspect = container.clientWidth / container.clientHeight;

            perspectiveCamera.aspect = aspect;
            perspectiveCamera.updateProjectionMatrix();

            const frustumSize = 12;
            orthographicCamera.left = frustumSize * aspect / -2;
            orthographicCamera.right = frustumSize * aspect / 2;
            orthographicCamera.updateProjectionMatrix();

            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        // --- WAVE CALCULATION ---
        const waveFunctions = {
            sine: u => Math.sin(2 * Math.PI * u),
            pulse: u => Math.exp(-10 * u * u)
        };

        function updateVertices() {
            if (!mesh) return;

            const positions = mesh.geometry.attributes.position;
            const colors = mesh.geometry.attributes.color;
            const t = state.time;

            for (let i = 0; i < positions.count; i++) {
                const x0 = originalPositions.getX(i);
                const y0 = originalPositions.getY(i);
                const r = Math.sqrt(x0 * x0 + y0 * y0);

                let dx = 0, dy = 0, dz = 0;

                // P-wave (longitudinal)
                const p_u = (r / state.pWave.wavelength) - t;
                const p_mag = state.pWave.amplitude * waveFunctions[state.pWave.type](p_u);
                if (r > 0.01) {
                    dx += p_mag * (x0 / r);
                    dy += p_mag * (y0 / r);
                }

                // S-wave (transverse)
                const s_u = (r / state.sWave.wavelength) - t;
                const s_mag = state.sWave.amplitude * waveFunctions[state.sWave.type](s_u);
                if (state.sWave.direction === 'z') {
                    dz += s_mag;
                } else { // 'xy' direction
                    if (r > 0.01) {
                        dx += s_mag * (-y0 / r);
                        dy += s_mag * (x0 / r);
                    }
                }

                const finalX = x0 + dx;
                const finalY = y0 + dy;
                const finalZ = dz;

                positions.setXYZ(i, finalX, finalY, finalZ);

                // Color based on Z displacement
                const maxAmp = 0.3; // Corresponds to max slider value
                const normalizedZ = Math.max(-1, Math.min(1, finalZ / maxAmp));
                let r_col = 1, g_col = 1, b_col = 1;
                if (normalizedZ > 0) { // Red for positive
                    g_col = 1 - normalizedZ;
                    b_col = 1 - normalizedZ;
                } else { // Blue for negative
                    r_col = 1 + normalizedZ;
                    g_col = 1 + normalizedZ;
                }
                colors.setXYZ(i, r_col, g_col, b_col);
            }

            positions.needsUpdate = true;
            colors.needsUpdate = true;

            // Also need to update wireframe geometry if it exists
            if (wireframe) {
                wireframe.geometry.attributes.position.copy(positions);
                wireframe.geometry.attributes.position.needsUpdate = true;
            }
        }

        // --- ANIMATION LOOP ---
        function animate() {
            requestAnimationFrame(animate);

            if (state.isPlaying) {
                state.time += 0.05; // Animation speed
                if (state.time > timeSlider.max) {
                    state.time = 0;
                }
                timeSlider.value = state.time;
            }

            controls.enabled = (activeCamera === perspectiveCamera);
            controls.update();
            updateVertices();
            renderer.render(scene, activeCamera);
        }

        // --- UI EVENT HANDLERS ---
        const timeSlider = document.getElementById('time-slider');
        const playPauseButton = document.getElementById('play-pause-button');
        const resetButton = document.getElementById('reset-button');
        const stepButton = document.getElementById('step-button');
        const plotPointsSlider = document.getElementById('plot-points-slider');

        // Time controls
        playPauseButton.addEventListener('click', () => {
            state.isPlaying = !state.isPlaying;
            playPauseButton.textContent = state.isPlaying ? '❚❚' : '►';
        });

        resetButton.addEventListener('click', () => {
            state.isPlaying = false;
            playPauseButton.textContent = '►';
            state.time = 0;
            timeSlider.value = 0;
            updateVertices(); // Force a redraw
        });

        stepButton.addEventListener('click', () => {
            if (state.isPlaying) {
                 state.isPlaying = false;
                 playPauseButton.textContent = '►';
            }
            state.time += 0.1;
            if (state.time > timeSlider.max) state.time = 0;
            timeSlider.value = state.time;
            updateVertices(); // Force a redraw
        });

        timeSlider.addEventListener('input', () => {
            state.time = parseFloat(timeSlider.value);
            if (state.isPlaying) {
                 state.isPlaying = false;
                 playPauseButton.textContent = '►';
            }
            updateVertices();
        });

        // Plot points
        plotPointsSlider.addEventListener('input', () => {
            state.plotPoints = parseInt(plotPointsSlider.value);
            createGrid();
        });

        // Helper for button groups
        function setupButtonGroup(selector, stateKey, stateSubKey = null) {
            const group = document.querySelector(selector);
            group.addEventListener('click', (e) => {
                const button = e.target.closest('button');
                if (!button) return;

                Array.from(group.children).forEach(child => child.classList.remove('active'));
                button.classList.add('active');

                const value = button.id.split('-').slice(-2, -1)[0]; // e.g., 'sine' from 'p-wave-sine-button'
                if (stateSubKey) {
                    state[stateKey][stateSubKey] = value;
                } else {
                    state[stateKey] = value;
                }
            });
        }

        // Helper for sliders
        function setupSlider(id, stateKey, stateSubKey) {
            const slider = document.getElementById(id);
            slider.addEventListener('input', () => {
                state[stateKey][stateSubKey] = parseFloat(slider.value);
            });
        }

        // P-Wave Controls
        setupSlider('p-wave-amplitude-slider', 'pWave', 'amplitude');
        setupSlider('p-wave-wavelength-slider', 'pWave', 'wavelength');
        setupButtonGroup('#p-wave-type-selector', 'pWave', 'type');

        // S-Wave Controls
        setupSlider('s-wave-amplitude-slider', 'sWave', 'amplitude');
        setupSlider('s-wave-wavelength-slider', 'sWave', 'wavelength');
        setupButtonGroup('#s-wave-type-selector', 'sWave', 'type');
        setupButtonGroup('#s-wave-direction-selector', 'sWave', 'direction');

        // Viewpoint Controls
        const viewpointSelector = document.getElementById('viewpoint-selector');
        viewpointSelector.addEventListener('click', (e) => {
            const button = e.target.closest('button');
            if (!button) return;

            Array.from(viewpointSelector.children).forEach(child => child.classList.remove('active'));
            button.classList.add('active');

            if (button.id === 'viewpoint-top-button') {
                activeCamera = orthographicCamera;
            } else {
                activeCamera = perspectiveCamera;
            }
        });

        // --- INITIALIZE ---
        // Set initial values from state to UI controls
        document.getElementById('plot-points-slider').value = state.plotPoints;
        document.getElementById('p-wave-amplitude-slider').value = state.pWave.amplitude;
        document.getElementById('p-wave-wavelength-slider').value = state.pWave.wavelength;
        document.getElementById('s-wave-amplitude-slider').value = state.sWave.amplitude;
        document.getElementById('s-wave-wavelength-slider').value = state.sWave.wavelength;

        init();
    </script>

</body>
</html>
