<script>
const sketch = (s) => {
    // State variables
    let a, b, c;
    let z;
    let plotRange;
    let showLabels, showCircle, showAxes, showGridLines;
    let geomCenter;

    // DOM Elements
    let sliderZ, labelZ;
    let sliderPlotRange, labelPlotRange;
    let checkLabels, checkCircle, checkAxes, checkGrid;
    let btnNewExample, btnGoldenRatio;
    let formulaText, valuePz, valueL3L2, valueZ;

    // Geometry Points
    let O, L1, L2, L3, L1_prime, L2_prime;

    s.setup = () => {
        const canvasContainer = document.getElementById('canvas-container');
        const canvas = s.createCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
        canvas.parent(canvasContainer);
        s.noLoop();

        // Get DOM element references
        sliderZ = document.getElementById('slider-z');
        labelZ = document.getElementById('label-z');
        sliderPlotRange = document.getElementById('slider-plot-range');
        labelPlotRange = document.getElementById('label-plot-range');
        checkLabels = document.getElementById('check-show-labels');
        checkCircle = document.getElementById('check-show-circle');
        checkAxes = document.getElementById('check-show-axes');
        checkGrid = document.getElementById('check-show-grid-lines');
        btnNewExample = document.getElementById('btn-new-example');
        btnGoldenRatio = document.getElementById('btn-golden-ratio');
        formulaText = document.getElementById('formula-text');
        valuePz = document.getElementById('value-pz');
        valueL3L2 = document.getElementById('value-l3l2');
        valueZ = document.getElementById('value-z');

        // Attach event listeners
        sliderZ.addEventListener('input', updateAndRedraw);
        sliderPlotRange.addEventListener('input', updateAndRedraw);
        checkLabels.addEventListener('change', updateAndRedraw);
        checkCircle.addEventListener('change', updateAndRedraw);
        checkAxes.addEventListener('change', updateAndRedraw);
        checkGrid.addEventListener('change', updateAndRedraw);
        btnNewExample.addEventListener('click', handleNewExample);
        btnGoldenRatio.addEventListener('click', handleGoldenRatio);

        // Initial setup
        handleGoldenRatio();
    };

    function updateStateFromControls() {
        z = parseFloat(sliderZ.value);
        plotRange = parseFloat(sliderPlotRange.value);
        showLabels = checkLabels.checked;
        showCircle = checkCircle.checked;
        showAxes = checkAxes.checked;
        showGridLines = checkGrid.checked;
    }

    function updateDisplay() {
        labelZ.textContent = z.toFixed(2);
        labelPlotRange.textContent = plotRange.toFixed(1);

        const pz = a * z * z + b * z + c;
        const l3l2prime = Math.abs(pz);

        valuePz.textContent = pz.toFixed(4);
        valueL3L2.textContent = l3l2prime.toFixed(4);
        valueZ.textContent = z.toFixed(4);
    }

    function calculateGeometry() {
        O = s.createVector(0, 0);
        L1 = s.createVector(a, 0);
        L2 = s.createVector(a, b);
        L3 = s.createVector(a - c, b);

        L1_prime = s.createVector(a, -z * a);
        L2_prime = s.createVector(a + b * z + a * z * z, b);
    }

    function calculateViewCenter() {
        const points = [O, L1, L2, L3];
        let minX = Infinity, maxX = -Infinity, minY = Infinity, maxY = -Infinity;
        points.forEach(p => {
            minX = Math.min(minX, p.x);
            maxX = Math.max(maxX, p.x);
            minY = Math.min(minY, p.y);
            maxY = Math.max(maxY, p.y);
        });
        geomCenter = s.createVector((minX + maxX) / 2, (minY + maxY) / 2);
    }

    function updateFormulaDisplay() {
        let formula = '';
        // a term
        if (a !== 0) {
            if (Math.abs(a) === 1) formula += (a > 0 ? '' : '-') + 'z^2 ';
            else formula += `${a}z^2 `;
        }
        // b term
        if (b !== 0) {
            if (b > 0 && formula.length > 0) formula += '+ ';
            if (Math.abs(b) === 1) formula += (b > 0 ? '' : '-') + 'z ';
            else formula += `${b > 0 ? b : `- ${Math.abs(b)}`}z `;
        }
        // c term
        if (c !== 0) {
            if (c > 0 && formula.length > 0) formula += '+ ';
            formula += `${c > 0 ? c : `- ${Math.abs(c)}`}`;
        }

        formula = formula.trim().replace(/\+ -/g, '- ').replace(/- -/g, '+ ');
        if (formula.startsWith('+ ')) formula = formula.substring(2);
        if (formula === '') formula = '0';

        formulaText.innerText = formula;
        if (window.MathJax && MathJax.typesetPromise) {
            MathJax.typesetPromise(['#formula-display']);
        }
    }

    function updateAndRedraw() {
        updateStateFromControls();
        calculateGeometry();
        updateDisplay();
        s.redraw();
    }

    function handleNewExample() {
        a = Math.random() > 0.5 
            ? s.floor(s.random(1, 4)) 
            : s.floor(s.random(-3, 0));
        b = s.floor(s.random(-5, 6));
        c = s.floor(s.random(-5, 6));

        updateFormulaDisplay();
        calculateViewCenter();
        updateAndRedraw();
    }

    function handleGoldenRatio() {
        a = 1;
        b = -1;
        c = -1;

        updateFormulaDisplay();
        calculateViewCenter();
        updateAndRedraw();
    }

    s.draw = () => {
        s.background(250);

        // Set up coordinate system
        const scaleFactor = s.width / (2 * plotRange);
        s.push();
        s.translate(s.width / 2, s.height / 2);
        s.scale(1, -1); // Flip Y-axis
        s.scale(scaleFactor);
        s.translate(-geomCenter.x, -geomCenter.y);

        const strokeWeight = 1 / scaleFactor;

        // Draw Grid
        if (showGridLines) {
            drawGrid(strokeWeight);
        }

        // Draw Axes
        if (showAxes) {
            drawAxes(strokeWeight);
        }

        // Draw coefficient path
        s.strokeWeight(2.5 * strokeWeight);
        s.stroke(0);
        s.line(O.x, O.y, L1.x, L1.y);
        s.line(L1.x, L1.y, L2.x, L2.y);
        s.line(L2.x, L2.y, L3.x, L3.y);

        // Draw test path
        s.stroke('black');
        s.line(O.x, O.y, L1_prime.x, L1_prime.y);
        s.stroke('magenta');
        s.line(L1_prime.x, L1_prime.y, L2_prime.x, L2_prime.y);

        // Draw error segment
        s.stroke('red');
        s.line(L2_prime.x, L2_prime.y, L3.x, L3.y);

        // Draw points
        drawPoint(O, 'black');
        drawPoint(L1, 'black');
        drawPoint(L2, 'black');
        drawPoint(L3, 'black');
        drawPoint(L1_prime, 'black');
        drawPoint(L2_prime, 'black');

        // Draw Circle
        if (showCircle) {
            s.noFill();
            s.stroke('black');
            s.strokeWeight(1.5 * strokeWeight);
            const circleCenterX = (a - c) / 2;
            const circleCenterY = b / 2;
            const circleDiameter = Math.sqrt((a - c) ** 2 + b ** 2);
            s.ellipse(circleCenterX, circleCenterY, circleDiameter, circleDiameter);
        }

        // Draw Labels
        if (showLabels) {
            drawLabel("O", O, strokeWeight);
            drawLabel("L1", L1, strokeWeight);
            drawLabel("L2", L2, strokeWeight);
            drawLabel("L3", L3, strokeWeight);
            drawLabel("L1'", L1_prime, strokeWeight);
            drawLabel("L2'", L2_prime, strokeWeight);
        }

        s.pop();
    };

    function drawPoint(p, col) {
        const pointSize = 8 / scaleFactor;
        s.noStroke();
        s.fill(col);
        s.ellipse(p.x, p.y, pointSize, pointSize);
    }

    function drawLabel(label, p, sw) {
        s.push();
        s.translate(p.x, p.y);
        s.scale(1, -1); // Flip text back to upright
        s.noStroke();
        s.fill(0);
        s.textSize(12 / scaleFactor);
        s.text(label, 10 / scaleFactor, 0);
        s.pop();
    }

    function drawGrid(sw) {
        s.stroke(220);
        s.strokeWeight(sw);
        const xStart = Math.floor(geomCenter.x - plotRange);
        const xEnd = Math.ceil(geomCenter.x + plotRange);
        const yStart = Math.floor(geomCenter.y - plotRange);
        const yEnd = Math.ceil(geomCenter.y + plotRange);

        for (let i = xStart; i <= xEnd; i++) {
            s.line(i, yStart, i, yEnd);
        }
        for (let i = yStart; i <= yEnd; i++) {
            s.line(xStart, i, xEnd, i);
        }
    }

    function drawAxes(sw) {
        s.stroke(0);
        s.strokeWeight(1.5 * sw);
        const xMin = geomCenter.x - plotRange;
        const xMax = geomCenter.x + plotRange;
        const yMin = geomCenter.y - plotRange;
        const yMax = geomCenter.y + plotRange;

        // X-axis
        s.line(xMin, 0, xMax, 0);
        // Y-axis
        s.line(0, yMin, 0, yMax);
    }
};

new p5(sketch);
</script>

