        const solutions = [];
        for (let i = 0; i < solutionCurveData.length - 1; i++) {
            const p1 = solutionCurveData[i];
            const p2 = solutionCurveData[i+1];

            // Check if the vertical line at `alpha` crosses the segment between p1 and p2
            if ((p1.alpha <= alpha && p2.alpha > alpha) || (p2.alpha <= alpha && p1.alpha > alpha)) {
                // Linear interpolation
                const t = (alpha - p1.alpha) / (p2.alpha - p1.alpha);
                const lerp = (start, end) => start + t * (end - start);

                solutions.push({
                    x1: lerp(p1.x1, p2.x1),
                    x2: lerp(p1.x2, p2.x2),
                    t1: lerp(p1.t1, p2.t1),
                    t2: lerp(p1.t2, p2.t2),
