    }

    // --- EVENT LISTENERS ---
    btnPlot.addEventListener('click', () => updateView('plot_solutions'));
    btnTable.addEventListener('click', () => updateView('table'));
    btnTurningPoints.addEventListener('click', () => updateView('plot_turning_points'));
    sliderAlpha.addEventListener('input', handleSliderUpdate);


    // --- P5.JS VISUALIZATION ---
    const sketch = (p) => {
        const padding = { top: 40, right: 40, bottom: 60, left: 60 };
        const alphaMin = 0.00, alphaMax = 0.07;
        const t2Min = 0, t2Max = 8.5;

        let canvasWidth, canvasHeight, plotWidth, plotHeight;

        p.setup = () => {
            canvasWidth = plotView.offsetWidth;
            canvasHeight = 600; // Fixed height for consistency
            plotWidth = canvasWidth - padding.left - padding.right;
            plotHeight = canvasHeight - padding.top - padding.bottom;

            p.createCanvas(canvasWidth, canvasHeight).parent('plot-view');
            p.noLoop(); // We will redraw manually
        };

        p.draw = () => {
            p.background(255);

            drawAxesAndGrid();
            drawSolutionCurve();

            if (currentView === 'plot_solutions') {
                drawAlphaLineAndIntersections();
            } else if (currentView === 'plot_turning_points') {
                drawTurningPoints();
            }
        };

        function mapAlphaToX(a) {
            return p.map(a, alphaMin, alphaMax, padding.left, padding.left + plotWidth);
        }
        function mapT2ToY(t2) {
            return p.map(t2, t2Min, t2Max, padding.top + plotHeight, padding.top);
        }

        function drawAxesAndGrid() {
            p.stroke(220);
            p.strokeWeight(1);
            // Grid lines
            for (let i = 1; i < 10; i++) { // Y-axis grid
                const y = mapT2ToY(i);
                p.line(padding.left, y, padding.left + plotWidth, y);
            }
            for (let i = 1; i < 8; i++) { // X-axis grid
                const x = mapAlphaToX(i * 0.01);
                p.line(x, padding.top, x, padding.top + plotHeight);
            }

            p.stroke(0);
            p.strokeWeight(1.5);
            // Axes lines
            p.line(padding.left, padding.top + plotHeight, padding.left + plotWidth, padding.top + plotHeight); // X-axis
            p.line(padding.left, padding.top, padding.left, padding.top + plotHeight); // Y-axis

            // Ticks and labels
            p.fill(0);
            p.noStroke();
            p.textAlign(p.CENTER, p.TOP);
            for (let i = 0; i <= 7; i++) {
                const alpha = i * 0.01;
                const x = mapAlphaToX(alpha);
                p.text(alpha.toFixed(2), x, padding.top + plotHeight + 5);
            }
             p.text('α', canvasWidth / 2, padding.top + plotHeight + 30);


            p.textAlign(p.RIGHT, p.CENTER);
            for (let i = 0; i <= 8; i++) {
                const y = mapT2ToY(i);
                p.text(i, padding.left - 5, y);
            }
            p.push();
            p.translate(padding.left - 45, canvasHeight / 2);
            p.rotate(-p.HALF_PI);
            p.textAlign(p.CENTER, p.BOTTOM);
            p.text('T₂(α)', 0, 0);
            p.pop();
        }

        function drawSolutionCurve() {
            p.stroke(255, 0, 0); // Red
            p.strokeWeight(2);
            p.noFill();
            p.beginShape();
            solutionCurveData.forEach(point => {
                p.vertex(mapAlphaToX(point.alpha), mapT2ToY(point.t2));
            });
            p.endShape();
        }

        function drawAlphaLineAndIntersections() {
            const currentAlphaX = mapAlphaToX(alphaValue);

            // Draw dashed vertical line
            p.stroke(0, 150, 0); // Green
            p.strokeWeight(1.5);
            drawDashedLine(currentAlphaX, padding.top, currentAlphaX, padding.top + plotHeight, 5);

            // Find and draw intersections
            const solutions = findSolutionsForAlpha(alphaValue);
            solutions.forEach(sol => {
                const y = mapT2ToY(sol.t2);
                p.fill(0, 0, 255); // Blue
                p.noStroke();
                p.ellipse(currentAlphaX, y, 10, 10);
            });
        }

        function drawDashedLine(x1, y1, x2, y2, dashLength) {
            p.push();
            const distance = p.dist(x1, y1, x2, y2);
            const dashCount = distance / dashLength;
            const dx = (x2 - x1) / dashCount;
            const dy = (y2 - y1) / dashCount;
            let x = x1;
            let y = y1;
            for (let i = 0; i < dashCount; i += 2) {
                p.line(x, y, x + dx, y + dy);
                x += dx * 2;
                y += dy * 2;
            }
            p.pop();
        }


        function drawTurningPoints() {
            p.fill(0, 255, 0); // Green
            p.noStroke();
            turningPointsData.forEach(point => {
                const x = mapAlphaToX(point.alpha);
                const y = mapT2ToY(point.t2);
                p.ellipse(x, y, 10, 10);
            });
        }

        // Handle resizing if the container size changes
        p.windowResized = () => {
             canvasWidth = plotView.offsetWidth;
             plotWidth = canvasWidth - padding.left - padding.right;
             p.resizeCanvas(canvasWidth, canvasHeight);
             p.redraw();
        };

    };

    // --- INITIALIZATION ---
    p5sketch = new p5(sketch);
    handleSliderUpdate(); // Initialize value display and alphaValue
    updateView('plot_solutions'); // Set initial view
});
</script>

</body>
</html>
